css:
@-moz-document url-prefix("http://forum.minecorp.ru") {
body {
	background-image: none;
	background: #0f0f0f;
}

.main-category {
	background: #1c1c1c;
}

#topic-review .firstpost {
	border-bottom-color: transparent;
	border-top-color: transparent;
}

.main-content,
.main-item,
.main-item li,
.main-item div,
td {
	border-color: transparent;
	border-bottom-color: transparent;
}

.main-subhead {
	position: relative;
	background-image: none;
	background: #252525;
	border: none;
	top: 0px;
	box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.80);
	padding-bottom: 2px;
	z-index: 99;
	margin-right: -2px;
	margin-left: -2px;
}

.main-head {
	margin-bottom: 12px;
	background: transparent;
	border: transparent;
}

#brd-stats,
#brd-online {
	background: #383838;
	border-color: transparent;
	box-shadow: 0px 2px 7px 1px black;
}

.st-users strong,
.st-users a,
.st-activity strong {
	color: #9aa4a4;
}

.item-subject p .modlist a {
	color: #cbb5ff;
}

#brd-navlinks {
	background: #252525;
	position: fixed;
	margin-top: -3px;
	width: 100%;
}

#brd-navlinks li.isactive {
	background: #d8d8d8;
}

#brd-navlinks li.isactive a {
	color: black;
}

.frm-buttons {
	border-color: #383838;
}

#brd-announcement {
	height: 95px;
	background: #1a1a1a;
	border: 1px solid #191919;
	box-shadow: -4px -8px 35px -7px rgba(0, 0, 0, 0.90) inset;
}

.main-content .main-item .icon.new {
	background: url(https://i.imgur.com/CJQyu7P.png) 0px 0px no-repeat;
}

.main-content .odd .icon,
.main-content .even .icon {
	background: url(https://i.imgur.com/ImkSPZ5.png) 0px 0px no-repeat;
}

.main-content .sticky .icon {
	background: url(https://i.imgur.com/YgFyUcm.png) 0px 0px no-repeat;
}

.main-content .sticky.closed .icon {
	background: url(https://i.imgur.com/KkIi0hQ.png) 0px 0px no-repeat;
}

.main-content .main-item span.redirect {
	background: url(https://i.imgur.com/CAYHgjd.png) 0px 0px no-repeat;
}

.main-item .item-info li span.label {
	visibility: visible;
}

.logo,
.announ {
	margin-top: 50px;
}

icon {
	content: url(https://i.imgur.com/hbotV93.png) 0 0 no-repeat;
}

.logo {
	content: url(https://i.imgur.com/pKph2uA.png);
}

.st-activity span,
.st-users span,
p>span,
.modlist,
.hn a,
.hn span,
.hn>a>span,
.content strong,
.info-replies strong,
.info-views strong,
.info-lastpost strong a,
.info-lastpost>strong>a,
.info-lastpost cite,
.sig-demo,
.hn>.item-num,
.hn>a,
.item-starter>cite,
.info-posts strong,
.info-topics strong,
.quotebox cite,
blockquote>p,
blockquote>p>strong,
.usertitle span,
.userstatus span,
.entry-content p,
.entry-content h5,
.content-options .options,
.txt-set textarea,
.txt-set textarea:focus,
.author-info>li>span,
a:link,
a:visited,
.item-subject>hn>small,
#brd-index .main-item .hn small,
.gen-content p,
input[name="submit_button"],
input[name="pm_send"],
input[name="preview"],
input[name="search"],
.main-frm,
table,
label>span,
legend>span,
.mf-item>label,
#fld1,
#fld2,
#fld3,
#fld4,
#fld5,
#fld6,
#fld7,
#fld8,
#fld9,
#fld10,
#fld11,
#fld12,
.tc1,
.tc0,
.tc2,
.tc3,
.tc4,
.td1,
.data-list>li>span>strong,
a.newpost,
a.newpost:link,
a.newpost:visited,
.pun_poll_item,
.pun_poll_header,
label,
.content-head>.hn,
.pun_poll_item>dl>dt>strong,
.paging span.pages {
	color: #d8d8d8;
}

.main-forum,
.main-content .sticky {
	background: #1c1c1c;
}

.main-foot {
	background: #282828;
	border-color: #1c1c1c;
}

.main-content .main-item .hn .item-status em.sticky {
	color: #c995ff;
}

.main-content .main-item .hn .item-status em.closed {
	color: #8d69aa;
}

.group_color_3 a:link,
.group_color_3,
.group_color_3 a:visited {
	color: #c47748!important;
}

.group_color_20 a:link,
.group_color_20,
.group_color_20 a:visited {
	color: #2fb5a7!important;
}

.group_color_23 a:link,
.group_color_23,
.group_color_23 a:visited {
	color: #b653e8!important;
}

.group_color_1 a:link,
.group_color_1,
.group_color_1 a:visited,
.group_color_10 a:link,
.group_color_10,
.group_color_10 a:visited,
.group_color_19 a:link,
.group_color_19,
.group_color_19 a:visited {
	color: #ff5b5b!important;
}

.group_color_13 a:link,
.group_color_13,
.group_color_13 a:visited {
	color: #00a805!important;
}

.item-nav a {
	color: #8a56bf;
}

.post,
.posthead .hn {
	background-color: #252525;
	border-color: transparent;
}

.postbody {
	background: #1a1a1a;
}

h3.post-ident,
h4.post-title,
.posthead h3,
.userstatus span,
.post,
.even,
.replypost {
	border-color: transparent;
	margin-top: 0px;
	position: relative;
}

.post div,
.sig-line {
	border-color: #383838;
}

.brd .entry-content .quotebox {
	background-image: none;
	background-color: #282828;
	outline-color: #1a1a1a;
	border: 1px solid #383838;
}

.post-options {
	background: #1a1a1a
}

.txt-set textarea {
	background-color: #282828;
	border: #1a1a1a;
}

.txt-set textarea:focus {
	background: #282828;
	border: #1a1a1a;
	outline: none;
	-moz-appearance: none;
}

.main-extensions,
#brd-stats,
#brd-online,
#brd-qpost {
	background: #1a1a1a;
}

.author-info>li>span>strong {
	color: #cbb5ff;
}

.brd .postfoot .post-actions .edit-post a {
	background: url(https://i.imgur.com/1jtlM7b.png) 0px -168px no-repeat;
	color: #a254c9;
}

.brd .postfoot .post-actions .report-post a {
	background: url(https://i.imgur.com/1jtlM7b.png) 0px -24px no-repeat;
	color: #a254c9;
}

input[name="submit_button"],
input[name="pm_send"],
input[name="pm_draft"],
.submit input,
input[name="preview"],
input[name="search"] {
	background: #282828;
	border: #1a1a1a;
}

input[name="submit_button"]:hover,
.submit input:hover,
input[name="search"]:hover,
input[name="pm_send"]:hover,
input[name="preview"]:hover,
input[name="search"]:focus,
.submit input:focus,
input[name="submit_button"]:focus,
input[name="pm_send"]:focus,
input[name="preview"]:focus {
	background: #d8d8d8;
	color: #1a1a1a!important;
	border-color: #d8d8d8!important;
}

.fancy_spoiler_switcher_header,
.post-entry .entry-content .fancy_spoiler_switcher {
	background: #2a2a2a;
	border: #d8d8d8!important;
	outline: none;
	color: #c995ff;
}

.main-frm,
table {
	background: #1a1a1a;
	border: 1px solid #1a1a1a;
}

tr>th {
	background: #1a1a1a;
}

#fld1,
#fld2,
#fld3,
#fld4,
#fld5,
#fld6,
#fld7,
#fld8,
#fld9,
#fld10,
#fld11,
#fld12,
.tc1,
.tc0,
.tc2,
.tc3,
.tc4,
.td1 {
	background: #2e2e2e;
	outline: none;
	border-color: #1a1a1a;
}

* {
	font-family: 'IBM Plex Sans Condensed', sans-serif;
}

.item-subject span.modlist,
.main-content .main-item cite,
b,
.item-subject p span {
	color: #a2a2a2;
}

#brd-navlinks {
	box-shadow: 0px 3px 7px rgba(0, 0, 0, 0.95);
}

.main-category {
	box-shadow: 0px 2px 7px 1px black;
}

.posthead {
	background: transparent;
}

* {
	border: transparent;
	color: #c2c2c2;
}

#vk_community_messages {
	visibility: hidden;
}

.content>p>a>strong,
.content>p>strong>a,
.user-ident .username strong,
.crumblast {
	color: #cbb5ff;
}

.paging strong,
.paging a {
	background: #1a1a1a;
	border-color: #2a2a2a;
	color: #dab7ff;
}

a.newpost,
a.newpost:link,
a.newpost:visited {
	background: #282828;
	border: transparent;
}

.brd .postfoot .post-actions span {
	background: #282828;
	border: transparent;
}

.brd .postfoot .post-actions span a {
	color: #c2c2c2;
}

.brd .postfoot .post-actions span:hover {
	color: #606060;
}

em {
	font-style: normal;
}

.main-topic .firstpos,
.main-topic .topicpost,
.main-topic .even,
.main-topic .replypost {
	margin-bottom: 12px;
}

.pun_poll_item {
	background: #2a2a2a;
	border: solid 1px #0f0f0f!important;
	box-shadow: 4px 2px 32px -4px #000 inset;
}

#brd-announcement .content p a img,
#brd-announcement .content a img,
.main-head .options {
	display: none;
	visibility: hidden;
}

.gen-content .posting {
	position: relative;
	top: 50px;
	z-index: 120;
}

#pun_bbcode_bar input.image {
	border-color: #2a2a2a;
}

.ct-group table th {
	border-color: #1a1a1a;
}

.main-frm .ct-group td,
.ct-group table tr.even td {
	background-color: #212121;
}

#pun_bbcode_bar input[name="b"].image,
#pun_bbcode_bar input[name="i"].image,
#pun_bbcode_bar input[name="u"].image,
#pun_bbcode_bar input[name="list"].image,
#pun_bbcode_bar input[name="list item"].image,
#pun_bbcode_bar input[name="color"].image,
#pun_bbcode_bar input[name="code"].image,
#pun_bbcode_bar input[name="quote"].image,
#pun_bbcode_bar input[name="img"].image,
#pun_bbcode_bar input[name="url"].image,
#pun_bbcode_bar input[name="email"].image {
	background: url(https://i.imgur.com/I7qphgH.png) 0 0 no-repeat;
}

#pun_bbcode_bar input[name="list item"].image {
	background-position: -157px 0;
}

#pun_bbcode_bar input[name="i"].image {
	background-position: -132px 0;
}

#pun_bbcode_bar input[name="u"].image {
	background-position: -261px 0;
}

#pun_bbcode_bar input[name="list"].image {
	background-position: -183px 0;
}

#pun_bbcode_bar input[name="quote"].image {
	background-position: -209px 0;
}

#pun_bbcode_bar input[name="code"].image {
	background-position: -27px 0;
}

#pun_bbcode_bar input[name="email"].image {
	background-position: -79px 0;
}

#pun_bbcode_bar input[name="url"].image {
	background-position: -286px 0;
}

#pun_bbcode_bar input[name="img"].image {
	background-position: -105px 0;
}

#pun_bbcode_bar input[name="color"].image {
	background-position: -53px 0;
}
}
