css:
@-moz-document domain("mac-torrents.com"), domain("mac-torrents.io") {
/* ====================================
   VARIABLES
   ==================================== */

:root
{
  --color-container-dark-1: rgb(30, 30, 30);
  --color-container-dark-2: rgb(40, 40, 40);
  --color-container-dark-3: rgb(50, 50, 50);
  --color-text-light-1: rgb(255, 255, 255);
  --color-text-light-2: rgb(240, 240, 240);
  --color-text-light-3: rgb(190, 190, 190);
  --color-text-light-4: rgb(160, 160, 160);
}


/* ====================================
   CONTAINERS
   ==================================== */

.filter-style-ios .filter-categories,
.filter-style-ios .filter-extras,
.filter-style-ios .filter-categories.new-style a:after,
.filter-style-ios .paginator .page-links,
.filter-style-ios .page-nav,
#phantom,
.post.bg-on,
.bg-on.team-container,
.post.bg-on.fullwidth-img.format-quote,
.post.bg-on.fullwidth-img.format-link
{
	background: var(--color-container-dark-1);
}

.masthead:not(.mixed-header),
.masthead:not(.sub-downwards) .main-nav .dt-mega-menu-wrap
{
	background: var(--color-container-dark-2);
}

body,
#page,
#footer.solid-bg
{
    background: var(--color-container-dark-3);
}

#page
{
    margin-top: -25px;
}

body.home div.blog-media,
body.category div.blog-media,
body.search div.blog-media
{
    margin-right: 5%;
}

.bold-icons .mini-search .submit:before
{
	filter: invert(1);
}

.page-title .wf-container-title,
.page-title.full-width-line,
.article-top-bar .wf-container-top,
.sidebar-right .sidebar,
.sidebar .widget,
.sidebar-content .widget,
.wtpsw-post-li,
.separator-holder,
.accent-border-color .bg-on .separator-holder
{
	border-color: var(--color-container-dark-1);
}

/* ====================================
   TYPOGRAPHY
   ==================================== */

html,
body,
body.page,
.wf-container > *,
.post-content .text-secondary,
.dt-fancy-separator,
.dt-fancy-separator *
{
    color: var(--color-text-light-4);
}

h1,
h2,
h3,
h4,
h5,
h6,
.h1-size,
.entry-title.h1-size,
.h2-size,
.entry-title.h2-size,
h1.entry-title,
.h3-size,
.entry-title.h3-size,
h2.entry-title,
.h4-size,
.entry-title.h4-size,
h3.entry-title,
.h5-size,
.entry-title.h5-size,
.h6-size,
.entry-title.h6-size,
.header-color,
.color-title,
.main-nav .sub-nav > li > a .menu-text,
.mini-nav .sub-nav > li > a .menu-text,
.sidebar-content .widget-title,
.stripe .sidebar-content.solid-bg .widget-title,
.comment-meta,
.comment-meta a,
a.comment-reply-link,
.entry-meta a,
.entry-meta span,
.entry-meta a *
{
    color: var(--color-text-light-3);
}

h1 a,
h2 a,
h3 a,
h4 a,
h5 a,
h6 a,
h1 a:hover,
h2 a:hover,
h3 a:hover,
h4 a:hover,
h5 a:hover,
h6 a:hover,
.paginator a, .paginator a *,
.main-nav > li > a .menu-text,
.mini-search .submit,
.items-grid a,
.recent-posts a,
.contact-info .secondary-color,
.main-nav .sub-nav > li > a,
.mini-nav .sub-nav > li > a,
#bottom-bar .sub-nav > li > a,
.sidebar-content a,
.sidebar-content.solid-bg a *,
.sidebar-content .recent-posts a,
.wtpsw-post-thumb-right h6 a.wtpsw-post-title,
.single .entry-tags a,
a.clear-form
{
    color: var(--color-text-light-2);
}

.page-title h1,
.page-title h1 *,
.page-title h1 a:hover,
#page .page-title .entry-title
{
    color: var(--color-text-light-1);
}


/* ====================================
   FORMS
   ==================================== */

input[type="text"],
input[type="tel"],
input[type="url"],
input[type="email"],
input[type="number"],
input[type="date"],
input[type="range"],
input[type="password"],
select,
textarea
{
    color: var(--color-text-light-1);
    border-color: var(--color-container-dark-1);
    background-color: var(--color-container-dark-1);
}
}
