css:
@-moz-document domain("soitgo.es"), domain("intotheinter.net") {
#login h1#logo {
    filter: invert(100%) !important;
    font-weight: 400 !important;
    opacity: 0.75 !important;
}

#logo2 {
    color: #555 !important;
    font-size: 24px;
    font-weight: 200;
    font-family: open sans, noto sans, sans-serif;
    letter-spacing: -.01em;
}
#logo2:hover {
    color: #999 !important;
}

section#news a#poll_tab, section#news p {
    background: #222 !important;
}
a.irc_chat {color: #777 !important;}

body {
    background: #111 !important;
    color: #ccc;
    font: 13.5px/21px noto sans, open sans,  sans-serif !important;
}

/* category  badges */
#mainpage #links .row .columns {
    border: none !important;
} 
#mainpage #links .row .category {
	border-radius: 2px !important;
	background: none  !important;
    text-align: left !important;
}
  
div.eleven.columns.alpha.title {
    color: #aaa !important;
}
span.tags, 
div.three.columns.alpha.omega.date {
    color: #777 !important;
}




/* links list */

#links .row {
    padding: 0.5em 0.25em;
    margin: 0em !important;
}
#links .row:nth-child(even) {background: #141414 !important;}

#mainpage #links span.tags {font-weight: 600;}
  
  
span.tags:visited, a:visited, a:visited div.eleven.columns.alpha.title {color: #777 !important;}
div.eleven.columns.alpha.title:hover {color: #fff !important; font-weight: 400 !important;}
a:visited:hover div.eleven.columns.alpha.title {color: #aaa !important;}


/* hover poster thumbnail */

.poster img {
    border-radius: 2px;
    border: 1px solid #000;
    background-color: #000;
    box-shadow: 1px 1px 12px 1px rgba(0, 0, 0, 0.75);
    vertical-align: bottom;
    max-width: 260px;
    max-height: 300px;
}
.poster:hover em {
    visibility: visible;
    top: 5em;
    left: 03;
    text-align: right;
    position: absolute;
    z-index: 999
}


/* top bar with category selectors */

#mainpage #topbar span {color: #999 !important; border-color: #555;}
#mainpage #topbar span.selected {color: #ccc !important; font-weight: 600 !important;}
#mainpage #topbar span:hover {color: #fff !important; font-weight: 400 !important;}
#mainpage #topbar span.selected:hover {font-weight: 600 !important;}

/* icons */

nav.container div.ten.columns.omega {margin: 0px 0 2px 12px;}

#icon_admin, #icon_profile, #icon_favorites, #icon_featured, #icon_leaderboard, #icon_sigtv, #icon_utag, #icon_tvbot, #icon_topics, #icon_random, #icon_shop, #icon_messages, #icon_bugs, #icon_requests, #icon_stats, #icon_settings, #icon_faq, #icon_rules, #icon_logout {
    margin-right: 3.5px;
}

.container .ten.columns.omega a {
    opacity: 0.5;
}
.container .ten.columns.omega a:hover {
    opacity: 1 !important;
}
.icontext {
    color: #eee !important;
    font-weight: 600;
}




#mainpage #middlebar {
    margin-top: 1em;
    margin-bottom:  1em;
    padding: 0;
}
.three.columns.alpha a[href="?i=add"] input {margin-bottom: 0 !important;}




.button, button, input[type="submit"], input[type="reset"], input[type="button"] {
    font-family: open sans !important;
    font-weight:  600;
    padding: 6px 12px;
    color: #999 !important;
}
#middlebar button {
    margin: 0  2px 0em;
}
.button:hover, button:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="button"]:hover {
    color: #eee !important;
}
button.selected, button:active {
    background: rgb(35,35,35) !important;
  -webkit-box-shadow: inset 0px 2px 8px rgb(20,20,20) !important;
    color: #000 !important;
    text-shadow: 0px 1px 1px #4a4a4a !important;
    border: none !important;
    outline: none !important;
    font-weight: 700 !important;
}
button:active {
    color: #ccc !important;
}


/* disable featured link */
#links div[class="row header"] {display: none;}



/* pages */

.profiletabs:disabled {border-bottom: none !important;}





/* links page */


div#title.header {
    color: #ccc !important;
}
#links_page .header {
    text-align: left;
    font-size: 24px;
    font-weight: 200;
    padding-bottom: 15px;
}

#links_page .alpha, #links_page .omega {
    border-color: #555;
}
#links_page a {
    font-weight:  600;
}


.utbtn {border-color: #333 !important;}


/* description box */
#info {
    padding: 0 !important;
}
