css:
@-moz-document url-prefix("http://swampservers.net/bans/"), url-prefix("https://swampservers.net/bans/") {
body > div.container {
    width: 100%;
    text-align: center;
}

body > div.container > div.text-center {
    display: inline-block;
    vertical-align: top;
}

.bancard > div:nth-child(2) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    width: 125px;
}

.panel-success > .panel-heading {
    background-color: /*[[ebcolor]]*/;
    border-color: /*[[ebcolor]]*/;
}

.panel-success {
    border-color: /*[[ebcolor]]*/;
}

.panel-primary > .panel-heading {
    background-color: /*[[abcolor]]*/;
    border-color: /*[[abcolor]]*/;
}

.panel-primary {
    border-color: /*[[abcolor]]*/;
}

input, [placeholder="Name/Reason/Notes/STEAMID/STEAMID64"] {
    color: #ccc;
    background-color: #333;
}

div.panel.panel-primary.text-left.bancard div.panel-body table.table tbody tr td span input {
    color: #ccc;
    background-color: #2b2b2b;
    width: 350px;
}

.btn.btn-primary.btn-sm:focus {
    border: 2px solid #1b3e44;
}

.btn.btn-primary.btn-sm:hover {
    border: 2px solid #1e3c44;
}

.btn.btn-primary.btn-sm:active {
    border: 2px solid #112627 !important;
}

.bancard {
    word-break: break-all;
}
}

@-moz-document url-prefix("https://swampservers.net/log-viewer/"), url-prefix("http://swampservers.net/log-viewer/") {
pre {
    background: #1d1d1d;
    color: #e3e3e3;
    border-color: #7c7c7c;
}

input, [placeholder="query"] {
    color: #ccc;
    background-color: #333;
}
}
