@-moz-document regexp("https://www.twitch.tv/(?!directory).*") {
/*Arrow left and right*/

.side-nav__toggle-visibility,
.right-column__toggle-visibility {
	height: 0 !important;
	width: 0 !important;
	border-top: 26px solid transparent;
	border-bottom: 26px solid transparent;
	background-color: transparent;
}

.right-column__toggle-visibility,
.side-nav__toggle-visibility {
	opacity: unset;
}

/*Replaced icons*/
.side-nav__toggle-visibility > figure,
.right-column__toggle-visibility > figure {
	display: none;
}

.side-nav__toggle-visibility {
	background:transparent !important;
}


/*Left-sidebar-closed*/
.side-nav__toggle-visibility {
	margin: 0px -10px 0 !important;
	left: 60px;
	border-left: 20px solid rgba(23, 20, 31, var(--opamid));
	top: -1px;
}

/*Right-sidebar-closed*/
[data-a-target="right-column__toggle-collapse-btn"] {
	/*     margin: 0px -11px 0 !important; */
	top: 0px;
	left: -20px;
	border-right: 20px solid rgba(255, 255, 255, var(--opamid)) !important;
}

/*Left-sidebar-open*/
[class="side-nav__toggle-visibility tw-absolute tw-block tw-flex-grow-0 tw-flex-shrink-0 tw-z-above"] {
	top: -.9px; 
	left: 250.2px;
	border-right: 20px solid var(--bar-and-arrow-color) !important;
	border-left: 0 solid transparent !important;
}



/*Right-sidebar-open*/
[data-a-target="right-column-chat-bar"] [data-a-target="right-column__toggle-collapse-btn"] {
	top: -.9px;
	left: -20px !important;
	border-right: 0 !important;
	border-left: 20px solid var(--bar-and-arrow-color) !important;
}
}

