}



/*Left-sidebar-closed*/
.side-nav__toggle-visibility {
	margin: 0px -10px 0 !important;
	left: 60px;
	border-left: 20px solid rgba(23, 20, 31, var(--opamid));
	top: -1px;
}

.tw-root--theme-dark .side-nav__toggle-visibility {
	background: transparent;
	
}

/*Left-sidebar-open*/
[class="side-nav__toggle-visibility tw-absolute tw-block tw-flex-grow-0 tw-flex-shrink-0 tw-z-above"] {
/* 	top: -.5px; */
	left: 250.2px;
	border-right: 24px solid var(--bar-and-arrow-color) !important;
	border-left: 0;
/* 	border-top: 26px solid rgba(23, 20, 31, var(--opamid)) !important; */
/* 	border-bottom: 26px solid rgba(23, 20, 31, var(--opamid)) !important; */
	margin-top: -1px;
	margin-left: 1px;
	opacity: 1 !important;
}

.tw-tab-nav {
	box-shadow: none;
}


/*text*/
li.tw-tab-nav__item > a:nth-child(1), li.tw-tab-nav__item:nth-child(2) > a:nth-child(1), h4.tw-c-text-alt-2 {
	color: white;
	text-shadow: var(--textshadow);
}

