css:
@-moz-document domain("filmirrgarten.de") {
body {
    font-family: 'Verdana'!important;
}


.postbody h3,
h2.topic-title,
h2.forum-title{
    text-align: left;
    font-family: 'Verdana'!important;
    margin-bottom: 10px;
    font-size: 1.3em!important;
    font-weight: bold;
}

.page-body {
    margin: 0;
}

#header-subsilver {
    background-color: #ffffff;
    border: none;
}

.navbar, .forabg, .forumbg, .panel, .post, .responsive {
    border: 1px solid #ddd;
}

.navbar-top {
    padding: 0 20px;
    background-color: #1865aa;
    background: #7db9e8; 
	background: -moz-linear-gradient(top, #7db9e8 1%, #1b66ac 9%, #1b66ac 80%,#19528c 100%); 
	background: -webkit-linear-gradient(top, #7db9e8 1%, #1b66ac 9%, #1b66ac 80%,#19528c 100%); 
	background: linear-gradient(to bottom, #7db9e8 1%, #1b66ac 9%, #1b66ac 80%,#19528c 100%); 
    color: #ffffff;
}

.navbar-top a {
    color: #ffffff;
}

.navbar {
    background-color:  #003c81;
    border: none;
    border-radius: 0 0 5px 5px;
    padding: 1px 20px 3px;
}

.navbar a {
    color: #ffffff;
    font-weight: 300;
}

.navbar-top-link {
    padding: 5px 20px;
}

.nav-footer {
    color: #bcbcbc;
}


.forumbg, .forabg, .body-blok {
    background-image: none;
    border: none;
}

a.forumtitle {
   font-family: 'Verdana'!important; 
    font-size: 1.1em!important;
}

li.header,
.body-blok-h3 h3,
#subsilver-nav-topic {
    background-image: none;
    background-color: #1865aa;
    color: #ffffff;
}

.post.has-profile .leftsided,
.post.has-profile .rightsided{
    color: #ffffff;
}

.forumbg .header a,
.forabg .header a,
th a {
    color: #ffffff;
}

li.header dt {
    color: #ffffff;
}

li.header dd {
    color: #ffffff;
    margin-top: 10px;
}

li.header dt .list-inner {
    margin-right: 240px;
}

.topiclist.forums .list-inner {
    padding: 10px 10px 5px 63px;
    border-right: 1px solid #dddddd;
    min-height: 55px;
    margin-right: 340px;
}

ul.topiclist li.row dl {
    padding: 0;
}

ul.topiclist dd,
li.row {
    border-color: #ddd;
    border-top: none;
}

li.row:hover dd {
    border-color: #ddd;
    border-top: none;
}


.topiclist.forums dd {
    padding: 20px 13px!important;
    float: right;
	margin-right: 20px;
}

dd.posts,
dd.topics {
    display: none!important;
}

dd.lastpost {
    border: none!important;
}

.forumbg dd.lastpost {
    border-left: 1px solid #dddddd!important;
}

.forumbg .header dd.lastpost {
    border-left: none!important;
}

a.forumtitle {
    font-size: 1.3em;
    font-weight: bold;
    text-decoration: none;
    font-family: 'Verdana'!important; 
}

a.topictitle {
    font-size: 1.1em;
    font-weight: 600;
    text-decoration: none;
    font-family: 'Verdana'!important; 
}

ul.topiclist dt {
    font-size: 1.1em;
    line-height: 1.5;
}

ul.topiclist dd {
    padding: 10px;
}


ul.topiclist .header dd {
    padding: 2px;
}

/*.forumlist dl.row-item dt, .forumlist dl.row-item dd {
    min-height: 70px;
}*/

dd.lastpost, dd.redirect, dd.moderation, dd.time, dd.info {
    width: 310px;
    font-size: 1.1em;
}

.body-blok-info {
    padding: 0 20px 10px;
}

.body-blok-info p {
    line-height: 1.7;
}

.nav-main, .navbar-top-link {
    padding: 5px 0;
}

.topiclist.topics li.row dl.row-item dt .list-inner {
   padding: 10px 10px 10px 43px; 
}

.topic-poster {
    margin-bottom: 8px;
    margin-top: 1px;
}

.postbody .content {
    font-size: 1.3em;
    line-height: 1.6;
    margin-top: 15px;
    margin-bottom: 20px
}

blockquote {
    background-image: none;
    background-color: #f0efef;
    padding: 10px;
    color: #666666;
    margin-bottom: 20px;
}

blockquote cite {
    margin: 0;
}

blockquote cite::before, .uncited::before {
    content: '';
}

.bg1, .bg2 {
    background-color: #ececec;
}

li.row:hover {
   background-color: #ececec; 
}

/*.post {
    border-top: none;
    margin-bottom: 10px;
}*/

.button {
    border-radius: 0;
}

.stat-block.permissions {
    display: none;
    clear: both;
}

.copyright {
    margin-top: 10px;
    opacity: 0.3;
}


.page-footer .navbar {
    border: none;
    padding: 5px 20px;
}
}
