css:
@-moz-document domain("anifilm.tv") {
::-webkit-scrollbar {width: 10px;}
::-webkit-scrollbar-thumb {background: rgb(27, 27, 27) !important;}
::-webkit-scrollbar-track {background: rgb(13, 13, 13) !important;}
::-webkit-scrollbar-track-piece {background: rgb(13, 13, 13)!important;}
::-webkit-scrollbar-corner {background: rgb(13, 13, 13)!important;}
* {color: #C7D4D4 !important;border-color: #444444 !important;-webkit-transition: width 0.2s ease-in-out;}
html,body {background: rgb(10, 10, 10) !important;}
#header  {height: 1.5rem !important;}
#header a {display:none}
#navigation{background-color: rgba(17, 17, 17, 0.9) !important;}
input{background: inherit !important;}
#container,#footer{background-color: rgba(17, 17, 17, 0.3) !important;}
textarea,select,.sidebar,.release__work,.release__torrents-item,.comment__show,.search-fast__result,.search-fast__run,.hr__text--gray,.panel,.panel__overlay-text,.popup,.hr__text,.alert,.identity,.drop-element.drop-theme-popup .drop-content,.comment-form.is-stick,.player,.player__panel{background-color: rgb(13, 13, 13) !important;}
.sidebar__content ul>li:hover,.nav__link:hover,.comment__show:hover,.tabs__header-item.is-active, .tabs__header-item:hover,.player__playlist-service li:hover, .player__playlist-service li.active,.search-fast__run:hover,.search-fast__item:hover,option:checked,.nav__item--active .nav__link,.pagination__item a:hover ,.pagination__item--active a, .pagination__item--active a:hover,.panel__link:hover,.navigation-mobile__nav li.active a,.navigation-mobile__nav li a:hover  {background-color: #282828 !important;}
.btn-social:hover{background: rgba(68, 68, 68, 0.1)!important;}
.player,.player__panel,.player__playlist-service{background-color:  rgb(13, 13, 13)!important;}


}
