css:
@-moz-document domain("postfix.org") {
html {
	background: #181616;
}

body {
	background-color: #252222;
	color: #adaaaa;
	padding: calc(1vh + 1vw);
}

body > h1:first-child {
	margin-top: 0;
}

a:link {
	color: #8679ff;
	text-decoration: none;
}
a:hover,
a:active {
	color: #8679ff;
	text-decoration: underline;
}
a:visited {
	color: #5447C5;
}

H1, H2, H3, H4, H5, H6 {
	font-weight: bold;
	color: #cfcccc;
	border-bottom: 1px solid #fff2;
}

blockquote {
	margin-left: calc(-1vh - 1vw);
	margin-right: calc(-1vh - 1vw);
	background: #1d1a1a;
	padding: 1rem calc(1vh + 1vw);
}
img[alt="[LOGO]"] {
	width: 0px !important;
	height: 0px !important;
	padding: 45.5px 65px;
	background-image: url(data:image/svg+xml;base64,);
}

/* alt="" width="203" height="98">*/
img[src="postfix-logo.jpg"] {
	width: 0 !important;
	height: 0 !important;
	padding: 49px 101.5px;
	background-repeat: no-repeat;
	background-image: url(data:image/svg+xml;base64,);
}

input[type="text"] {
	width: 100%;
	background-color: #ffffff15;
	border-width: 0 0 2px 0;
	border-color: #ffffff40;
	padding: 5px;
	box-sizing: border-box;
}
input[type="text"]:focus {
	background-color: #ffffff30;
	border-color: #8679ff;
}

input[type="submit"] {
	width: 100%;
	background: #555;
	border: none;
	padding: 5px;
	box-sizing: border-box;
}
input[type="submit"]:hover,
input[type="submit"]:active {
	background-color: #666;
}

@media screen and (min-width: 1280px)
{
	body {
		width: 55%;
		margin: 10vh auto;
		box-shadow: 0 0 calc(1vh + 1vw) #0008;
		min-width: 1024px;
	}
}
}
