@-moz-document url-prefix("https://juejin.im/post") {
/* 文章页 */

/*目录 */
nav.article-catalog {
/*     position: absolute;
    top: 5rem; */
    /*     height: calc(100%); */
    min-width: 208.813px;
    overflow: auto;
    padding: 15.6px;
    /*     border: 1px solid rgba(0, 0, 0, .2); */
    background-color: #fff;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, .04);
    /*     opacity: 0.5; */
}
div.catalog-title {
    margin-bottom: 20px;
    padding: 4px;
    /*     border: 1px solid #d7d7d7; */
    /*     border-radius: 5px; */
    /*     color: #0593d3; */
    color: #444;
    text-align: center;
    font-size: 1.5em;
    box-shadow: 0 0 2px 2px #e7ede7;
}

/* div.sticky-block-box {
  position: fixed;
} */

/* 作者信息 */
div.author-block {
    z-index: 249;
}
div.block-body {
    display: none;
}
div.block-title:hover + div.block-body {
    display: block;
}
div.block-body:hover {
    display: block;
}
}

@-moz-document url-prefix("https://juejin.im/post") {
/* 文章页 */

/* 右侧干扰元素隐藏 */

div.index-book-collect,
div.app-download-sidebar-block,
div.wechat-sidebar-block {
    display: none!important;
}

/* 折叠底部相关推荐 */
ul.recommended-entry-list {
    display: none;
}
.recommended-entry-list-title:hover + .recommended-entry-list {
    display: block;
}
.recommended-entry-list:hover {
    display: block;
}

/* 回到顶部、反馈按钮位置 */
/* div.suspension-panel {
