css:
@-moz-document domain("tumblr.com") {
.is_recommended {
	display: none!important;
}
.recommended-unit-container.blog-card-compact {
	display: none!important;
}
.stretchy_kids {
	display: none!important;
}
.recommendation-reason-footer {
	display: none!important;
}
.sponsored-day-media-section {
	display: none!important;
}
.notification.single_notification.alt.takeover-container.big-margin {
	display: none!important;
}
.video-ad-container {
	display: none!important;
}
.video-ad {
	display: none!important;
}
.standalone-ad-container {
	display: none!important;
}
.controls_section_radar,
.radar_post,
.radar_controls,
.recommended_tumblelogs,
.sidebar_link.avatar_align,
.radar {
	display: none!important;
}
#sidebar_footer_nav {
	display: none !important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/tagged") {
.auto-pagination-loader,
.l-container,
.l-container.l-container--two-column,
.l-container.l-container--two-column-dashboard {
	width: 100% !important;
	overflow: hidden !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
}

/* blog posts page */
/* allow sidebar */

#right_column {
	width: calc(100% - 649px) !important;
	float: none !important;
	right: 8px !important;
	margin: 0 !important;
	padding: 0 !important;
	position: fixed !important;
	top: 62px;
	overflow: hidden;
}

/* tag search */
#right_column .related_tags_show_more,
#right_column .tag_controls,
#right_column .related_tags.list {
	max-width:25vw;
}
}
