css:
@-moz-document url-prefix("http://boards.4chan.org/"), url-prefix("https://boards.4chan.org"), domain("4chan.org")
{
/* 
4chan Black & Red (-CyberationMicro-) v2.0.1 CSS3
Do not edit my code or use it for anything other than what i give you 
Do not upload and call your own.  You have no right.
Please check out: http://www.cyberationmicro.com/ 
Please check out my own search engine called http://www.searchblackbox.net/ 

Updates
v2.0.0 Aug 28,2014 Fill rebuild.  Enjoy.
v2.0.1 Aug 29,2014 Minor Tweaks



*/


  a:link
  {
    color: #BD0000 !important;
  }

  a:visited
  {
    color: #C0B5B5 !important;
  }

  a:hover
  {
    color: #FF7A00 !important;
  }

  a:active
  {
    color: #5F5F5F !important;
  }

  .boxcontent,dt,#entries th,input,input[type="submit"],select,h1, h2, h3, h4, h5, h6
  {
    color: #F00!important;
  }

  li,strong,textarea,td,div,.blotter-date,.postMessage,.dateTime,.right-box,p
  {
    color: #9C9C9C!important;
  }

  ::-webkit-scrollbar
  {
    height: 12px!important;
    width: 12px!important;
    background: -webkit-linear-gradient(40deg , #000000 , #1e1e1e , #000000 100%)!important;
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #840000!important;
    border-radius: 16px!important;
  }

/*Border Remover*/
  footer,footer li,a,body,table.postForm>tbody>tr>td:first-child,#search-box,input[type="submit"],div.pagelist,select,div#boardNavMobile,.button,div.reply,hr,#ft ul,#ft li
  {
    border: 0 !important;
    border-left: 0 !important;
    border-right: 0 !important;
    border-top: 0 !important;
    border-bottom: 0 !important;
  }

/*Transparent Remover*/
  #masthead #logo a,#masthead,hr,table.postForm>tbody>tr>td:first-child,div.replyContainer,div.reply,#recent-images li
  {
    background-color: transparent !important;
  }

/*Transparent URL Remover*/
  body
  {
    background: url(http://files.enjin.com/106810/websitestyles/blank.png) !important;
  }

/*Background*/
  html
  {
    background-image: linear-gradient(to right bottom, #690000 0%, #000000 100%) !important;
    background-attachment: fixed!important;
  }

/*Column*/
  #content,.post_content,#entries,#sidebar,article.post,#bannerCnt,div.reply,.left-box,.right-box,.top-box,#announce
  {
    background-image: linear-gradient(to right bottom, #000000 52%, #000000 53%, #141414 100%)!important;
    border-radius: 10px !important;
    box-shadow: 0 0 34px #000 !important;
  }

/*Column 2*/
  .post-meta,div.pagelist,div.opContainer,form
  {
    background-image: linear-gradient(to right bottom, #111111 52%, #000000 53%, #0C0C0C 100%)!important;
    background-color: transparent !important;
    border-radius: 10px !important;
    box-shadow: 0 0 3px #000 !important;
  }

/*Header Bar*/
  .reply.highlight,.postblock,.blue_bar,#entries th,.content h3,div.postLink,div#boardNavMobile,div.postInfoM,.boxbar
  {
    background-image: linear-gradient(180deg, #000000 52%, #000000 53%, #414141 100%)!important;
    background-color: transparent !important;
    box-shadow: 0 0 34px #000 !important;
  }

/*Input*/
  #search-box
  {
    outline: none!important;
    background-image: linear-gradient(to right bottom, #3C3C3C 0%, #000000 100%) !important;
    background-color: transparent !important;
    border-radius: 10px !important;
    box-shadow: 0 0 34px #000 !important;
  }

/*Input2*/
  textarea,input
  {
    outline: none!important;
    background-image: linear-gradient(to right bottom, #3C3C3C 0%, #2B2B2B 100%) !important;
    background-color: transparent !important;
    box-shadow: 0 0 6px #000 inset !important;
  }

/*Button*/
  footer li,input[type="submit"],select,.button,#ft li
  {
    outline: none!important;
    background-image: linear-gradient(to right bottom, #4B4B4B 0%, #000000 100%) !important;
    background-color: transparent !important;
    border-radius: 43px 8px 43px 8px !important;
  }

/*Button Hover*/
  footer li:hover,input[type="submit"]:hover,.button:hover,#ft li:hover
  {
    outline: none!important;
    background-image: linear-gradient(to right bottom, #141414 0%, #000000 100%) !important;
    background-color: transparent !important;
    border-radius: 43px 8px 43px 8px !important;
    box-shadow: 0 0 1px #000 !important;
  }

/*Boarder Grey*/
  .content h3,#announce,#recaptcha_response_field:not(:focus),textarea,input,#bannerCnt,div.opContainer,.left-box,.right-box,.top-box
  {
    border-color: #363636 #2B2B2B #1D1D1D #1D1D1D!important;
  }

/*Boarder Grey Top*/
  .reply.highlight,#content,.post-meta,#sidebar,article.post,div.reply,form,div.postLink
  {
    border-top: 1px solid #1D1D1D!important;
  }

/*Boarder Grey Bottom*/
  .reply.highlight,#content,dd,.widget,#sidebar,article.post,div.mPagelist,.panelHeader,div.reply,form,div.postInfoM
  {
    border-bottom: 1px solid #1D1D1D!important;
  }

/*Boarder Grey Right*/
  .reply.highlight,#content,#sidebar,article.post,div.reply,form
  {
    border-right: 1px solid #1D1D1D!important;
  }

/*Boarder Grey Left*/
  .reply.highlight,#content,#sidebar,article.post,div.reply,form
  {
    border-left: 1px solid #1D1D1D!important;
  }

/*Margin-Top*/
  div.reply
  {
    margin-top: 10px !important;
  }

/*Box-Shadow */
  img
  {
    box-shadow: #000000 0 3px 16px !important;
  }

/*Padding */
  #content,.post_content,.desktop,form
  {
    padding: 10px!important;
  }

  #search-box
  {
    padding: 3px!important;
  }

/*Element Remover */
  iframe,.fill
  {
    display: none!important;
  }

/*Element Remover */
  #search
  {
    display: none!important;
  }

/*Other*/

/*Column Margin Center 900px*/
  #content,div.pagelist,.desktop,form
  {
    width: 900px!important;
    margin: 0px auto!important;
    float: none!important;
  }

  #sidebar
  {
    width: 278px!important;
  }

  #logo
  {
    background: url('http://www.damnpic.net/images/2013/07/22/4chanlogo.png') top left no-repeat !important;
    height: 80px!important;
  }
}
