css:
@-moz-document domain("telegraph.co.uk") {
.main-content {
    background: #333 !important;
    color: #eee !important;
}
.header-container, .header-top-wrapper, .header-headline-wrapper, .header-nav-primary--long .component-content, .header-nav-local .component-content, .header-nav-button {
    background: #333 !important;
    color: #eee !important;
}
.header-nav-global__link .global-nav-icon {
    -webkit-filter: invert(100%);
}
.header-logobar.header-logobar--small .header-logobar__img {
    -webkit-filter: invert(100%);
}
.header-nav-overlay-button .header-nav-overlay-button__button {
    background: #333 !important;
    color: #eee !important;
}
.header-nav-global__link {
    background: #333 !important;
    color: #eee !important;
}
.header-nav-local__link {
    background: #333 !important;
    color: #eee !important;
}
.header-nav-primary__link {
    background: #333 !important;
    color: #eee !important;
}
.header-nav-local__button {
    background: #333 !important;
    color: #eee !important;
}
.headline h1 {
    background: #333 !important;
    color: #eee !important;
}
.articleRenderer2 .article__content, .galleryRenderer .article__content, .offerDetailRenderer .article__content, .hotelRenderer .article__content {
    background: #333 !important;
    color: #eee !important;
}
.page-article .row {
    background: #333 !important;
    color: #eee !important;
}
.container {
    background: #333 !important;
    color: #eee !important;
}
.article-body-text p:nth-child(1), .article-body-text ol li:nth-child(1), .article-body-text ul li:nth-child(1) {
    background: #333 !important;
    color: #eee !important;
}
.article-body-text p, .article-body-text ol li, .article-body-text ul li {
    background: #333 !important;
    color: #eee !important;
}
#card[data-widget-size="responsive"] {
    background: #333 !important;
    color: #eee !important;
}
#card .row[data-type="table"] table tbody tr td:not(.header) {
    background: #333 !important;
    color: #eee !important;
}
.block-heading__label {
    background: #333 !important;
    color: #eee !important;
}
.page-article .polarad, .page-article .lead-asset img[data-frz-ratio^="0."], .page-article .lead-asset figcaption, .page-article .live-post__key-incident-title, .page-article .live-post__title, .page-article .live-post__wrapper-body::before, .page-article .live-post__title-prefix, .page-article .article-date, .page-article .article-body-list, .page-article .article-body-table, .page-article .article-body-text, .page-article .article-body-image, .page-article .article__body header .entity-property-stars, .page-article .article__category, .page-article .author, .page-article .byline, .page-article .breadcrumb, .page-article .facebookLike, .page-article .headline, .page-article .html-embed, .page-article .inline-content, .page-article .list-of-links, .page-article .listOfTags, .page-article .social-block, .page-article .comments-block, .page-article .premium-paywall, .page-article .registration-wall, .page-article .standfirst, .page-article .travel-offers, .page-article .tweet.section, .page-article .sparks, .page-article .sponsor-slot__above-headline, .page-article .sponsor-slot__below-headline {
    background: #333 !important;
    color: #eee !important;
}
.footer-list-corporate__list {
    background: #333 !important;
    color: #eee !important;
}
article, aside, details, figcaption, figure, footer, header, hgroup, main, nav, section, summary {
    background: #333 !important;
    color: #eee !important;
}
}
