css:
@-moz-document domain("forum.novelupdates.com") {
/*   / __ \___  ____  ___  _____(_)___  ____ 
    / /_/ / _ \/ __ \/ _ \/ ___/ / __ \/ __ \
   / _, _/  __/ /_/ /  __/ /  / / / / / /_/ /
  /_/ |_|\___/ .___/\___/_/  /_/_/ /_/\____/ 
            /_/                              */

body {
    background-color: #160024 !important;
}
.topHelper {
    background-color: rgba(30, 0, 50, 1) !important;
}
#logoBlock {
	background-color: #1E0032 !important;
}
.bottomHelper {
    background-color: rgba(30, 0, 50, 1) !important;
}
.navTabs {
    color: rgb(153, 0, 255) !important;
    background-color: rgb(30, 0, 50) !important;
    border: 1px solid rgb(30, 0, 50) !important;
}
.navTabs .navTab.selected .tabLinks {
    background-color: rgba(20, 0, 34, 1) !important;
}
.navTabs .navTab.selected .navLink {
    background-color: rgb(20, 0, 34) !important;
}
#content .pageContent {
    background-color: rgb(30, 0, 50) !important;
}
.navTabs .navTab.PopupClosed:hover {
    background-color: rgb(42, 0, 70) !important;
}
.messageSimple .poster, .navTabs .navTab.PopupOpen .navLink, .secondaryContent a, .ugc a:link, .ugc a:visited, .bbCodeQuote .quoteContainer .quoteExpand.quoteCut, .xenOverlay.memberCard .userInfo h3 a {
    color: #7132BC;
}
#QuickSearch, #QuickSearch .formPopup, #QuickSearchQuery {
    background-color: rgb(20, 0, 34) !important;
}
.breadcrumb {
    color: rgb(235, 235, 235);
    background-color: rgb(18, 0, 32) !important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.breadcrumb .crust a.crumb {
    background-color: rgb(18, 0, 32) !important;
    border-bottom: 1px solid rgb(42, 0, 70) !important;
}
.breadcrumb .crust .arrow span {
    border-left-color: rgb(18, 0, 32) !important;
}
.breadcrumb .crust .arrow {
    border-left-color: rgb(42, 0, 70) !important;
}
.secondaryContent {
    background-color: rgb(30, 0, 50) !important;
    border-bottom: 1px solid rgb(43, 0, 70) !important;
}
.avatar img, .avatar .img, .avatarCropper {
    background-color: rgb(20, 0, 34) !important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    background-color: rgb(20, 0, 34) !important;
}
.widget-tabs .tabs {
    background: rgb(20, 0, 34) !important;
    border-bottom: 1px solid rgb(20, 0, 34) !important;
}
.nodeList .categoryStrip, .widget-tabs .tabs li.active a, .widget-tabs .tabs.noLinks li.active, .discussionList .sectionHeaders, .PageNav a.currentPage, .message .newIndicator, .subHeading {
    background-color: rgb(20, 0, 34) !important;
}
.nodeList.sectionMain {
    border-top: 1px solid #1D0030 !important;
}
.node .nodeText {
    background-color: #1e0032 !important;
}
.widget-tabs .tabs li a, .widget-tabs .tabs.noLinks li {
    background: rgb(20, 0, 34) !important;
    border-bottom: 1px solid rgb(20, 0, 34) !important;
}
.node .nodeInfo.primaryContent, .node .nodeInfo.secondaryContent {
    background-color: #1e0032 !important;
}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: #1e0032 !important;
    border-bottom: 1px solid rgb(20, 0, 34) !important;
}titleBar h1 {
    color: rgb(156, 156, 156) !important;
}
.footer .pageContent {
    color: rgb(222, 222, 222);
    background-color: rgb(20, 0, 34) !important;
}
.primaryContent a {
    color: rgb(190, 190, 190) !important;
}
body {
    color: rgb(235, 235, 235) !important;
}
.breadcrumb .crust a.crumb {
    cursor: pointer;
    color: rgb(235, 235, 235) !important;
}
.messageList .message {
    background-color: rgb(18, 0, 32) !important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.primaryContent a {
    color: #7F00D3 !important;
}
.messageUserBlock .extraUserInfo {
    background-color: rgb(30, 0, 50) !important;
}
.messageUserBlock h3.userText {
    padding: 6px;
    border-top: 1px solid rgb(12, 0, 20) !important;
    border-bottom: 1px solid rgb(12, 0, 20) !important;
}
.messageUserBlock div.avatarHolder {
    background-color: rgb(30, 0, 50) !important;
}
.messageUserBlock {
    background-color: rgb(30, 0, 50) !important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.messageUserBlock a.username, .xenOverlay.memberCard .userInfo h3 a, .xenOverlay a.close::before, #redactor_modal_close::before, #DiscussionListOptionsHandle a {
    color: #7F00D3 !important;
}
.xenOverlay.memberCard {
    color: white !important;
    background: rgba(30, 0, 50, 0.8) !important;
    box-shadow: 0 0 15px 0 rgba(30, 0, 50, 0.8) !important;
}
.breadcrumb .jumpMenuTrigger {
    color: rgb(228, 231, 235) !important;
}
.discussionListItem {
    background-color: rgb(30, 0, 50) !important;
    border-bottom: 1px solid rgb(20, 0, 34) !important;
}
.sectionFooter {
    color: rgb(155, 155, 155) !important;
    background-color: rgb(20, 0, 34) !important;
    border-bottom: 1px solid rgb(42, 0, 70) !important;
}
.primaryContent {
    background-color: transparent !important;
    border-bottom: 1px solid rgb(42, 0, 70);
}
.textCtrl {
    color: rgb(226, 226, 226) !important;
    background-color: rgb(18, 0, 32) !important;
    border: 1px solid rgb(18, 0, 32) !important;
}
.button {
    color: rgb(255, 255, 255) !important;
    background-color: rgb(30, 0, 50) !important;
}
.formPopup .controlsWrapper {
    background: rgb(30, 0, 50) none repeat-x top !important;
}
.Menu .primaryContent {
    background-color: rgb(18, 0, 32) !important;
	border-bottom: 1px solid rgb(22, 0, 36) !important;
}
.Menu {
    border: 1px solid rgb(30, 0, 50) !important;
    border-top: 5px solid rgb(30, 0, 50) !important;
    box-shadow: 0px 5px 5px rgb(20, 0, 34) !important;
}
.PageNav a {
    color: rgb(113, 113, 113) !important;
    background-color: rgb(20, 0, 34) !important;
}
.message .likesSummary {
    border: 1px solid rgb(20, 0, 34) !important;
}
.messageUserBlock .arrow span {
    border-left-color: rgb(30, 0, 50) !important;
}
.bbCodeBlock .type {
    color: rgb(235, 235, 235) !important;
    background-color: rgb(18, 0, 32) !important;
    border-bottom: 1px solid rgb(30, 0, 50) !important;
}
.bbCodeBlock {
    border: 1px solid rgb(30, 0, 50) !important;
}
.bbCodeQuote .quoteContainer {
    background-color: rgb(18, 0, 32) !important;
}
.quickReply {
    background-color: rgba(18, 0, 32, 1)!important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.tagList .tag:after {
    border: 1px solid rgb(18, 0, 32) !important;
    background: rgb(18, 0, 32) !important;
}
.tagList .tag .arrow {
    border-right-color: rgb(70, 70, 70) !important;
}
.tagList .tag .arrow:after {
    border-right-color: rgb(18, 0, 32) !important;
}
.tagList .tag {
    background: rgb(18, 0, 32) !important;
    border: 1px solid rgb(70, 70, 70) !important;
}
.message .signature {
    border-top: 1px dashed rgb(30, 0, 50) !important;
}
.hasJs .bbCodeSpoilerText {
    background-color: rgba(18, 0, 32, 1) !important;
    border: 1px solid rgb(30, 0, 50) !important;
}
.redactor_box {
    background-color: #120020 !important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.redactor_toolbar {
    background-color: rgb(30, 0, 50) !important;
    border-bottom: 1px solid rgb(42, 0, 70) !important;
}
.redactor_toolbar li.redactor_btn_group ul {
    background-color: rgb(18, 0, 32) !important;
    border: 1px solid rgb(42, 0, 70) !important;
    box-shadow: 1px 1px 1px rgba(18, 0, 32, .5) !important;
}
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    background-color: rgb(18, 0, 32) !important;
}
h2.heading {
    background-color: rgb(30, 0, 50) !important;
}
.node .forumNodeInfo.unread .nodeIcon {
    color: #ffffff !important;
}
.profilePage .tabs.mainTabs {
    background: rgb(30, 0, 50) !important;
}
.tabs li a, .tabs.noLinks li {
    color: rgb(235, 235, 235);
    background-color: rgb(20, 0, 34) !important;
    border: 1px solid rgb(42, 0, 70) !important;
}
.profilePage .mast .section.infoBlock {
    border: 1px solid rgb(42, 0, 70) !important;
}
.profilePage .mainProfileColumn {
    border-left: 1px solid rgb(42, 0, 70) !important;
}
.profilePage .primaryUserBlock {
    border-top: 1px solid rgb(42, 0, 70) !important;
}
.mast {
    border-right: 1px solid rgb(42, 0, 72) !important
}
.tabs {
    border-bottom: 1px solid rgb(42, 0, 70) !important;
    background-color: rgb(30, 0, 50) !important;
}
element.style {
    border: 1px solid rgb(42, 0, 70) !important;
}
.profilePage .primaryUserBlock .lastActivity {
    border-top: 1px solid rgb(42, 0, 70) !important;
}
.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    background-color: rgb(30, 0, 50) !important;
}
.heading, .xenForm .formHeader {
    color: rgb(255, 255, 255) !important;
    background-color: rgb(20, 0, 34) !important;
    border-bottom: 1px solid rgb(42, 0, 70) !important;
}
.transactionList .sectionHeaders {
    color: rgb(255, 255, 255) !important;
    background-color: rgb(18, 0, 32) !important;
}
.discussionListItem .noteRow {
    background-color: rgb(18, 0, 32) !important;
}
html {
    background-color: rgb(30, 0, 50) !important;
}
.dataTable tr.dataRow th {
    background: rgb(18, 0, 32) none repeat-x top !important;
    border-bottom: 1px solid rgb(42, 0, 70) !important;
    border-top: 1px solid rgb(42, 0, 70) !important;
    color: rgb(255, 255, 255) !important;
}
.dataTable tr.dataRow td {
    border-bottom: 1px solid rgb(43, 0, 70) !important;
}
}
