css:
@-moz-document domain("weibo.com") {
.WB_global_nav_alpha {
    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12);
    display: block;
    width: 100%;
    background-color: #1867c0 !important;
    border-color: #1867c0 !important;
    height: 58px;
}
.WB_global_nav {
    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12);
    display: block;
    width: 100%;
    background-color: #1867c0 !important;
    border-color: #1867c0 !important;
    height: 58px;
}
.WB_global_nav .gn_nav_list li a {
    color: #fff !important;
}
.gn_logo {
    display: none
}
.WB_global_nav_v2 .gn_nav_list li {
    display: flex;
    height: inherit;
    max-width: 100%;
    padding: 0;
    color: #fff;
}
.WB_global_nav .gn_nav_list li a {
    text-transform: capitalize;
    font-size: 16px;
    font-weight: 300;
    cursor: pointer;
    align-items: stretch;
    color: #fff;
    background-color: transparent!important;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
    border-radius: 2px;
    display: -webkit-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    height: 58px;
    -ms-flex: 0 1 auto;
    flex: 0 1 auto;
    justify-content: center;
    min-width: 88px;
    outline: 0;
    text-decoration: none;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1), color 1ms;
    transition: .3s cubic-bezier(.25, .8, .5, 1), color 1ms;
    position: relative;
    vertical-align: middle;
}
.WB_global_nav .gn_nav_list li a .W_ficon {
    align-items: center;
    border-radius: inherit;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    height: inherit;
    -ms-flex: 1 0 auto;
    flex: 1 0 auto;
    justify-content: center;
    margin: 0 auto;
    padding: 0 16px;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1);
    transition: .3s cubic-bezier(.25, .8, .5, 1);
    white-space: nowrap;
    width: inherit;
}
.WB_global_nav .gn_position {
    align-items: center;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    float: none;
}
.WB_global_nav .gn_nav_list li a .S_txt1 {
    align-items: center;
    border-radius: inherit;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    height: inherit;
    -ms-flex: 1 0 auto;
    flex: 1 0 auto;
    justify-content: center;
    margin: 0 auto;
    padding: 0 16px;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1);
    transition: .3s cubic-bezier(.25, .8, .5, 1);
    white-space: nowrap;
    width: inherit;
}
.FRAME_main .WB_global_nav .gn_nav_list li .home em {
    color: #fff
}
.WB_global_nav .gn_search_v2 {
    width: 200px;
    color: #fff;
    background-color: transparent;
    border: none;
    margin: 10px 0 0 0;
}
.WB_global_nav .gn_search {
    display: none
}
.WB_global_nav .S_ficon, .WB_global_nav .S_ficon_dis, .WB_global_nav a.S_ficon_dis:hover, .WB_global_nav a:hover .S_ficon_dis {
    color: #fff;
}
.WB_global_nav .ficon_send {
    background: none
}
.WB_global_nav .gn_nav {
    margin: 0
}
.WB_global_nav .gn_position {
    margin-left: 0
}
.WB_global_nav .S_line1 {
    display: flex;
    height: inherit;
    max-width: 100%;
    padding: 0;
    color: #fff;
}
.WB_global_nav a, .WB_global_nav .S_link1, .WB_global_nav a.S_txt1:hover, .WB_global_nav a:hover .S_txt1, .WB_global_nav a.current .S_txt1, .WB_global_nav a.S_txt2:hover, .WB_global_nav .SW_fun:hover .S_func1 {
    align-items: center;
    border-radius: inherit;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    height: inherit;
    -ms-flex: 1 0 auto;
    flex: 1 0 auto;
    justify-content: center;
    margin: 0 auto;
    padding: 0 16px;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1);
    transition: .3s cubic-bezier(.25, .8, .5, 1);
    white-space: nowrap;
    width: inherit;
}
.WB_global_nav .gn_nav_list li a:hover {
    height: 58px;
}
.WB_global_nav .gn_nav_list li .W_new {
    display: none
}
.WB_global_nav .gn_topmenulist_notice {
    min-width: 110px;
    transform-origin: left top 0px;
    z-index: 8;
    background: #fff;
    color: rgba(0, 0, 0, .87);
    list-style-type: none;
    padding: 8px 0;
    -webkit-transition: height .3s cubic-bezier(.4, 0, .2, 1);
    transition: height .3s cubic-bezier(.4, 0, .2, 1);
}
.WB_global_nav .gn_topmenulist ul li a {
    cursor: pointer;
    align-items: center;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    font-size: 16px;
    font-weight: 400;
    height: 48px;
    margin: 0;
    padding: 0 16px;
    position: relative;
    text-decoration: none;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1);
    transition: .3s cubic-bezier(.25, .8, .5, 1);
    text-align: left !important;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.WB_global_nav .gn_topmenulist .W_layer_arrow .W_arrow_bor_t {
    display: none
}
.WB_global_nav .ficon_send:hover {
    background: none
}
.WB_main_l {
    height: 100%;
    margin-top: 0px;
    transform: translateX(0px);
}
.WB_left_nav {
    background: inherit;
    list-style-type: none;
    padding: 8px 0;
}
.WB_left_nav .lev_Box h3.lev a {
    transition: none;
    font-weight: 500;
    align-items: center;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    font-size: 16px;
    height: 48px;
    margin: 0;
    padding: 0 16px;
    position: relative;
    text-decoration: none;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1);
}
.WB_left_nav .lev a {
    transition: none;
    font-weight: 500;
    align-items: center;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    font-size: 16px;
    height: 48px;
    margin: 0;
    padding: 0 16px;
    position: relative;
    text-decoration: none;
}
.WB_frame {
    background: #eee!important;
    width: 1200px!important;
}
.WB_left_nav .S_txt1, .WB_left_nav a.S_txt1:hover, .WB_left_nav .S_ficon, .WB_left_nav a:hover .S_ficon, .WB_left_nav a.S_ficon:hover {
    color: #000
}
.WB_frame #plc_main {
    width: 100%
}
.B_discover .WB_frame_c, .WB_main_c {
    width: 100%;
}
.WB_main_l {
    display: none;
}
body, .S_page {
    background: #eee!important;
}
.WB_miniblog {
    background: none
}
.WB_timeline .current em, .WB_timeline .S_dot, .W_gotop {
    margin-left: 600px;
}
.WB_main_r {
    display: none
}
.send_weibo .key {
    display: none
}
.WB_tab_a {
    display: none
}
.WB_notes {
    margin: 0;
    padding: 0;
    background: none
}
.WB_notes a, .WB_notes .wrap {
    color: #000
}
.layer_menu_list ul li a {
    cursor: pointer;
    align-items: center;
    color: inherit;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    font-size: 16px;
    font-weight: 400;
    height: 48px;
    margin: 0;
    padding: 0 16px;
    position: relative;
    text-decoration: none;
    transition: .3s cubic-bezier(.25, .8, .5, 1);
    text-align: left !important;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.btn_30px {
    align-items: center;
    border-radius: 2px;
    display: -webkit-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    height: 36px;
    -ms-flex: 0 1 auto;
    flex: 0 1 auto;
    font-size: 14px;
    font-weight: 500;
    justify-content: center;
    min-width: 88px;
    outline: 0;
    text-transform: uppercase;
    text-decoration: none;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1), color 1ms;
    transition: .3s cubic-bezier(.25, .8, .5, 1), color 1ms;
    position: relative;
    vertical-align: middle;
    background-color: #2196f3 !important;
    border-color: #2196f3 !important;
    box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12);
}
.W_btn_a_disable, .W_btn_a_disable:hover {
    align-items: center;
    border-radius: 2px;
    display: -webkit-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    height: 36px;
    -ms-flex: 0 1 auto;
    flex: 0 1 auto;
    font-size: 14px;
    font-weight: 500;
    justify-content: center;
    min-width: 88px;
    outline: 0;
    text-transform: uppercase;
    text-decoration: none;
    -webkit-transition: .3s cubic-bezier(.25, .8, .5, 1), color 1ms;
    transition: .3s cubic-bezier(.25, .8, .5, 1), color 1ms;
    position: relative;
    vertical-align: middle;
    background-color: #2196f3 !important;
    border-color: #2196f3 !important;
    box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12);
}
.send_weibo .func .limits {
    margin-top: 15px
}
.webim_fold_v2, .webim_fold_v2 .fold_bg, .webim_fold_v2 .fold_cont {
    display: none
}
.PCD_mplayer {
    display: none
}
.S_page .WB_miniblog {
    background: none !important
}
.WB_left_nav .lev_Box, .WB_left_nav fieldset {
    padding-bottom: 10px;
}
.lev_line {
    display: none
}
.WB_left_nav .lev .ico_block {
    display: none
}
.WB_left_nav .lev .W_new {
    display: none
}
.WB_left_nav .lev .W_new_count {
    background: #1867c0;
    margin: 0;
    margin-left: 10px
}
.WB_global_nav .gn_nav_list li .gn_name .S_txt1 {
    max-width: 96px;
}
.WB_frame_a {
    width: 100%
}
.WB_timeline {
    display: none
}
.B_page .WB_frame #plc_main {
    width: 1200px;
}
.WB_frame_c {
    width: 880px;
    margin: 0
}
.B_page a.W_gotop {
    margin-left: 600px;
}
.PCD_header .pf_wrap .icon_setskin {
    display: none
}
.WB_global_nav .gn_header {
    width:1580px
}
.WB_global_nav_v2 .gn_search_v2 .placeholder, .WB_global_nav_v2 .gn_search_v2 .W_input {
    width: 150px;
    padding:9px 0 4px 0;
    color:#fff
}
.WB_global_nav_v2 .gn_search_v2 .gn_topmenulist_search {
    display:none
}
}
