css:
@-moz-document domain(searx.me) {

html {}
body {background-color: #333333 !important;}
body a { color: #ff00ff; }
body a:hover {color:#ff00ff; text-decoration:underline; }
.searx-navbar { background: #333; border-bottom: 1px solid #a4a4a4 }
.searx-navbar ::selection { background-color: #00AAAA; color: #FFF; }
.external-link { color: #FF5555!important; }
#categories input[type=checkbox]:checked+label { color: #000; }
.panel-default>.panel-heading { background-color: #DEDEDE; }
#categories label { background-color: #292929;color:#ddd; border:1px solid #a4a4a4;}
#categories input[type=checkbox]:checked+label { border-bottom: #FF5555 5px solid; color:#ff5555;}
#categories label:hover, input[type=checkbox]:hover+label { border: #a4a4a4 1px solid; color:#ff5555;}

.instance, .pull-left a {color:#ff5555 !important; font-size:1.1em !important;}

.text-info { color: #FF5555!important; }
.text-info:hover { color: #FA3232!important; }
.result_header a { color: #000000; }
.result_header a:hover { color: #FA3232; }
#q { background-color:#292929; color:#ddd; }
.btn-default { background-color:#E7E7E7; }

#search_form .input-group-btn .btn {border-left: 0 !important; background-color:#292929; }
#search_form .input-group-btn .btn:hover {border-left:0 !important; background-color:#1f1f1f; }

#search_url  {background:#292929 !important; }

.tt-dropdown-menu { background-color:#E7E7E7; }
::selection { background-color: #FF5555; color: #FFF; }
.well { background-color: #DEDEDE; margin-bottom: 0px; }
.result:hover { background-color: #DEDEDE; }
.panel { border: 1px solid #C8C9C8; background-color: #E7E7E7; }

.form-control[readonly] { background-color: #D6D7D6; }
.form-control {border-color:#a4a4a4; background-color: #292929; color:#e6e6e6; }
.form-control:focus {box-shadow:0px 0px 5px #a4a4a4; border-color:#a4a4a4; }

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover { background-color: #E7E7E7; }
.onoffswitch-switch { background-color: #FF5555; }

.btn-primary { background-color: #FF5555; border-color: #FF6666; }
.btn-primary:hover { background-color: #FF3333; border-color: #FF6666; }

.btn-default {background: #292929; color:#e6e6e6; }
.btn-default:hover {background: #292929; color:#ff5555; }

.nav-tabs.nav-justified>.active>a, .nav-tabs.nav-justified>.active>a:hover, .nav-tabs.nav-justified>.active>a:focus { border-bottom-color: #E7E7E7; }
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus { background-color: #E7E7E7; }
.result_header a:visited { color: darkgrey; }
.infobox .btn { background-color: #FF5555; }

.external-link:hover {text-decoration:underline;}

.result_header a .highlight{font-weight:800; background-color:#333;}
.result-content .highlight{font-weight:800; background-color:#333; }
.result-default:hover,.result-code:hover,.result-torrent:hover,.result-videos:hover,.result-map:hover{background-color:#434343}
.result-default a:hover{/*border-bottom: 2px solid #a4a4a4*/ text-decoration:underline !important; color:#e6e6e6 !important;}
.result-default, .result_header a {color:#e6e6e6;}
.result-default p {color:#CBC7B8;} 

.panel, .panel-default {background:#292929!important;}
.panel, .panel-default p {color:#cbc7b8 !important; }
.panel, .panel-heading {background:#292929 !important; } 
.panel, .panel-default, .infobox {background:#292929 !important; border:1px solid #a4a4a4 !important;}
.panel, .panel-title {color:#e6e6e6; }


/* Donation page */ 
.text-center, .col-sm-6, .col-lg-6, .col-lg-offset-3{color:#e6e6e6;}

/* Preferences */ 
.col-sm-3, .col-md-2 {color:#e6e6e6;}
}
