css:
@-moz-document domain("homestuck.com") {
a, a:visited {
    color: #1c9dec;
}
.type-center {
    color: #000;
}
.o_search-ac.o_site-search-field {
    background-color: #494949;
    color: #fff;
}
#site-header, #header-container {
    background-color: #000 !important;
}
#flex.flex-column, .pos-r, #o_content, body {
    background-color: #222;
    color: #fff;
}
.row.bg-hs-gray.bg-light-gray--md.pad-t-md--md.pos-r, .o_site-footer.row.pos-r.pad-t-rg.bg-dark-gray, .row.bg-hs-gray.bg-light-gray--md.pad-t-md--md.pad-b-lg {
    background-color: #333;
}
.row.bg-hs-gray.bg-light-gray--md.pad-b-md.pad-b-lg--md.pos-r {
    background-color: #333;
}
.pad-t-md.pad-x-lg--md.type-center.type-hs-header.line-tight, .type-center.pad-t-md--md {
    color: #fff;
}
.pad-t-md.pad-x-lg--md.type-center.type-hs-header.line-tight {
    font-family: courier !important;
}
#game-overlay.o_overlay-wait {
    background-color: #222;
}
.mar-x-auto.disp-bl.bg-hs-gray.pad-t-lg, .row.bg-light-gray.pad-y-md, .mar-x-auto.disp-bl.bg-white, .o-story_text.type-rg.type-hs-small--md.type-center.line-caption.line-copy--md.pad-x-0.pad-x-lg--md.pad-b-lg {
    background-color: #494949;
    color: #fff;
    line-height: 17px;
    font-family: courier !important;
}
.o_story-nav.pad-b-xl.mar-x-rg.mar-x-lg--md>.mar-y-sm>.mar-t-md.pos-r>.read-link, .mar-x-auto.disp-bl.bg-hs-gray {
    background-color: #494949;
}
.pad-y-md.pad-x-lg--md.type-center.type-hs-header.line-tight.bg-hs-gray {
    background-color: #555;
    color: #fff;
}
button {
    background-color: #666;
    border: 1px solid #666;
    color: #fff;
}
.type-center.mar-x-0.mar-x-hs-md--md.mar-b-hs-lg.o_chat-container {
    border: none;
    background-color: #555;
    border-radius: 18px;
}
#home_img {
    width: 654px;
    height: 454px;
    border: 2px solid black;
}
.mar-x-auto.pos-r.mar-y-rg.mar-y-md--md.type-center {
    max-width:650px;
}
.mar-x-auto.mar-y-rg.mar-y-md--md.type-center>.ar-23x9.bg-black, .icon-tool-tip.tooltip-target {
    display: none;
}
.mar-x-auto.mar-y-rg.mar-y-md--md.type-center {
    border: none !important;
}
.mar-x-xl--sm.mar-x-xxl--md.pad-y-md.bg-white.top-rounded {
    background-color: #555;
}
.type-center {
    color: #fff;
}
.type-center>ul>li>a {
    color: #fff !important;
    transition: .5s;
}
.type-center>ul>li>a:hover {
	color:  #1c9dec !important;
    transition: .5s;
}
/*--WV Storyline--*/
body {
    background-color: #222 !important;
}
.mar-x-auto.pad-b-lg.pad-b-xl--md.disp-bl {
    background-color: #333 !important;
}
.bg-white.mar-x-auto.pad-b-lg.pad-b-xl--md.type-center.type-md {
	background-color: #494949;
}
/*--Scratch--*/
body.scratch, body.scratch>.pos-r {
    background-color: black !important;
}
.row {
    background-color: #082A00;
}
.mar-x-auto.disp-bl.bg-scratch-mid-green.pad-t-lg>.o-story_text.type-rg.type-hs-small--md.type-center.line-caption.line-copy--md.pad-x-0.pad-x-lg--md.pad-b-lg {
    background-color: #0E4603;
}
/*--Scroll bar--*/
body::-webkit-scrollbar {
    max-width: 14px;
}
body::-webkit-scrollbar-track {
	background-color: #333;
}
body::-webkit-scrollbar-thumb {
	background-color: #494949;
}
}
