css:
@-moz-document domain(virgool.io) {
body,
a {
    color: #fff !important;
}

.editor--title input {
    background-color: #181818 !important;
}

.header,
.navbar {
    background-color: #464646 !important;
}

.body-createPost .navbar,
.body-editPost .navbar,
.md-RichEditor-root {
    background-color: hsla(0, 0%, 18.4%, 0.9) !important;
}

body,
.comments,
.page-feed,
.page-home,
.m-listview,
.page-notifications,
.page-search .gsc-control-wrapper-cse .gs-result:not(.gs-no-results-result) {
    background-color: #333 !important;
}

.navbar-menu,
.listItemPlaceholder .avatarPlaceholder,
.listItemPlaceholder .linePlaceholder,
.notify-list .notify--title,
.editor-action .editor-menu,
.user-popover-content {
    background-color: #595959 !important;
}

.author .module--name,
.post-suggestion .postItem--title,
.navbar .user-menus-list li a,
.page-feed .section--title,
.page-home .section--title,
.sidebar .block .block--title,
.sidebar .block .block-list.Posts li .Posts-info-title,
.post-stream .card-post .post-content .post--title,
.post-stream .card-post .post-content .post--text,
.follow-suggestion-title,
.follow-suggestion .row .followItemContent-body-name,
.user-information .follow-count .follow-label span,
.page-profile .no-post,
.section-setting--title,
.body-editprofile .label--username input,
.m-listview .listview-header .listview--label.showName h2,
.page-feed .posts-section .postItem.postItemNoPic .post-caption .title,
.page-home .posts-section .postItem.postItemNoPic .post-caption .title,
.page-feed .posts-section .postItem.postItemNoPic .post-caption .name,
.page-home .posts-section .postItem.postItemNoPic .post-caption .name,
.page-roadmap .roadmap-container .info-content,
.notify-items li .user-info-name,
.page-drafts .draft-item .draft--name a,
.table-stats-list thead th,
.page-notifications .notifications-list li .user-info-name,
.gsc-preview-reviews,
.gsc-control-cse .gs-snippet,
.gsc-control-cse .gs-promotion em,
.gsc-control-cse .gs-snippet,
.gsc-control-cse .gs-promotion em,
.editor--title input,
.md-RichEditor-root .md-block-image figcaption {
    color: #fff !important;
}

.author .module-info,
.post-suggestion .section--title,
.userInfo .readTime,
.comments .section--title,
.comments .responses-editor .responses-header,
.comments .responses-editor.open .user--name::after,
.md-block-code .code-container,
.navbar .user-menus-list li:hover a,
a:hover,
.md-block-blockquote,
.md-RichEditor-editor .md-RichEditor-blockquote .public-DraftStyleDefault-block,
.page-feed .section--subtitle,
.page-home .section--subtitle,
.post-stream .card-post .post-meta .meta--author,
.follow-suggestion .row .followItemContent-body-desc,
.user-information .follow-count .follow-label,
.body-editprofile .module.author .module-info .module-header input,
.body-editprofile .module.author .module-info .module-header textarea,
.section-setting .sg--title,
.section-setting .section-toggleButton .toggleButton-group--title,
.m-listview .listview-header .listview--label + *,
.page-feed .posts-section .postItem.postItemNoPic .post-caption .date,
.page-home .posts-section .postItem.postItemNoPic .post-caption .date,
.page-roadmap .roadmap-container .done-tasks,
.page-roadmap .roadmap-container .inprogress-tasks,
.page-roadmap .roadmap-container .upcoming-tasks,
.page-privacy .article--title,
.page-privacy h1,
.page-single .article--title,
.page-single h1,
.page-terms .article--title,
.page-terms h1,
.m-listview .listview-header .listview--label.showCount,
.notify-items li .user-info-action,
.ct-label,
.page-drafts .draft-item-info .last-edit,
.post-content .md-block-image figcaption,
.table-stats-list .postName--options,
.m-listview .listview-items .listview-item-info .item--descreption,
.user-popover--about,
.page-notifications .notifications-list li .user-info-action,
.md-inline-code {
    color: #d8d8d8 !important;
}

.gs-result a.gs-title * {
    color: #f2ff04 !important;
}


/* Content */
.page-privacy .article-content,
.page-single .article-content,
.page-terms .article-content,
.comments .card-cm .cm-body {
    color: #fff !important;
}

[class*="md-block-"]:not(.md-block-code):not(.md-block-embed) a,
.comments .card-cm .cm-body a {
    color: #9cbde4 !important;
    text-decoration: underline;
}

.page-privacy .post-actions .share-buttons .shorturl-input,
.page-single .post-actions .share-buttons .shorturl-input,
.page-terms .post-actions .share-buttons .shorturl-input,
.btn-white:hover {
    background-color: #999 !important;
}


/* Editor */
#AppEditor,
.md-RichEditor-root .md-block-image figcaption,
.btn-white,
.btn-white:hover {
    color: #333 !important;
}

.md-add-button svg path,
.dp-editor-menu svg {
    fill: #fff;
}

.md-RichEditor-editor,
.body-createPost .editor--title input,
.body-editPost .editor--title input {
    padding-right: 20px !important;
    padding-left: 20px !important;
}

.md-side-toolbar > span {
    margin-right: 15px;
}

.md-RichEditor-root [class*="md-block-"]:not(.md-block-code):not(.md-block-embed) a {
    color: #2f66aa !important;
}


/* Comments */
.comments .card-cm {
    border: 0;
}


/* suggestion */
.post-suggestion .postItem .postItemContent,
.comments .card-cm,
.page-privacy .page-content .footerPostSticky,
.page-single .page-content .footerPostSticky,
.page-terms .page-content .footerPostSticky,
.navbar .user-menus-list,
.md-block-blockquote,
.md-RichEditor-editor .md-RichEditor-blockquote .public-DraftStyleDefault-block,
.start-post-section,
.card,
.follow-suggestion .row .followItemContent,
.page-feed .posts-section .postItem.postItemNoPic > a,
.page-home .posts-section .postItem.postItemNoPic > a,
.page-roadmap .roadmap-container .done-tasks,
.notify-list,
.notify-items li,
.App_Search,
.App_Search .search--input,
.zoom-overlay,
.m-listview .listview-items .listview-item,
.notify .older-notification,
.page-notifications .notifications-list li,
.author .iffollow,
.btn-gray-border,
.btn-gray-border:hover {
    background-color: #464646 !important;
}

.post-suggestion .postItem--title::after {
    display: none;
}


/* Links */
.btn-text,
.page-privacy .post-tags ul li > a,
.page-single .post-tags ul li > a,
.page-terms .post-tags ul li > a,
.userInfo .name,
.post-stream .card-post .post-meta .link,
.follow-suggestion .row .followItemContent-body .followers-count,
.sidebar .block .block-list.hashTags li a,
.table-stats-list .postName--options a,
.page-notifications .notifications-list li .user-info-time {
    color: #9cbde4 !important;
}
}
