css:
@-moz-document domain("disk.yandex.ru") {
.header {
--color: whitesmoke;
--bgcolor: #555;
  background: var(--bgcolor);
  color: var(--color); 
}
.header svg * {
  fill: var(--color);
  color: var(--color);
}
.header svg [d^="M9.547"] {
	fill: #F55;
}
.header .header__username {
	color: var(--color);
	font-size: 1.4em;
  text-shadow: 0 0 2px #000, 0 0 1px #000;
}
.header .header__username:first-letter {
	color: #F55;
  font-size: 1.03em;
}
.header .inline-services-menu__link {
	color: var(--color);
  font-size: .8em;
  text-shadow: 0 0 3px #000;
}
.header .button2_theme_action:before {
  background-color: #f0cc00;
}
.header .textinput_theme_normal .textinput__control,
.header .textinput_theme_normal .textinput__control:hover
.header .textinput_theme_normal .textinput__control:active {
  border: 1px solid var(--bgcolor);
  background-color: #333;
	color: var(--color) !important;
  padding-left: 30px
}
.software-header-promo,
.software-header-promo_win {
  display: none;
}
}
