css:
@charset 'win1251';
@namespace url(http://www.w3.org/1999/xhtml);


/*
Name: exler.ru - Фикс для нормальных мониторов
Author: Andrei Krutsko
Version: 0.08
*/


@-moz-document domain(exler.ru)
{

/* 
 AdBlock 
 Блокировка рекламы - на вашей совести. 
 Если есть желание поддержать проект, лучше не включайте. 
*/
/*
div[id='promoblock'][class='promo'],
div[aria-label*="cookieconsent"],
div[class*="cc-banner"],
div[class*="yandex_rtb"],
yatag[class*="yap"]
{
	display: none !important
}
*/

/* Global fonts */
.body,
.html {
    font-size: 15px !important;
    font-family: Verdana, Arial, Helvetica, sans-serif !important;
}
.blog-item-content,
.film-review-text,
.film-review-content,
.film-item-content,
.film-review-content p,
.film-item-content p,
.film-review-text i,
.topic-links ul li,
.custom-btn,
.top-item-descr,
.film-result-title,
.custom-item-header,
.archive-item {
    font-size: 15px !important;
}
blockquote {
    font-size: 14px !important;
}
.blog-item-content,
.film-review-text,
.film-review-content p,
.topic-links ul li,
.custom-btn,
.top-item-descr,
.film-result-title {
    line-height: 20px !important;
    margin-bottom: 25px !important;
}
.custom-btn {
    height: 24px !important;
}
.blog-item-title {
    font-size: 26px !important;
    line-height: 30px !important;
    font-weight: 600 !important;
    margin-bottom: 18px !important;
}
.top-item-title,
.film-title {
    font-size: 22px !important;
    line-height: 26px !important;
}
.top-item-descr {
    max-height: 60px !important;
}
.footer-rights {
    font-size: 12px !important;
    line-height: 16px !important;
}
.sorting-block,
.sorting-block span,
.sorting-block ul li a,
.custom-actions-right .custom-link {
    font-size: 14px !important;
    height: 20px !important;
    line-height: 20px !important;
}


/* Size of columns */
.side-left {
    width: 75% !important;
}
.side-right {
    width: 15% !important;
}


/* Left navigator, section */
.custom-section .side-right {
    margin-bottom: 20px !important;
}
.custom-section > .custom-section-title,
.comments-header-title {
    font-size: 16px !important;
    line-height: 18px !important;
    margin-bottom: 9px !important;
    letter-spacing: 0.05em !important;
    font-weight: 600 !important;
}
.custom-section > .tags-list .tags-item,
.custom-section > .topic-list .topic-item,
.custom-section .custom-btn,
.custom-section .custom-item-header,
.check-filter-list a,
.side-section-subtitle,
.film-filter-section .side-section-subtitle > a,
.alphabet-links ul li a {
    font-size: 14px !important;
}
.custom-section .custom-btn {
    height: 26px !important;
    line-height: 22px !important;
}
.custom-section-border {
    padding: 10px 14px !important;
}

/* Comments */
.comments-header {
    padding-top: 14px !important;
    margin-top: 12px !important;
}
.comments-header {
    margin-bottom: 3px !important;
}
.comments-filter,
.comments-filter span,
.comments-filter ul li a {
    font-size: 14px !important;
    line-height: 24px !important;
    height: 24px !important;
    padding: 0 10px !important;
}
.comment-login {
    margin-left: 8px !important;
    font-size: 16px !important;
    padding-top: 8px !important;
    margin-top: 6px !important;
    padding-bottom: 8px !important;
    margin-bottom: 6px !important;
}
.comments-item {
    padding-bottom: 8px !important;
    margin-bottom: 6px !important;
}
.comments-text,
.comments-item-reply,
.comments-item-reply .comments-item-title,
.comment-textarea textarea{
    font-size: 14px !important;
}
.profile-link {
    font-size: 14px !important;
}

/* Films */
.film-result-table table td,
.film-result-text, 
.discuss-link {
    font-size: 13px !important;
}

/* Youtube */
.blog-item-content iframe {
    display: block !important;
    margin: 0 0 24px auto !important;
    width: 700px !important;
    height: 400px !important;
}

  
}
