@-moz-document domain("kinoarhiv.tv"), domain("kinostrana.tv") {
body {
    background: #161818 url(https://images.vfl.ru/ii/1522745207/f6945c76/21226213.jpg) 50% 0 !important;
}
div.update-hide {
    background-color: #00316C !important;
    color: #fff !important;
}
ul.tags.desc_tag li {
    color: #fff !important;
    font-size: 13pt;
}
li.cat a {
    color: #fff !important;
}
.watch {
    color: #fff !important;
}
.topic .voting li.total {
    color: #fff !important;
}
li.minus a {
    background-color: #db032e !important;
}
li.plus a {
    background-color: #048021 !important;
}
div.added {
    background-color: #098021 !important;
}
li.date {
    background-color: #53fa12 !important;
    font-weight: bold !important;
    color: #000 !important;
}
span.red {
    color: #e40000 !important;
    background-color: #eeff00 !important;
    font-weight: bold !important;
}
a.watch {
    background-image: -webkit-linear-gradient(top, #098021, #000) !important;
    font-weight: bold;
}
div.block.ablock {
    display: none;
}
div.right {
    display: none;
}
div.topic {
    border-style: solid;
    width: 140%;
}
div.topic_info {
    width: 75%;
}
li.select2-search.select2-search--inline {
    background-color: #c7c7c7 !important;
}
#filter_toggle {
    color: #e40000 !important;
}
input.select2-search__field {
    color: #e40000 !important;
}
div.title {
    color: #e40000 !important;
}
span.select2-selection.select2-selection--multiple {
    background-color: #c4c4c4 !important;
}
a.stream-author.left {
    color: #e40000 !important;
    font-weight: normal !important;
}
button.button_action {
    background-color: #28bf4c;
}
button,
input[type='button'],
input[type='submit'],
.button,
div.title {
    background-image: -webkit-linear-gradient(top, #098021, #000) !important;
}
.topic .title {
    background-image: -webkit-linear-gradient(top, #098021, #000) !important;
    text-align: center !important;
    text-shadow: 1px 1px 1px #fff, 0 0 3em #00316C;
    font-size: 25pt;
    color: #fff;
}
.topic .added {
    font-size: 24px;
}
.tags li strong {
    color: #ad3;
}
.topic .tags li a {
    color: #adf;
}
.top_film .list li a,
.stream-comment {
    color: #adf;
}
.block.stream a.stream-comment {
    color: #adf;
}
h1.container {
    color: #ffffff;
}
a.stream-author.left {
    color: #ad7 !important;
}
input.text.ui-autocomplete-input {
    background-color: #3990b8;
    color: #e40000;
}
div#M387953PreloadC656767,
.topic .title-eng {
    display: none !important;
}
ul.user_profile.clearfix li a {
    color: #fff !important;
    font-size: 200% !important;
}
a.author {
    font-size: 100% !important;
}
#new_messages {
    display: none !important;
}
}

@-moz-document url-prefix("http://kinoarhiv.tv/series"), url-prefix("http://kinoarhiv.tv/movie") {
#container {
    background-color: #00316C !important;
}
h1.title {
    color: #fff !important;
    font-size: 200% !important;
}
.big_topic .topic_info_wrapper .title {
    width: 120%;
    left: -40px;
}
div.seo_bar.breadcrumbs.pathway {
    color: #ffffff;
}
a.pathway {
    color: #00f531;
}
div.seo_bar.breadcrumbs.pathway span a {
    color: #ff0303;
}
h2.title-eng,
div.block.blogs.clearfix,
div.comments,
div.topic_desc,
ul.list,
ul.action li a,
div.tag_wrapper strong,
li a span,
ul.tags,
ul.vote_area_topic_11134.voting,
div.img_container img,
a.favourite_a_bottom,
div.vote_area_topic_11134.voting,
noindex p a,
div.topic_left_clum noindex p,
ul.block-nav.reply-title,
div.cmt,
div.left.left_pos,
#bn_2HRwCJv9JL,
#header,
div.vote_area_topic_16220.voting,
.topic .favourite .voting,
.topic .voting li.total,
.topic .voting li a {
    display: none !important;
}
div.topic.big_topic.clearfix {
    margin-left: -25% !important;
    width: 120% !important;
}
div.topic_info_wrapper {
    width: 160% !important;
}
div.title {
    width: 200% !important;
    margin-left: -100px !important;
}
#pause_note {
    font-size: 200% !important;
    color: #ffffff !important;
}
a.addMark {
    color: #ff0000 !important;
    background-color: #00a3fa !important;
}
a.rewindingByHash {
    font-size: 200% !important;
    color: #000000 !important;
    background-color: #40ff00 !important;
}
a.pathway {
    border-style: solid !important;
}
}

@-moz-document url-prefix("http://kinoarhiv.tv/profile") {
p.realname {
    color: #000000 !important;
}
div.update-hide.white-back {
    color: #000000 !important;
}
td.var {
    color: #000000 !important;
}
div.clear {
    color: #000000 !important;
}
div.text {
    color: #000000 !important;
}
div.vote_count_user_267123.count {
    color: #ff0505 !important;
}
div.topic {
    color: #3aabe8;
    width: 200%;
}
}

@-moz-document url-prefix("http://kinoarhiv.tv/favfeed") {
div.topic {
    color: #3aabe8;
    width: 190%;
}
}

@-moz-document url-prefix("http://kinoarhiv.tv/blog"), url-prefix("http://kinoarhiv.tv/top"), url-prefix("http://kinoarhiv.tv/tag") {
div.block.blogs.clearfix {
    display: none !important;
}
tbody tr td {
    color: #00e5f5 !important;
    background-color: #2a1f80 !important;
}
tr td a {
    color: #03ff68 !important;
}
div.content a {
    color: #ffffff !important;
}
}

@-moz-document url-prefix("http://kinoarhiv.tv/news") {
#container {
    background-color: #00316c !important;
    color: white !important;
}
#bn_2HRwCJv9JL {
    display: none !important;
}
div.topic {
    border-style: solid;
    width: 95% !important;
}
a,
h1,
h2,
h3,
h4,
h5,
h6,
.topic .tags li,
#form_comment label {
    color: #ad1;
}
}
