css:
@-moz-document url-prefix("https://ssl.jobcan.jp") {
a {
	color: #ccc !important;
}

a:hover {
    color: #eee !important;
}

body, footer {
    background-color: #21262b !important;
    color: #ddd !important;
}

input, select {
    background: #31363b;
    color: #ddd !important;
}

input[type="text"], input[type="password"], select, textarea {
    border: 1px solid #666;
}

img {
    filter: invert(100%) !important;
}

.login-block {
    background: #31363b !important;
    color: #ddd !important;
}

.form-control {
    background: #41464b !important;
    color: #eee !important;
}

.or, .or p {
    background: #41464b !important;
    color: #eee !important;
}

.footer-gray-block {
    background: #31363b !important;
    color: #ddd !important;
}

.content-introduce {
    background-color: #21262b !important;
}

div.navbar-inner {
    background: #31363b !important;
     color: #ddd !important;
    border-top: 1px solid #666 !important;
	border-bottom: 1px solid #666 !important;
}

.page-header-block {
    border-bottom: 1px solid #666 !important;
}

.navbar .nav > li > a {
    color: #eee !important;
    border-left: solid 1px #666 !important;
}

.contents-wrap-middle {
    background: #21262b !important;
    color: #ddd !important;
}

.btn {
    background: #31363b !important;
    color: #eee !important;
    border: 1px solid #666 !important;
}

.btn:hover, .btn:focus {
    background: #41464b !important;
    color: #eee !important;
}

.info div {
    background: #21262b !important;
    color: #ddd !important;
}

.wrap-type-b-table table th, .wrap-type-c-table table th {
    background: #41464b !important;
    color: #eee !important;
    border-bottom: 1px solid #666 !important;
    border-right: 1px solid #666 !important;
}

table.note th, table.note td {
    border-top: 1px solid #666 !important;
    border-left: 1px solid #666 !important;
}

table.note th, table.note td:first-child {
    background: #41464b !important;
}

table.note tr {
    background: inherit !important;
}

table.note2 th {
    background: #41464b !important;
    color: #eee !important;
}

#contents {
    background: #31363b !important;
    border: 1px solid #555;
}

#main h3 {
    background: #41464b !important;
    color: #eee !important;
}

#menu_adit table, #menu_order table {
    background: #31363b !important;
    color: #eee !important;
}

#rollover-menu {
    background: #31363b !important;
    color: #ddd !important;
    border: 1px solid #666 !important;
}

}
