css:
@-moz-document domain("bearychat.com") {
/* Very left sidebar */
.sideMenu {
	background-color: #202225!important;
}

/* Left sidebar */
.sidePanel, ul, li, span[class^="creator-"] {
	background-color: #2F3136!important;
}

.sidePanel > div, .sidePanel > div > div > div {
    color: white!important;
	background-color: #2F3136!important;
}

/* Title Text*/
span, h4[class^="c"], div[class^="title-"] > span, div[class^="name-"], .title-0-157, .button-1-229, div[class^="content-"] {
	color: #FFFFF2!important;
}

/* Left Channel Actived */
.vchannel-0-381.isActive-1-84, .layout-0-289, .layout-0-303 {
    background-color: #42464D!important;
}

.vchannel-0-381.isActive-1-84 .textOverflow-1-90 {
    background-image: linear-gradient(to right, rgba(47, 49, 54, 0.1), rgb(66, 70, 77) 25px)!important;
}

/* Left Channel   div[class^="c"] > div > div > article, */
article[class^="c"], .article-0-160, .stickyBars-0-293, .header-1-319, .subheader-0-66, .item-1-208 {
	background-color: #2F3136!important;
}

/* Left Channel Title */
article[class^="c"] h1[class^="c"], .primary-0-264, .name-1-213, .name-0-256 {
	color: #72767A!important;
}

.vchannel-0-381.isActive-1-84 .infoTitle-1-92, .subheader-0-66, .button-1-350 {
	color: #F6F6F7;
}

article[class^="c"] span[class*="c"] {
	background-image: linear-gradient(to right, rgba(47, 49, 54, 0.1), rgb(47, 49, 54) 25px)!important;
}

/* Main chat flow */

div[class^="c"] > div:not(.bubble) > div:not(.bubble), .content-1-237, .container-0-102 {
	background-color: #36393E!important;
}

div[class^="sidePanel-"] {
	border-right: 0;
}

.functions-0-180 {
	background-color: #36393F;
}

div[class^="divider"] > span, div[class^="container"] > span {
	background-color: #42464D;
}

/* Message input */

div > section[class^="c"] {
    background-color: #484B51!important;
}

.DraftEditor-editorContainer {
	color: white!important;
}

/* username in chat */
span[class^="prior-"] {
    color: #2EAE4F!important;
}

.robotType-1-146, .text-1-168 {
	background-color: #202225;
}

.leading-0-112 .messageContent-actions, .followed-0-113 .messageContent-actions {
	background-color: rgba(0,0,0,0.5)
}

.bubble {
	background-color: rgba(0,0,0,0.5)!important;
}

.md-paragraph, .text-1-64 {
	color: #C0C1BC;
}

.featureIcon-1-62, .icon-1-351, .icon-1-230 {
	fill: #C0C1BC!important;
}

.compose-0-222 {
	border-top: 1px solid #42454A;
}

::-webkit-scrollbar {
	width: 14px;
}

::-webkit-scrollbar-track {
    background-color: #2f3136;
    border-color: #36393e;
}

::-webkit-scrollbar-thumb {
    background-color: #1e2124;
    border-color: #36393e;
    border: 3px solid #fff;
    border-radius: 7px;
}

.CodeMirror {
	background-color: black!important;
}

.CodeMirror-gutters {
	background-color: #202225!important;
}

code {
	color: yellow!important;
}
}
