/* i really want this to be global */

#chat-extended-side #im-list ul.im-list li .content-popup .content .last-online {
    display: block;
    color: hsl(0, 0%, 100%);
}

body#root-page-mobile {
    background-color: #ff0000;
    background-color: #ff370f;
    background-image: -webkit-linear-gradient(top,#ff370f 0,#ff370f);
    background-image: linear-gradient(180deg,#b50000 0,#ff370f);
}
#mobile-page .payment-product-component .product header {
    background-color: rgba(88, 0, 0, 0.56);
}

footer #footer-header {
    background-color: rgba(167, 0, 0, 0.83);
}
footer #footer-about {
    color: hsl(0, 0%, 100%);
    background-color: hsla(0, 0%, 100%, 0.25);
}
footer #footer-about {
    padding: 40px;
    color: hsl(0, 0%, 100%);
    background-color: hsla(0, 0%, 100%, 0.25);
}
footer #footer-about h4 {
    color: hsl(0, 0%, 100%);
}
a {
    color: hsl(0, 0%, 100%);
}
footer #footer-company .company a {
    color: #ffffff;
}
#chat-extended-side #im-list ul.im-list li .content-popup .content .join a {
    color: #ffc107;
}
#mobile-page .games-list .game-item .game-name-stats {
    background-color: #b10000;
}
.paging .paginator li a {
    color: hsla(0, 0%, 100%, 0.83);
}
body#root-page-mobile header#pageheader {
    background-color: rgba(179, 0, 0, 0.9);
    box-shadow: 0 1px 3px rgba(134, 0, 0, 0.4);
}
body#root-page-mobile header#pageheader #meta-nav>li.profile-credits .xp-level a .xp {
    color: hsl(0, 0%, 100%);
}
#root-page-mobile #header-notify-toggle #notify .container {
    background-color: #ef2800;
}
body#root-page-mobile header#pageheader #profile-extended .username {
    background-color: rgb(186, 13, 13);
}
body#root-page-mobile header#pageheader #profile-extended .user-credits {
    background-color: rgb(186, 13, 13);
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0);
}
body#root-page-mobile header#pageheader #profile-extended .profile-links .arrow-top {
    top: -2px;
    border-color: rgba(144, 0, 0, 0.9) #5f353500 #94282800;
}
body#root-page-mobile header#pageheader #profile-extended {
    background-color: #de2500;
    box-shadow: -4px 7px 10px rgba(255, 0, 0, 0.3);
}
body#root-page-mobile header#pageheader #profile-extended .user-credits .gold .data, body#root-page-mobile header#pageheader #profile-extended .user-credits .silver .data {
    background-color: #980000;
}
body#root-page-mobile header#pageheader #profile-extended .username small {
    color: #ffc107;
}
body#root-page-mobile header#pagesubheader ul.submenu>li a {
    color: hsl(0, 0%, 85%);
}
#profile-news-feed ul.news-feed-thumbs>li.item {
    background-color: hsl(0, 100%, 100%);
}
#mobile-page #profile-page .section-top .avatar-image .image {
    border: 6px solid #b70000;
}
#mobile-page #profile-page .section-top .progression .level {
    background-color: #b70c0c;
}
.pure-button-primary, .pure-button-selected, a.pure-button-primary, a.pure-button-selected {
    background-color: #167ac6;
}
#root-page-mobile .comments li .body {
    color: #000000;
    background: #ffffff;
}
#root-page-mobile .comments li .body .arrow-left {
    border-color: transparent #ffffff transparent transparent;
}
#mobile-page #news-post .post-content .post-body {
    background-color: hsl(0, 0%, 100%);
}
#build-invitations #list-requests ul.thumbs li.item .game-invite {
    background-color: #c12000;
}
#root-page-mobile .emote-form-popup-container .emotes-collection ul.emote-navigation-list {
    background: #d80000;
}
#chat-extended-side #chat-bar ul.windows li.chat-window.open .chatbox .header {
    background-color: #a21b00;
}
#project-list ol.builder-steps li .content {
    background: #ca2200;
}
