css:
@-moz-document domain("gamespark.jp") {
.header-control,
.thm-header .catch,
.thm-nav.global-nav,
.main-slider,
.main-feature,
.thm-aside,
.topics-title-wrapper,
.topics-list,
.main-topics--detail,
body .thm-footer,
.page-top,
.js-nav-spacer,
.itx-widget,
#commentFormArea,
#Kakakucom,
.main-ranking,
.wide-ad,
.jackad,
.item--cate-gamespark-PR,
.readmore,
#adWindowJack,
.main-news .news-list .item--special,
.main-news .news-list .item--linkbox
{display:none !important;}
        
.thm-main,
.header-logo
{float:none; margin:0 auto;}

.header-logo .link .logo
{margin:5px 0 0 0;}

.news-title-wrapper
{margin-top:-40px;}

body,
.thm-header
{background-color:white; background:white;}

.thm-main
{width:75%;}

.thm-body
{width:100%; margin-top:0px;}

.main-feature .item--main, .main-feature .item--sub, .main-news .news-list .item
{padding:8px;}
.main-news .news-list .figure
{margin:8px 12px;}
}

@-moz-document url-prefix("https://www.gamespark.jp/article/") {
.thm-header .header-wrapper,
.breadcrumb,
.main-ad,
.icon-pencil,
.reply-area,
.cmnt-report,
.share-block,
.uz-gamespark_pc,
.arti-provider,
.arti-header .arti-summary,
#_popIn_recommend,
*[class*="cx_giga_relations"],
.openCommentButton,
.commentForm,
.commentMenu,
.comment-reply,
.category-group-name,
.category-name,
.recaptcha-term
{display:none !important;}

.thm-body
{width:80%}
.thm-main
{width:100%}


.hot,
.nice
{background:none; background-color:white;}

.arti-header .head
{font-size:26px; width:97%}

.arti-header
{padding:10px; width:80%;}

.arti-header .date, .arti-header .group
{width:99%;}

.main-pickup
{padding-top:30px;border-top:none;}

.arti-body h2.ttl-chara
{background:none; padding-left:0;}

.arti-body img
{width:90%;}



.comment-ttl
{height:20px !important; padding-bottom:0 !important; background-color:#d1d1d1; border:none !important; margin-bottom:5px !important;}

.comment-date
{color:gray !important;}

.commentBox .normal,
.commentBox .upvoted,
.commentBox .downvoted,
.commentBox .controversial
{padding:0 !important; border:none !important;}

.commentBox.parent
{border:none !important;}

.childPageButton
{padding:3px 0 !important;}

.commentBox .upvoted .comment-txt
{font-weight:bold;}

.commentBox .upvoted,
.commentBox .controversial,
.commentBox .downvoted
{background-color:white !important;}

.commentBox.child
{padding-top:0 !important;}

.commentChildContainer
{padding-bottom:3px !important;}

.arti-header .date, .arti-header .group
{top:80%;}
}
