css:
@-moz-document domain('androidrepublic.org'), url-prefix('https://androidrepublic.org') {
	html {
    background: #000000de;
    font: 13px / 1.4 sans-serif;
}


.p-header-logo.p-header-logo--image img{
    display: none;
}

.uix_headerContainer {
    background: #181818;
}

.p-header {
    color: #181818;
    padding-top: 0px;
    padding-bottom: 0px;
}

.p-nav {
    color: rgba(255, 255, 255, 0.7);
    background: #181818;
    transition: ease-in 0.15s all;
}

.uix_searchBar .uix_searchBarInner .uix_searchForm {
    max-width: 125px;
    width: 100%;
    pointer-events: all;
    position: relative;
    color: #fff;
    background: rgb(24, 24, 24);
    border: 0px solid rgba(255, 255, 255, 0.12);
    border-radius: 0px;
}

.uix_headerContainer .p-navSticky.is-sticky .p-nav {
    background: #1818187d;
    border-radius: 0 !important;
}

.button.button--scroll:hover, a.button.button--scroll:hover, .button.button--scroll:focus, a.button.button--scroll:focus {
    background: #1818187a;
}

.button.button--scroll:active, a.button.button--scroll:active {
    background: #c8c8c8a6;
}

.porta-article-item {
    margin-bottom: 20px;
    display: inline-block;
    width: calc(100% / 4);
}

.p-staffBar {
    background: #181818;
}

.p-staffBar.is-sticky {
    position: fixed !important;
    bottom: auto !important;
    top: 0 !important;
    left: 0;
    right: 0;
    z-index: 400;
    margin: 0 auto;
    box-shadow: 0 0 2px 0 rgba(0, 0, 0, 0.14), 0 2px 2px 0 rgba(0, 0, 0, 0.12), 0 1px 3px 0 rgba(0, 0, 0, 0.2);
    border-radius: 0 !important;
    background: #181818;
}


.p-body-sidebar * {
    display: none;
}

.p-body-sidebar {
    display: none;
}

.button.button--scroll, a.button.button--scroll {
    background: #181818;
}

.p-footer-copyrightRow {
    display: none
}

.uix_extendedFooter {
    display: none
}

.p-footer-inner {
    display: none
}

.porta-article-item .porta-article-header .porta-header-image {
    height: 320px;
}

.porta-article-item {
    margin-bottom: 20px;
    display: inline-block;
    width: calc(100% / 4);
}


.p-body-main--withSidebar .p-body-content {
    width: calc(100% - 120px);
    max-width: calc(100% - 0px);
    display: inline-block;
}

.button:hover, a.button:hover, .button:focus, a.button:focus {
    background: rgba(201, 201, 201, 0.1);
}

.uix_headerContainer .p-navSticky.is-sticky {
    top: 48px !important;
}

.structItemContainer-group--sticky-block .structItem--thread {
    color: #fff;
    font-weight: 900;
    background-color: rgb(24, 24, 24) !important;
}

.structItemContainer-group--normal-block .structItem--thread {
    color: #fff;
    font-weight: 900;
    background-color: rgb(24, 24, 24) !important;
}

.structItem:hover {
    background-color: #000000c4;
}

.menu {
    min-width: 340px
}

.pageNav-jump {
    border: 1px solid rgb(24, 24, 24);
    background: #383838;
}

.pageNav-page {
    background: #383838;
    border: 1px solid rgb(24, 24, 24)
}

.userBanner.userBanner--green {
    background: #969c03;
    border-color: #fff100;
    border-radius: 0px;
}

.userBanner.userBanner--staff, .userBanner.userBanner--primary {
    background: #2daa24;
    border-color: #198b84;
    border-radius: 0px;
    border-color: #0afa00;
}

.avatar.avatar--s {
    border: 1px solid #2daa24;
}

.blockMessage.blockMessage--error {
    border-left: 3px solid #c84448;
    background: #a30c0c24;
    color: #f8474d;
}

.formSubmitRow-bar {
    background: #1d1d1d;
}

.button.button--provider--google, a.button.button--provider--google {
    color: #ffffff;
    background-color: #ce4848;
    border-color: #ce4848;
}

.button.button--provider--google:hover, a.button.button--provider--google:hover, .button.button--provider--google:active, a.button.button--provider--google:active, .button.button--provider--google:focus, a.button.button--provider--google:focus {
    background-color: #823030;
}

.button.button--provider--facebook:hover, a.button.button--provider--facebook:hover, .button.button--provider--facebook:active, a.button.button--provider--facebook:active, .button.button--provider--facebook:focus, a.button.button--provider--facebook:focus {
    background-color: #2e467a;
}

.dataList-row.dataList-row--header .dataList-cell {
    background: #00000036;
}

.userBanner.userBanner--yellow {
    background: #a506ff;
    border-radius: 0px;
    border-color: #ff00f6;
    color: #fff100;
}

.memberHeader-main {
    background: #181818;
    border-bottom: none;
}

.p-nav-scroller .hScroller-action.hScroller-action--start {
    background: #11605b;
    background: linear-gradient(to right, #2e850f 66%, rgba(17, 96, 91, 0) 100%);
}

.p-nav-scroller .hScroller-action.hScroller-action--end {
    background: linear-gradient(to right, rgba(17, 96, 91, 0) 0%, #2e850f 33%);
}

.menu-header {
    color: #ffffff;
    border-bottom: 3px solid rgb(34, 34, 34);
}

.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus {
    color: #2e850f;
    text-decoration: none;
    background: #131212;
}

.ratingStars-star.ratingStars-star--full:before {
    color: #2e850f;
}

.ratingStars-star.ratingStars-star--half:after {
    color: #2e850f;
}

.pageNav-page:not(:first-child) {
    border-left-color: rgb(24, 24, 24);
}

.userBanner.userBanner--skyBlue {
    color: #ffffff;
    background: #0075a5;
    border-color: #00b5ff;
    border-radius: 0px;
}

.userBanner.userBanner--gray {
    color: #ffffff;
    background: #5d5d5d;
    border-color: #ffffff;
    border-radius: 0px;
}

.userBanner.userBanner--lightGreen {
    color: #000000;
    background: #9eff95;
    border-color: #f7fef6;
    border-radius: 0px;
    border-color: #baff47;
}

.structItem-iconContainer .avatar {
    width: 36px;
    height: 36px;
    font-size: 21.6px;
    border: 1px solid #2daa24;
}

.menu-row.menu-row--highlighted {
    color: rgb(255, 255, 255);
    background: #000000ab;
}

.menu-arrow {
    border-bottom-color: rgb(8, 8, 8);
}

.menu-content {
    border-top: 3px solid rgb(8, 8, 8);
}

.menu-separator {
    border-top: 1px solid rgba(24, 24, 24, 0.87);
}
}
