css:
@-moz-document domain(radio.yandex.ru), domain(radio.yandex.kz), domain(radio.yandex.by), domain(radio.yandex.kz), domain(radio.yandex.ua)
{
.page-root__content, .nav__level.nav__level_shown, body, html{
background: #000 !important;
color: #aaa !important;
}

.nav__item:hover, .nav__item_selected {
background: #333 !important;
color: #aaa !important;
}

.link {
color: #aaa !important;
}

.nav__item_type_styled:hover, .link:hover {
color: #fff !important;
}

.nav__level {
border:none;
}

.spinner {
display: none !important;
}

.nav__item_arrow:after, .logo.logo__radio {
    filter:  invert(100) grayscale(100%) !important;
}
}
