css:
@-moz-document url("https://ln.hako.re/chuong-moi-nhat/tat-ca"), url("https://ln.hako.re/"), regexp("http(s?)://(www.|)ln.hako.re/((?!action).)*") {
#show {
    font-size: 16px;
}

.i-n-f_header {
    background-color: #000000;
}

body,
#mainpart,
.header-section,
.feature-section,
.feature-section main,
.noti-item,
.noti-sidebar,
.intromix-section,
.fea-extra,
.i-n-f_topic-wrapper:nth-child(2n),
.board-list,
.ln-list-option li,
.ln-list-default li,
.button-newpost a,
.ln-comment-info,
.ln-comment-form textarea,
.profile-nav,
.browse-alphabet .current,
ul.list-chapters li:hover, table.listext-table tr:hover {
    background: #232323 !important;
}

.listall_summary-more {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, #232323 50%, #232323 100%) !important;
}

.noti-item.untouch,
.listall-user_name,
.listext-item:nth-child(2n + 1),
.intromix-series,
.intromix-newest-forum,
.tab-list,
.board-topic:nth-child(2n+1),
.invidual-page-wrapper,
.page-sub-header,
.search-form_wrapper,
.black-hover,
.browse-alphabet,
.paging_item,
.bg_eee,
#ln-newestseries .ln-ns_item:nth-child(2n+1),
.navbar-default,
.panel.panel-default,
.chapter-comment,
ul.list-chapters li:nth-child(even), table.listext-table tr:nth-child(2n+1) {
    background: #565656 !important;
}

h2,
h4,
#chapter-content,
#mainpart,
a,
span.n-i_d-noti-phrase,
span.n-i_d-noti-time,
span.gerne-name,
.fantrans_intro,
.panel-body,
.ln-comment-form textarea,
.ln-comment-content, .ln-comment-content span {
    color: rgba(255, 255, 255, 0.6) !important;
}

#facebook-comments span {
    background: #6f6f6f !important;
}

.pagination_wrap .current {
    background: #9a9a9a !important;
}

.thumb-item a:visited,
a.noti-url:visited span,
.listall-rechap_item small,
.alphabet_item:hover {
    color: #8dff95!important;
}
.button-follow.follow {
    color: #e22590!important;
    background-color: white!important;
}
}

@-moz-document regexp("http(s?)://(www.|)ln.hako.re/truyen/(.*)") {
.listext_attr a:visited {
    color: #8dff95!important;
}
}
