css:
#tabs-inner-container.ytd-c4-tabbed-header-renderer,
div#sections,ytd-playlist-panel-video-renderer
ytd-browse.style-scope.ytd-page-manager,
ytd-page-manager>.ytd-page-manager,
ytd-watch.style-scope.ytd-page-manager {
	background-color: #81D4FA
}
#channel-header.ytd-c4-tabbed-header-renderer {
	padding: 15px;
	background-color: #81D4FA
}
#dismissable.ytd-video-renderer {
	padding: 10px;
	background-color: #00042;
	border-radius: 10px;
	box-shadow: 0 0 10px #00695C
}
ytd-grid-radio-renderer.style-scope.yt-horizontal-list-renderer,
ytd-grid-video-renderer.style-scope.yt-horizontal-list-renderer,
ytd-grid-video-renderer.style-scope.ytd-grid-renderer {
	box-shadow: 0 0 10px #00695C;
	background-color: #00042;
	width: 195px!important;
	padding: 5px;
	margin: 5px;
	border-radius: 10px;
	height: 225px
}
.badge.badge-style-type-live-now.style-scope.ytd-badge-supported-renderer {
	background-color: transparent;
	color: #fff;
	border-color: #fff;
	width: 100%;
	text-align: center
}
ytd-masthead[mode=legacy] #container.ytd-masthead,
ytd-masthead[mode=material-left] #container.ytd-masthead {
	background-color: #26C6DA
}
div#logo-icon-container {
	background-color: #fff;
	padding: 3px;
	border-radius: 5px
}
img.yt-img-shadow {
	border-radius: 10px
}
yt-icon.guide-icon.style-scope.ytd-guide-entry-renderer {
	color: #fff
}
#channel-header.ytd-c4-tabbed-header-renderer paper-button.style-scope.ytd-subscribe-button-renderer,
paper-button.yt-next-continuation,
paper-button.ytd-subscribe-button-renderer {
	background-color: #2196F3;
	color: #fff
}
#items.yt-horizontal-list-renderer>.yt-horizontal-list-renderer,
ytd-thumbnail.ytd-grid-radio-renderer,
ytd-thumbnail.ytd-grid-video-renderer {
	width: 195px
}
.header.ytd-playlist-panel-renderer {
        background: #63bcff9e;
}
ytd-playlist-panel-video-renderer{
}
ytd-watch-next-secondary-results-renderer {
	padding-bottom: 30px
}
app-drawer.ytd-app:not([persistent]) #header.ytd-app {
	background-color: #00c8db
}
div#footer {
	background-color: #7ad6fa
}
#guide-content.ytd-app {
	border-right: 1px solid #fff
}
#creator-page,
body #footer-container,
body>#body-container {
	background-color: #81D4FA
}
#yt-masthead-container {
	background: #26C6DA
}
span.logo.masthead-logo-renderer-logo.yt-sprite {
	background-color: #fff!important;
	border-radius: 5px
}
#description-text.ytd-video-renderer {
	color: #fff
}
yt-formatted-string.style-scope.ytd-grid-renderer {
	padding: 10px;
	background-color: #fff;
	border-radius: 10px;
}
ytd-page-manager > *.ytd-page-manager {
	-ms-flex: 1 1 0.000000001px;
	-webkit-flex: 1;
	flex: 1;
	-webkit-flex-basis: 0.000000001px;
	background-image: url(https://garthbaker.co.za/wp-content/uploads/2018/05/ocean-and-tree.jpg);
	background-attachment: fixed;
	background-size: 100% 100%;
}
ytd-grid-video-renderer.style-scope.yt-horizontal-list-renderer {
	background-color: #7ad6fa;
}
ytd-grid-video-renderer.style-scope.ytd-grid-renderer.use-ellipsis {
	background-color: #7ad6fa;
}
#dismissable.ytd-video-renderer {
	background-color: #7ad6fa;
}
div#secondary {
	margin: 10px;
	background-color: #ffffffc7;
}
div#dismissable {
	background-color: #71d8fa;
	padding: 2px;
	border-radius: 10px;
}
/* i really want this to be global */
