css:
@-moz-document url-prefix("https://tweakers.net/") {
body,
#contentArea,
.notificationsContainer,
#searchbar,
.darkFpBlock,
.userReviewDescription,
#categoryBrowser,
.mainlist,
.sublist,
#entity,
.pageTabsContainer,
#filter {
    background-color: #222 !important;
    color: #00a2ff;
}
#categoryBar li a,
.fpaItem a,
#news td.title > a,
a.newsSubmit,
#categoryBar li.more a,
#recentlyTested .title a,
#videos ul a,
.title a,
a.readMore,
.userInfo a,
#categoryBrowser li a {
    color: #FFF !important;
}

.relatedItProContent {
    background: #222222;
}

.darkFpBlock,
.userReviewDescription {
    background-image: none !important;
}

.fpaMeta a {
    color: #222;
}

h1, h2, header .subheading {
    color: #eaeaea;
}

.reactieBody {
    border: 1px solid #313030;
    margin-bottom: 10px;
    max-width: 656px;
    padding: 10px 15px 8px;
    position: relative;
    box-sizing: border-box;
}

.reactieBody.informative {
    background: #1f1e1e;
    border-color: #2f2f2f38;
}

.reactieHeader .userLink {
    color: #e0dede;
    font-weight: 700;
}

table.listing td {
    background-color: #585858;
    background-image: -webkit-linear-gradient(top, #f7f7f7, #f2f2f2);
    background-image: -moz-linear-gradient(#f7f7f7, #f2f2f2);
    background-image: linear-gradient(to bottom, #2b2a2a, #222222);
    border-bottom: 0px solid #3c3a3a;
    border-top: 0px solid #222222;
    color: #d2d4d4;
}
table.listing td.topic a {
    color: #00daff;
}
.commentCount {
    color: #f9dc00!important;
}
table.listing td.poster a {
    color: #00da9f;
}
table.listing td.poster {
    color: #048a66;
}
div.info,
div.error,
div.message,
div.faq,
form.insertMessage,
form.form1,
form.form2,
#folders,
#myprofilecontainer {
    background-color: #f2f2f2;
    background-image: -webkit-linear-gradient(top, #f7f7f7, #f2f2f2 40px);
    background-image: -moz-linear-gradient(#f7f7f7, #f2f2f2 40px);
    background-image: linear-gradient(to bottom, #292929, #252525 40px);
    background-repeat: no-repeat;
    border: 1px solid #383636;
    border-radius: 1px;
    box-shadow: inset 0 0 0 0px rgba(255, 255, 255, 0.33);
}
.messagecontent h4,
#pmessages .messagecontent h4 {
    background: #403f3e80;
}
.messageheader {
    border-bottom: 1px solid #4c4c4c;
}
.message .poster {
    border-bottom: 1px solid #4c4c4c;
}
.action_list li.viewcount {
    color: #a2a2a2;
}
a {
    color: #006ad4;
}
.user,
a.user:hover {
    color: #cacaca;
}
table.highlights {
    border-top: 1px solid #969595;
}
#categoryBrowser .images li {
    background: 0;
    border-bottom: 1px solid #4c4a4a;
    border-right: 1px solid #4C4A49;
    float: left;
    line-height: 17px;
    padding: 5px;
}
.bar {
    background: #a7082e;
    border: 1px solid #a22222;
}

body.hero #top {
    background-color: #222222;
}


.fpaImageContainer::before {
    background: #151313;
    bottom: -24px;
    content: '';
    display: block;
    height: 24px;
    left: 0;
    opacity: 0.5;
    position: absolute;
    width: 100%;
}

#toc.fixed.bottom {
    background: #131212d9;
    bottom: 0;
    box-shadow: 0 0 0 1px rgba(19, 19, 19, 0.74);
}

#about .button {
    background-color: #1f1f1fa8;
    border-radius: 2px;
    box-shadow: 1px 1px 1px 0 rgba(0, 0, 0, 0.08);
}

.labelCard {
    background: #0000002e;
    border: 0px solid #8c8c8c;
}

.labelCard>a {
    color: #768c9a;
    cursor: pointer;
    padding: 0 5px;
}

.relatedContentContainer .relatedSubjectsContainer .relatedSubjectItems .relatedSubject {
    border: 0px solid #0000000f;
    background-color: #1918183b;
    border-radius: 0px;
    color: #3a9bcc;
}

.mainSearchSuggest .twk li p.moreContent a {
    color: #d4d4d4;
}

.mainSearchSuggest .twk li p a {
    color: #0c86ff;
}
    

												/*** lines ***/

#top {
    background-color: #2d2d2d;
    background-image: linear-gradient(to bottom, #1f1e1e, #544f4f 40px);
    border-radius: 0 0 6px 6px;
    height: 6px;
}

.fpaItem {
    border-right: 0px solid #2b2a2a;
}

.greyTopBorderBlock {
    border-top: 0px solid #2b2a2a;
}

.fpaItemsWrapper {
    background-color: #f2f2f2;
    background-image: -webkit-linear-gradient(top, #f7f7f7, #f2f2f2 40px);
    background-image: -moz-linear-gradient(#f7f7f7, #f2f2f2 40px);
    background-image: linear-gradient(to bottom, #272626, #222222 40px);
    background-repeat: no-repeat;
    border: 0px solid #383636;
    border-radius: 1px;
    box-shadow: inset 0 0 0 0px rgba(255, 255, 255, 0.33);
}

#bestBuyGuides ul, #recentlyTested ul {
    border-top: 1px solid #2b2b2b;
}

#bestBuyGuides li, #recentlyTested li {
    border-bottom: 0px solid #e6e6e6;
}

.mainColumn.frontpage {
    border-right: 0px solid #2b2b2b;
}

table.highlights {
    border-top: 1px solid #2b2b2b;
}

table.highlights td {
    border-bottom: 0px solid #e6e6e6;
}

.darkFpBlock {
    background-color: #f2f2f2;
    border: 0px solid #d9d9d9;
    border-radius: 1px;
    box-shadow: inset 0 0 0 1px rgba(64, 64, 64, 0.33);
    padding: 15px 15px 17px;
}

#sponsored li {
    border-top: 1px solid #2d2d2d;
    padding-top: 10px;
}

#highlightedProducts .fpaItem {
    border-top: 0px solid #e6e6e6;
    min-height: 237px;
    padding-bottom: 10px;
}

#community .communityReviews {
    border-bottom: 2px solid #222222;
    border-top: 2px solid #222222;
    margin-bottom: 15px;
}

#community .communityReviews>div {
    border-bottom: 2px solid #222222;
    padding-bottom: 5px;
}

#community .communityTopics, #community .communityBlogs {
    border-bottom: 2px solid #222222;
    margin-bottom: 15px;
}

#community .communityItem:nth-child(-n+2) {
    border-top: 0px solid #e6e6e6;
}
#community .communityItem {
    border-bottom: 1 solid #2b2a2a;
    float: left;
    overflow: hidden;
    padding: 10px 0;
    position: relative;
    width: 305px;
}

#community .communityReviews .userReview .reviewContent .userReviewDescription {
    background-color: #f7f7f7;
    background-image: -webkit-linear-gradient(top,#f7f7f7,#f2f2f2);
    background-image: -moz-linear-gradient(#f7f7f7,#f2f2f2);
    background-image: linear-gradient(to bottom,#f7f7f7,#f2f2f2);
    /* border: 1px solid #d9d9d9; */
    border-radius: 2px;
    box-shadow: 0 0 0 1px rgba(68, 64, 64, 0.33) inset;
    line-height: 19px;
    min-height: 62px;
    padding: 11px 14px 34px 12px;
    position: relative;
}

														/*** background ***/

#bottom {
    background: #222;
}

#bottom div.hr {
    background-color: #2d2d2d;
    background-image: linear-gradient(to bottom, #1f1e1e, #544f4f 40px);
    border-radius: 0 0 6px 6px;
    height: 6px;
}

														/*** Reviews***/

#header h1 .subtitle {
    color: #9a0e2d;
    display: block;
    font-size: 16px;
}

h3 {
    color: #f1f1f1;
}

#categoryBrowser {
    background: #f2f2f2;
    border: 1px solid #222222;
}

#categoryBrowser div {
    background: white;
    border: 1px solid #d9d9d917;
    border-radius: 1px;
    height: 384px;
    position: relative;
    width: 280px;
}

.reviewPortalFpaItems .portalFpaItems .fpaItems {
    border-right: 1px solid #333232;
    float: left;
    margin-bottom: 10px;
    width: 660px;
}

.portalFpaItems .fpaItems {
    border-bottom: 1px solid #333232;
    border-top: 1px solid #333232;
}

body.hero #contentArea {
    background: 0;
    background-image: -webkit-linear-gradient(#fff,#fff),-webkit-linear-gradient(rgba(255,255,255,0.85),white);
    background-image: -moz-linear-gradient(#fff,#fff),-moz-linear-gradient(rgba(255,255,255,0.85),white);
    background-image: linear-gradient(#0e0e0ed9,#2d2b2b0d),linear-gradient(rgba(41, 39, 39, 0.77),#101010);
}

.authorBlock .info .name a {
    color: #d4d1d1;
}

.article .ScoreCard.Editorial .ScorecardView {
    background-color: #1b1a1a;
    border: 0;
    border-radius: 0;
    display: inline-block;
    overflow: inherit;
    padding: 20px;
    width: 640px;
}

.article .ScoreCard.Editorial .productInfo {
    background-color: #191717;
    border-left: 1px solid #252323;
    display: inline-block;
    flex-grow: 1;
    padding: 20px;
    vertical-align: top;
    width: 260px;
}

.article .ScoreCard.Editorial h3 {
    color: #ece8e8;
    font-size: 16px;
    line-height: 1.6;
}

.article table {
    background: #1f1f1f38;
    border: 1px solid #1b1a1a;
    border-spacing: 0;
    font-size: 12px;
    line-height: 20px;
    padding: 0 10px;
    width: 100%;
}

.nextPrevLinks.large .label {
    color: #07a2f0;
    display: block;
    font-weight: 700;
}

.reviewToc .tocContent ul li.active a {
    color: #009ffffa;
}

.thumb {
    background: #272727;
    border: 1px solid #2b2a2a;
    border-radius: 2px;
    display: block;
    padding: 5px;
    width: 60px;
}

div.info, div.error, div.message, div.faq, form.insertMessage, form.form1, form.form2, #folders, #myprofilecontainer {
    background-color: #f2f2f2;
    background-image: -webkit-linear-gradient(top, #f7f7f7, #f2f2f2 40px);
    background-image: -moz-linear-gradient(#f7f7f7, #f2f2f2 40px);
    background-image: linear-gradient(to bottom, #292929, #212121 40px);
    background-repeat: no-repeat;
    border: 0px solid #383636;
    border-radius: 1px;
    box-shadow: inset 0 0 0 0px rgba(255, 255, 255, 0.33);
}

body.hero #heroBlock .heroBackgroundImage {
    background-repeat: no-repeat;
    background-size: 100% auto;
    background-position: 50% 0;
    height: 600px;
    left: 0;
    position: absolute;
    top: 0px;
    width: 0%;
}

													/*** Pricewatch ***/

.productCards .card {
    border: 0px solid #a41e39;
}

.fancyButton.grey {
    background-color: #f2f2f2;
    background-image: linear-gradient(to bottom,#272727,#222222);
    border: 0px solid #ccc;
    border-radius: 1px;
    color: #0a95cd;
    cursor: pointer;
    font-size: 12px;
    height: 24px;
    padding: 0 20px;
    white-space: nowrap;
    text-shadow: none;
}

.pwPortalListing p.price a {
    color: gainsboro;
}

table.listing td {
    background-color: #585858;
    background-image: -webkit-linear-gradient(top, #f7f7f7, #f2f2f2);
    background-image: -moz-linear-gradient(#f7f7f7, #f2f2f2);
    background-image: linear-gradient(to bottom, #2b2a2a, #222222);
    border-bottom: 0px solid #3c3a3a;
    border-top: 0px solid #222222;
    color: #d2d4d4;
}

#b_re.pwPortal {
    border: 1px solid #222222;
}

.bestBuyItem {
    border-bottom: 1px solid #2222221f;
}

.counter {
    background-image: linear-gradient(#4d4e4e,#2f2e2e);
}

.pageTabs li {
    background-color: white;
    background-image: -webkit-linear-gradient(top,white,#f2f2f2);
    background-image: -moz-linear-gradient(white,#f2f2f2);
    background-image: linear-gradient(to bottom,#2d2c2c,#222222);
    border: 0px solid #d9d9d9;
    border-bottom: 0;
}

.pageTabs li a {
    color: #e8e8e8;
}

.pageTabs li.active {
    background: #211f1f;
}

.sortable {
    background: #f0f1f1;
    background-image: -webkit-linear-gradient(#f6f8f8,#e2e5e5);
    background-image: -moz-linear-gradient(#f6f8f8,#e2e5e5);
    background-image: linear-gradient(#525656,#222222);
    border: 0px solid #d9d9d9;
}

.sortable.select .specName {
    background: #272727;
    border-right: 1px solid #444040;
    color: #0097f3;
    margin: 0;
    padding: 0 3px;
}


.dropdown li, .dropdown li>a {
    color: #cecaca!important;
}

.dropdown ul {
    background: #232323;
    border: 0px solid #272525;
}

#filter {
    background: #f2f2f2;
    border: 0px solid #d9d9d9;
}

.filterBlock h4, .filterOption h4 {
    color: #e2e2e2;
    font-size: 12px;
    margin: 0;
}

.sliderLabel, .sliderSecondLabel {
    color: #059fdc;
}

.filterBlock {
    background: #211f1f;
    border: 1px solid #222222;
    border-radius: 1px;
    margin-bottom: 10px;
}

.sliderTick {
    color: #ccc;
}

.filterOption.active h4 {
    color: #dcd9d9;
}

.filterOption.active {
    background: #212020;
}

.filterOption {
    border-top: 2px solid #1f1f1f;
}

#activeFilters .filter {
    background: #1f1f1f;
    color: #b7b7b7;
    border: 1px solid #222222;
    padding: 0 5px;
    margin: 0 10px 10px 0;
    line-height: 24px;
}

#activeFilters .filter .value {
    color: #dedede;
}

.toggleSwitch span {
    background-color: #f2f2f2;
    background-image: -webkit-linear-gradient(top,#f2f2f2,#d9d9d9);
    background-image: -moz-linear-gradient(#f2f2f2,#d9d9d9);
    background-image: linear-gradient(to bottom,#2f2e2e,#222222);
    border-right: 1px solid #222;
    color: #2b2b2b;
}

.toggleSwitch {
    background: #b9da36;
    border: 1px solid #222;
}

.toggleSwitch.notSelect span+span.checked, .toggleSwitch.onSelect span+span.checked {
    background: #e41717;
}

table.listing .category a {
    color: #676363;
}

.entityHeader .thumb {
    border-color: #222222;
}

.editionOverview table.spec-detail .spec-column.first {
    border-top: 1px solid #424242;
}
.editionOverview table.spec-detail .spec-column {
    border-color: #424242;
}
table.spec-detail td.spec-column {
    border-bottom: 1px solid #424242;
    line-height: 19px;
    padding-left: 15px;
}

table.shop-listing tr:first-child td {
    border-top: 1px solid #424242;
}

table.listing th, table.shop-listing th, table.listing td, table.shop-listing td {
    border-bottom: 0px solid #424242;
    vertical-align: top;
}

.bulletSeparatedList.sortLinks .current {
    color: #dcdada;
    font-weight: 700;
}

.popup {
    box-shadow: 8px 5px 5px -3px rgba(0,0,0,0.1), 5px 8px 5px -3px rgba(0,0,0,0.1);
    background-color: rgba(39, 39, 39, 0.98);
    border: 0px solid #ccc;
}

.specs td.spec-label {
    color: #e2e1e1;
}

.specs .popularSpecs {
    background-color: #232323;
    border: 1px solid rgba(29, 29, 29, 0.72);
    border-radius: 2px;
    margin-bottom: 20px;
}

.productSpecifications td {
    border-bottom: 1px solid #1d1c1c;
}

.specs .popularSpecs th {
    border-bottom: 1px solid rgba(35, 35, 35, 0.8196078431372549);
}

.bar {
    background: #333333;
    border: 1px solid #252525;
    border-radius: 1px;
    color: #f1f1f1;
}

.productSpecificationImages .thumb {
    border-color: #2b2a2a;
    float: left;
    margin-bottom: 12px;
}

.toggleable .toggleBtn {
    color: #f1f1f1;
    display: inline-block;
}

.alternative_products.large .rbProductlist .item {
    border: 1px solid #222222;
}

.fancyButton.white {
    border: 1px solid #222222;
    background: linear-gradient(to bottom,#2d2c2c,#222222);
}

.ScoreCard {
    background: linear-gradient(to bottom,#2d2c2c,#222222);
    border: 1px solid #222222;
}

.ScoreCard .ScorecardView {
    background-color: rgba(29, 25, 25, 0.788235294117647);
    border: 1px solid #222222;
}

.articleColumn.userreview .reactieHeader .username {
    color: #eaeaea;
}

div.groupedReviewScoresBlock .percentageBar .innerBar {
    background-color: #2b2a2a;
    background-image: -webkit-linear-gradient(top,#8c8c8c,#999);
    background-image: -moz-linear-gradient(#8c8c8c,#999);
    background-image: linear-gradient(to bottom,#0995e8,#0671af);
    border: 1px solid #075f9e;
}

div.groupedReviewScoresBlock .percentageBar {
    background-color: #1d1c1c;
    border: 1px solid #232222;
}

.ScoreCard .ScorecardView h3 {
    color: #dedcdc;
}

ul.topKarma.show {
    background-color: #222222;
    border: 1px solid #222222;
    z-index: 4;
}

.articleColumn.userreview .authorContainer {
    border-top: 1px solid #2d2d2d;
}

div.groupedReviewScoresBlock {
    border-bottom: 1px solid #333030;
    border-top: 1px solid #403d3d;
    padding: 10px 0;
}

.articleColumn.userreview div#ScoreCardDiv+p {
    border-bottom: 1px solid #403f3f;
}

.entityHeader.edition .shield+.rating {
    border-right: 1px solid #2b2a2a;
    padding-right: 10px;
}

.greyBorderBox {
    background: linear-gradient(to bottom,#2d2c2c,#222222);
    border: 1px solid #222222;
}

.greyBorderBox>div {
    background: rgba(0, 0, 0, 0);
    border: 0px solid #d9d9d9;
}

.article h2 {
    color: #eaeaea;
    margin-bottom: 10px;
}

.article.bestbuy hr {
    background-color: #2f2828;
    margin: 20px 0;
}

#categoryBrowser div {
    background: #2b2a2a;
    border: 1px solid rgba(217, 217, 217, 0.011764705882352941);
}

ul.tabbedContent li.active {
    background: #131212;
    border-bottom: 2px solid #1085fd;
    color: #0a95cd;
}

ul.tabbedContent li {
    background: #403f3f;
    border: 1px solid #232323;
    bottom: -2px;
    color: #3a7ef3;
    cursor: pointer;
    display: inline-block;
    margin: 2px 2px 0;
    padding: 4px 17px;
    position: relative;
}

ul.tabbedContent {
    border-bottom: 2px solid #252424;
    list-style: outside none none;
    margin: 0;
    padding: 0;
}

.article img.border {
    background: linear-gradient(to bottom,#2d2c2c,#222222);
    border: 1px solid #222222;
}

.article table td {
    border-top: 0px solid #d9d9d9;
}

.pc {
    background: linear-gradient(to bottom,#2d2c2c,#222222);
    font-family: Helvetica, Arial, sans-serif;
    font-size: 15px;
    color: #444;
    line-height: 1.2;
    -webkit-text-size-adjust: 100%;
    margin: 0;
    padding: 0;
}

.pc .pc-option-link__body, .pc .pc-compare-options, .pc-fc .pc-compare-subscription, .pc-fc .pc-compare-advanced-options {
    background-color: #292929;
}



.pc .pc-result--header .pc-costs__price-upfront:before, .pc .pc-shop-offer--header .pc-shop-offer__price-upfront:before, .pc-fc-pr .pc-compare-subscription .pc-compare-subscription__unit:nth-child(3) .pc-slider__unit:after {
    content: ' Bijbetaling toestel';
    color: #0880ff;
}

.pc .pc-option-link__text-xs {
    color: #948a8a;
    float: left;
}

.pc .pc-option-link__open:before, .pc .pc-option-link__close:before, .pc .pc-option-link__reset:before {
    display: inline-block;
    background-color: #2b2a2a;
    width: 50px;
    padding: 5px 6px;
    border-left: 1px #383434 solid;
    text-align: center;
    color: #3e94bf;
}

.pc .pc-option-link {
    border-color: #292929;
    border-radius: 0;
    background-color: #292929;
}

#about .button {
    background-image: linear-gradient(to bottom,#262626,#222222);
    border-radius: 2px;
    box-shadow: 1px 1px 1px 0 rgba(0,0,0,0.16);
    color: white;
    display: inline-block;
    height: 28px;
    width: 128px;
}

}

@-moz-document url-prefix("https://gathering.tweakers.net/") {
																	/*** Forum ***/
body {
    background-color: #222;
}

#top {
    background: linear-gradient(to bottom,#222222,#2d2c2c);
    border-bottom: 1px solid #312b2b;
}

#searchbar {
    border-radius: 6px 6px 0 0;
    background: rgb(34, 34, 34);
}

body {
    background-color: #222;
    /* color: black; */
    margin: 0;
    min-height: 100%;
    min-width: 1000px;
    padding: 0;
    position: relative;
}

#contentArea {
    background: #222;
    margin-bottom: 20px;
    padding-bottom: 20px;
    padding-top: 20px;
}

table.listing td {
    background-color: #f7f7f7;
    background-image: -webkit-linear-gradient(top,#f7f7f7,#f2f2f2);
    background-image: -moz-linear-gradient(#f7f7f7,#f2f2f2);
    background-image: linear-gradient(to bottom, #2b2a2a, #222);
    border-bottom: 1px solid #312e2e;
    border-top: 0px solid #fafafa;
