css:
@-moz-document domain("eiga.com") {
.site-header,
.content-aside,
.ad-box,
.yads_ydn,
.poweredby,
.site-footer,
.breadcrumb,
#ad_wrapper_bottom,
.sns-link,
.content-full-wrapper,
.content-main .bookmarks,
.article-txt .article-writer,
.slide-show,
.sub-toolbar,
#rental,
.nav-simple,
.movie-impresses,
.post-device,
.related-box,
.col-s-2,
.row.item-5 .col-5,
.link-basic [href*="dvd"],
.link-basic [href*="magazine"],
.link-basic [href*="extra"],
.link-basic [href*="motion"],
.link-basic [href*="job"],
.link-basic [href*="free"],
.link-primary [href*="movie/video"],
.osorezone-pickup
{display:none !important;}

.content-container .content-main
{width:100%;}

.article-image img
{max-width:100%; width:90%;}

.title-xlarge
{margin-top:10px;}

.content-container
{width:93%;}
}

@-moz-document url-prefix("https://eiga.com/news/") {
.article-txt .article-writer,
.list-tile, .ranking-tile,
.title-xlarge
{display:none !important;}

.head-line .txt,
.head-line .time
{width:75%;}

.head-line .txt
{float:none;}
}

@-moz-document url-prefix("https://eiga.com/movie/") {
.btn,
.hide .award-item,
.col-s-4
{display:none !important;}
}

@-moz-document url-prefix("https://eiga.com/person/") {
.related-box
{display:none !important;}


.head-line .txt,
.head-line .time
{width:75%;}

}

@-moz-document url-prefix("https://eiga.com/now/"), url-prefix("https://eiga.com/upcoming/"), url-prefix("https://eiga.com/coming/") {
.list-block .txt-box
{width:78%;}
}
