css:
@-moz-document domain(terchan.xyz) {
body:not(#menu) {
    background-image: url(https://i.imgur.com/Vy1Gt8r.gif), url(https://i.imgur.com/pPHRerx.png);
    background-repeat: no-repeat, no-repeat;
	color:#CCC;
	text-shadow: 0px 8px 15px black;
    background-size: 15%, 100%;
	background-attachment: fixed, fixed;
	background-position: bottom right 16%, right 30%;
    hyphens: auto;
    word-wrap: break-word;
    word-break: normal;
}

.logo {
    clear: both;
    text-align: center;
    font-size: 35px;
    width: 100%;
	  font-weight: bold;
    font-family: arial black;
    -webkit-transition-duration: 0.2s;
    animation: hilight2 1s linear 1s infinite alternate;
    -webkit-animation: hilight2 1s linear 1s infinite alternate;
}

.omittedposts {
    font-size: 11px;
    font-family: sans-serif;
    color: #CCC;
    display: inline-block;
	margin-top: 0px;
	margin-left: 15px;
}

input,textarea,select {
	border: 1px dashed #005e7e;
	background-color: rgba(0, 0, 0, 0.5);
	color: #CCC;
    margin: auto;
}


@keyframes hilight2
{
0%   {color: yellow;text-shadow: 0 0 0 darkblue, 0 0 11px black;}
100% {color: yellow;text-shadow: 0 0 10px yellow;}
}

td.reply {
    background-color: rgba(0, 0, 9, 0.6);
    font-family: sans-serif;
    color:#CCC;
    border:dashed 1px #005e7e;
    margin-right: auto;
}

.postername {
  color: hotpink;
  font-weight: bold;
  font-family: "Roboto";
  font-style: normal;
}

.postername:hover {
     color: white;
    text-decoration: none !important;
    -webkit-transition-duration: 0.2s;
    text-shadow: 0 0 0 white, 0 0 11px white;
}

.postertrip {
	color: red;
}

.postblock {
    margin-top: 30px;
    color: yellow;
    font-family: sans-serif;
    font-weight: bold;
    background-color: transparent;
    text-shadow: 0 0 3px yellow!important;
}

form {
    z-index: 0;
    position: relative;
    width: 100%;
    margin: 0 auto !important;
    background-color: rgba(0, 0, 9, 0.8);
}

#counter {
    color: yellow!important;
    font-weight: bold;
    font:courier-new;
    text-shadow: 0 0 5px yellow!important;
}

.replymode {
    background-color: transparent;
    border-radius: 15px;
}

a {
	color: yellow;
	text-decoration: none;
	font-family: sans-serif;
}

a:visited {
	text-decoration: none;
	font-family: sans-serif;
    -webkit-transition-duration: 0.2s;
    text-shadow: 0 0 0 yellow, 0 0 11px yellow;
}

a:hover {
	color: #DD0000;
	background: inherit;
	font-family: sans-serif;
    text-decoration: none !important;
    -webkit-transition-duration: 0.2s;
    text-shadow: 0 0 0 yellow, 0 0 11px yellow;
}

a:link,
a:visited,
p.intro a.email {
    color: yellow;
    text-decoration: none;
}
a:link:hover {
    color: yellow;
    text-decoration: none;
}
.manageinfo {
	background: #00B930;
	color: #FFFFFF;
	width: 100%;
}

.catalogmode {
    background-color: rgba(0, 0, 9, 0.9);
	color: #CCC;
	width: 100%;
}

hr {
    display:block;
    margin: auto;
    padding: 0;
    width:100%;
    border-style: dotted none none none;
    border-width: 1px;
    border-color: #005e7e;
    background:transparent;
}
    
#delform hr {
    display:block;
	margin: 20px 1px 10px;
    width:100%;
    border-width: 1px;
    border-color: #005e7e;
    background:transparent;
}
    
.reflink a:hover {
	font-weight: bold;
}

.passvalid {
	background: #9988EE;
	text-align: center;
	width: 100%;
	color: #ffffff;
}

.dellist {
	background: inherit;
	text-align: center;
}

.delbuttons {
	background: inherit;
	text-align: center;
	padding-bottom: 4px;
}

.managehead {
	background: #0F8FE1;
	color: #000000;
	font-family: sans-serif;
	font-size: 14px;
	padding: 0;
}

s, s a:not(:hover) {
    color: #101047 !important;
    background: #101047 !important;
    text-decoration: none;
}

.unkfunc {
    font-weight:bold;
    font-family: comic sans MS;
}
}
