css:
@-moz-document domain("www.memrise.com") {
/** general and /home */
html, body,
h1, .h1, h2, .h2, h3{
	color: #d5d5d5; /** set default font color */
}
/** header bar */
.header-v2,
.footer{
	background-color: black;
}
/** active navigation point */
.header-nav .header-nav-item.plain.is-active{
	background: rgba(255,255,255,0.1);
}
/** back maincolor, dark grey */
body, html,
.dashboard {
	background-color: #101010;
}
/** back secundarycolor, middle grey */
#page-head,
.course-card-container .course-progress-box{
	background-color: #232222!important;     
}
#page-head{
	border-bottom: 1px solid transparent;
}
.footer{
	border-top: 1px solid transparent;
}
/** sidebar */
.sidebar-box{
    background-color: #232222!important;     
    border: 1px solid #7d7d7d;
}
/** headings */
.tabbed-main h1{
    color: #d5d5d5; 
}
/** box text */
.course-card-container .course-progress-box .card-top .card-main-container .detail .title a{
	color: #d5d5d5; 
}
/** Punkte heute, Lauf */
select,
.course-card-container .goal-setter .goal-setter-progress,
.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn.active,
.course-card-container .goal-setter .streak-row .streak-fake-btn,
.course-card-container .goal-setter .streak-edit .edit-goal-btn{
	background-color: #d5d5d5;    
    color: #232222;
}
/** Lauf text */
.course-card-container .goal-setter .streak-row .streak-fake-btn .text{
    color: #232222;
}
/** card bottom, light grey */
.course-card-container .course-progress-box .card-bottom{
    background-color: #333333!important;     
}

/** card button, highlight */
.mode-selector-button{
    background-color: #d5d5d5;
}
/** progress bar */
.course-card-container .course-progress-box .card-top .card-main-container .course-progress .progress-bar-container{
	background-color: #333333;
}
/** daily goal */
.course-card-container .goal-setter{
    background-color: #333333;
}
/** Sidebar */
.sidebar-profile .profile-content .content-stats .left .text, 
.sidebar-profile .profile-content .content-stats .right .text,
.sidebar-premium .premium-content .text{
	color: #7d7d7d;
}
/** Laufuhr */
.countdown-body .countdown-text{
	color: #7d7d7d;
}
.countdown-body .countdown-duration .time-block{
	color: #7d7d7d;
    background-color: #333333;
}
/** Bestenliste */
.leaderboard{
    background-color: #232222;
    border-color: #7d7d7d;
}
/** Followers */
.box-onboarding.box-follow,
.box-onboarding.box-follow .people-rows .row-fluid{
	border-color: #333333;
}
.user-box .username{
    color: #7d7d7d;
}
/** loading boxes */
.loading-item{
	background-color: #232222;
    border-color: #232222;
}
.loading-item .loading-animation{
    margin: 0 auto;    
    background: #232222;
	border: 8px solid #333;
  	border-radius: 50%;
  	border-top: 8px solid #d5d5d5;
  	width: 50px;
  	height: 50px;
  	-webkit-animation: spin 1.5s linear infinite; /* Safari */
  	animation: spin 1.5s linear infinite;
}
/* Safari */
@-webkit-keyframes spin {
  0% { -webkit-transform: rotate(0deg); }
  100% { -webkit-transform: rotate(360deg); }
}
@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}
.loading-item .loading-animation > *{
	display: none;
}


}

@-moz-document domain("www.memrise.com") {
/** inherit color from parent */
.dashboard section .wrapper{
    background-color: inherit!important;     
}
.sidebar-profile .profile-content .content-stats{
    background-color: inherit!important;     
}
body.levels .levels .level .level-index{
    background-color: inherit!important;     
}
}

@-moz-document domain("www.memrise.com") {
/** modal */
/** 101010, 232222, 333333, 7d7d7d, d5d5d5 */ 
.modal-content{
	background-color: #101010;
    box-shadow: 0px 0px 10px 0px #ffffff;
}
#goal-setter-popup .modal-body .modalContent .header{
	background-color: #232222;	
    border-bottom: none;
}
#goal-setter-popup .modal-body .modalContent .actions{
    background-color: #232222;
    border-top: none;
}
.goal-choices .goal-choice.active{
	background-color: #333333;	
}
.modalContent p{
	color: #d5d5d5;
}
.modal-footer{
    background-color: #232222;	
}
.btn:hover, .btn.hover, button:hover, button.hover, input[type="button"]:hover, input[type="button"].hover, input[type="reset"]:hover, input[type="reset"].hover, input[type="submit"]:hover, input[type="submit"].hover, button[type="submit"]:hover, button[type="submit"].hover{
	background-color: #232222;	
    color: #d5d5d5; 
}
.btn-primary:hover, .btn-primary.hover, input[type="submit"]:not(.btn-default):hover, input[type="submit"]:not(.btn-default).hover, button.btn-primary:hover, button.btn-primary.hover{
	background-color: #5cbdf2;
}
/** fix button position */
#goal-setter-popup .modal-body .modalContent .actions .btn{
	left: 69%;
}
}

@-moz-document url-prefix("https://www.memrise.com/course") {
/** course page */
#left-area p{
	color: #d5d5d5; /** set default font color */
}
.question-row .qquestion{
	color: white;
}
.extra-info{
	background-color: #333333!important;
    color: white;
}
.session-header.review,
#page-header.review{
	background-color: #176ea8;
}
.btn-primary, input[type="submit"]:not(.btn-default), button.btn-primary{
	border-color: #176ea8;
}
.btn.active, .btn:active, button.active, button:active, input[type="button"].active, input[type="button"]:active, input[type="reset"].active, input[type="reset"]:active, input[type="submit"].active, input[type="submit"]:active, button[type="submit"].active, button[type="submit"]:active{
	background-color: #101010;
}

.garden-box.end_of_session .course_box .bottom .upper .edit {    
    background-position: center;
    background-size: 20px;    
    right: 0;
    height: 17px;
    width: 17px;
    top: 2px;    
    background-color: #bcbbbb;
    border-radius: 14px;    
    box-shadow: 0 0 6px white;
}



.garden-box .hint{
	text-shadow: none;
}
.btn, button, 
input[type="button"], 
input[type="reset"], 
input[type="submit"], 
button[type="submit"],
.btn-inverse, 
input[type="submit"].btn-inverse, 
button.btn-inverse{
    color: #d5d5d5;
    background-color: #333333;
    background-image: none;
    text-shadow: none;
}
.btn-inverse, 
input[type="submit"].btn-inverse, 
button.btn-inverse{    
    background-color: #333333;
	text-shadow: none;
}
button.next-button .next-text{
	color: #9a9a9a;
}
.session-header.learn {
    background-color: #177b2a;
}
#page-header.learn {
    background-color: #177b2a;
}
.garden-box .shiny-box{
	background-color: #7d7d7d;
    border-color: #7d7d7d;
    color: black;
}
.thing-show .row.primary .row-value{
	text-shadow: none;
    color: white;
}
.thing-show .row .row-value{
    color: #d5d5d5; /** set default font color */
}
.thing-show .columns .show-more-link,
.thing-show .columns .show-less{
    color: #7d7d7d;
    background-color: #333333;
    background-image: none;
    text-shadow: none;
}
.shiny-box.roundy.typing-type-here::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  	color: #7d7d7d;
}

.garden-box .typing-wrapper .typing-type-here{
    cursor: url('data:image/png;base64,') 24 24, auto;
}
/** end of course - summary page */
.garden-box.end_of_session .course_box .top .info .header{
	color: #7d7d7d;
}
.whitebox{
	background-color: #232222;
}
.leaderboard{
    background-color: #333333;    
}
.garden-box.end_of_session .course_box .bottom,
.leaderboard .leaderboard-content .leaderboard-rows{
	background-color: #232222;    
}
.leaderboard .leaderboard-content .leaderboard-rows .leaderboard-row{
	color: #d5d5d5; /** set default font color */
}
.leaderboard .leaderboard-content .leaderboard-rows .leaderboard-row:hover{
	background-color: #7d7d7d;    
}
.garden-box.end_of_session .course_box .top .levels{
	background-image: none;
    background-color: #7d7d7d;
}
/** course level overview page */
body.levels .levels .level{
	background-color: #333333;
}
body.levels .levels .level{
    color: #d5d5d5; /** set default font color */
}
.level-ico.level-ico-plant-inactive.level-ico-s{
    filter: brightness(150%);
}
body.levels .levels .level .level-icon{
	background-color: #232222;
}
body.levels .levels .level .level-status{
	color: #7d7d7d;
}
.reverse-header-ruled #page-head {
    background-color: #4f3a6f;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#4f3a6f), to(#1e132f));
    background-image: linear-gradient(to bottom, #4f3a6f, #1e132f);
}
/** course single level wordlist page */
.course-tabs-wrap{
    background-color: #333333;
    border-bottom: none;
}
ul.nav-pills>li>a, .nav-pills>li>a, .nav.nav-pills>li>a{
    color: #d5d5d5; /** set default font color */
}
.progress-box{
	background-color: #333333;    
}
.progress-box .progress-box-title{
    color: #d5d5d5; /** set default font color */
}
.things{
    background-color: #333333;
}
/** chat bot */
.session-header{
    background-color: black;    
}
.session-header .header-block.header-left{
	border-right-color: rgba(213, 213, 213, 0.14);
}
.session-header .header-block.header-right{
	border-left-color: rgba(213, 213, 213, 0.14);
}

.session-header .header-block.header-middle .course-details-wrapper .course-details .course-info,
.session-header .header-block.header-middle .course-details-wrapper .course-details .course-title{
	color: #d5d5d5;
    text-shadow: none;
}
.chat-content{
	background-color: #101010;
    box-shadow: 0 0 10px #d5d5d5;
}
.chat-content .chat-list .chat-message .chat-bubble{
    background-color: #232222;
}
.chat-content .chat-user-input{
	background-color: #232222;
}
.chat-content .chat-choices-container{
    background-color: #101010;
}

/** replace svg with css close icon */
.session-header .header-exit svg {
    position: inherit;
    width: 28px;
    height: 28px;
    top: 0;
    left: 0;
    transform: translateY(0);
    display: none;
}
.session-header .header-exit a {    
  position: absolute;
  right: 0px;  
  width: 28px;
  height: 28px;
  top: 50%;
  transform: translateY(-50%);
  opacity: 0.3;
}
.session-header .header-exit a:hover {
  opacity: 1;
}
.session-header .header-exit a:before, 
.session-header .header-exit a:after {
  position: absolute;
  left: 15px;
  content: ' ';
  height: 28px;
  width: 2px;
  background-color: #d5d5d5;
}
.session-header .header-exit a:before {
  transform: rotate(45deg);
}
.session-header .header-exit a:after {
  transform: rotate(-45deg);
}
.session-header .header-block.header-right .header-exit .session-exit{
	background-image: none;
}
}

@-moz-document regexp("https://www.memrise.com/.*/courses/.*") {
/** courses page */
/** 101010, 232222, 333333, 7d7d7d, d5d5d5 */ 
.course-box{
	background-color: #232222;
    border: 1px solid #333333;
}
.course-box .details-wrapper .target-photo{
    background-image: none;
    background: #232222;
}
.course-box .inner-wrap{
	border: 1px solid #333333;
    background-image: none;
    background: #232222;
    color: #d5d5d5;
}
.course-box h3 .inner{
	color: #d5d5d5;
}
.course-box h3 .inner:hover{
    color: #fff;
}
.course-box .details-wrapper .details .stat,
.course-box .details-wrapper .details .stat:last-child{
	box-shadow: none;
}
.course-box .details-wrapper .details .stat{
	border-right: 1px solid #333333;
}
.course-box .details-wrapper .details{
	border-top: 1px solid #333333;
    background-image: none;
}
#page-head .inner .page-breadcrumb{
	color: #d5d5d5;
}
.categories-list{
    background: #232222;    
    border-color: #333333;
}
.categories-list>li{
    border-color: #333333;
}
.categories-list>li>a{
    color: #d5d5d5;
}
.infinite-scroller .infinite-scroller-trigger{
	background: #232222;    
	border-color: #333333;
}
}

@-moz-document regexp("https://www.memrise.com/user/.*/mems/created/") {
.thing-show .row.primary-smaller .row-value{
	text-shadow: none;
}
.thing-show .row .row-value{
	color: #d5d5d5;
}
}
