css:
@-moz-document regexp("^.*binance.com/trade.html.*$") {
*, 
*:before, 
*:after {
    transform-style: flat;
	transition: 
    	border-color 300ms linear,
        background-color 300ms linear,
        transform 300ms linear,
        opacity 300ms linear;
}

body, 
.main { 
    background-color: #10161c !important; 
}

.index-news, 
.footer {
	background: rgba(255,255,255,0.04) !important; 
}

.main-left, 
.main-con, 
.main-aside {
    top: 0 !important; 
	background: #20262c !important; 
	border: none !important; 
    border-radius: 3px!important;
    min-height: 692px !important; 
}

.main-con {
	margin: 0 15px !important; 
    padding: 0 !important; 
}

.main-con .fee {
    display: none !important; 
	color: #f0f0f0 !important; 
	margin: 0 !important; 
    padding: 0 8px 0 0 !important; 
	height: auto !important; 
	line-height: 3em !important; 
	background-color: rgba(255,255,255,0.2) !important; 
}

.scrollStyle .noData {
	background: rgba(255,255,255,0.06) !important;
}

.productSymbol.has-info strong {
	color: #f0f0f0 !important; 
}

.productSymbol.has-info .info-box a span {
	color: #bbb !important; 
    font-weight: bold !important; 
}

.depths {
    margin: 0 !important;
	background: transparent !important; 
	border: none !important; 
}

.depths .depthstitle {
	padding: 8px !important;
	background-color: rgba(255,255,255,0.1) !important;
}

.depths .newest {
    margin: 2px 0 !important;
	padding: 10px !important;
	background-color: rgba(255,255,255,0.1) !important;
}

.depths .newest > strong {
	color: #f0f0f0;
}

.depths .newest .depthwrong {
	top: 13px !important;
}

.depths .table th {
	height: auto !important;
	line-height: 3em !important;
}

.depths .table tr td {
    padding: 2px 8px !important;
	color: #888;
}

.depths .table tr:hover td span {
	color: #f0f0f0;
}

.depths .table td:first-of-type {
	padding-left: 8px !important;
}

.depths .zhuzhuang {
	background-color: rgba(255,255,255,0.05) !important;
    top: 1px; 
    bottom: 1px; 
}

.depths .depthstitle .tab-box {
	margin: 0 0 8px 0 !important;
}

.depths .tab-box li {
	border: none !important;
	background: #f0f0f0 !important;
	border-radius: 3px !important;
}

.depths .depthstitle img {
	border: none !important;
	border-radius: 3px !important;
}

.depths .depthstitle .searchFilter {
    border-radius: 3px !important;
	background: rgba(255,255,255,0.1) !important;
	padding: 0 !important;
}

.overvies {
    margin: 0 0 15px 0 !important;
	background-color: transparent !important;
	border: none !important;
}

.overvies #kline-con {
	background-color: transparent !important;
}

.overvies .kline-timeline {
	height: 38px !important;
	margin: 0 !important;
	padding: 0 8px !important;
	border: none !important;
	background-color: rgba(255,255,255,0.1) !important;
	line-height: 38px !important;
}

.overvies .kline-timeline span {
	padding: 0 8px !important;
	height: 20px !important;
	line-height: 20px !important;
	margin-right: 5px !important;
	border-radius: 3px !important;
    float: none !important;
    clear: both !important;
}

.overvies #chart_container, 
.overvies #chart, 
.overvies #p, 
.overvies #p #depth, 
.overvies #p #depth .box-inner {
    margin: 0 !important;
    padding: 0 !important;
    border: 0 !important;
}

#kline-con .chart canvas:nth-of-type(1),
#kline-con #depth canvas:nth-of-type(1) {
    background-color: #fff !important;
    color: #000 !important;
    text-shadow: none !important;
    filter: invert(90%);
}
#kline-con .chart canvas:nth-of-type(6) {
    filter: invert(1);
}
#kline-con #depth canvas:nth-of-type(2),
#kline-con #depth canvas:nth-of-type(3) {
    filter: invert(1) hue-rotate(180deg);
}

.orderforms {
	background-color: transparent !important; 
	border: none !important; 
}

.orderforms .orderform-type {
    display: flex !important; 
    flex-direction: row; 
    align-items: stretch;
    justify-content: center;
	position: relative;
	left: 0 !important; 
	top: 0 !important; 
	margin: 0 !important; 
    padding: 0 !important; 
    border: 0 !important; 
	z-index: auto !important; 
    background-color: transparent !important; 
}

.orderforms .orderform-type span {
	display: block !important; 
    margin: 0 !important; 
    padding: 0 !important; 
    border: 0 !important; 
    text-align: center !important; 
    font-size: inherit; 
    line-height: 2.5em;
    height: auto;
    background-color: rgba(255,255,255,0.06);
    color: #f0f0f0; 
    flex: 1; 
}

.orderforms .orderform-type span:hover {
	background-color: rgba(255,255,255,0.08);
}

.orderforms .orderform-type span.cur {
	background-color: rgba(255,255,255,0.1);
    color: #e8b342; 
}

.orderforms .orderform-type .toolTip {
	display: none; 
}

.orderforms .orderform-main > .f-fl, 
.orderforms .orderform-main > .f-fr {
	background-color: transparent !important; 
	margin: 0!important;
	padding: 15px 22px !important;
}

.orderforms .f-fl {
	border-right: 1px solid rgba(255,255,255,0.1) !important; 
}

.orderforms .orderforms-hd span {
	color: #e8b342 !important;
}
 
.orderforms .orderforms-hd label {
	color: #aaa !important;
}

.orderforms .orderforms-inputs .field.percent .col {
	height: auto !important;
    border: none !important;
	border-radius: 3px!important;
	background: rgba(255,255,255,0.08) !important; 
	color: #ccc !important;
}

.orderforms .orderforms-inputs .field.percent .col:hover {
	background: rgba(255,255,255,0.1) !important; 
	color: #e8b342 !important;
}

.orderforms .orderforms-inputs input {
	border: 1px solid transparent !important;
	color: #fff !important;
	background-color: rgba(255,255,255,0.2) !important;
	border-radius: 3px !important;
}

.orderforms .orderforms-inputs .iptwrap .jiantou {
	border: none !important;
	background-color: transparent !important;
}

.orderforms .orderforms-btn .btn {
	height: auto !important;
	line-height: 3em !important;
	border-radius: 3px!important;
}

.market-box .market-t {
	height: auto !important;
	line-height: 2em !important;
	border: none !important;
}

.market-box .market-con {
	height: 385px !important;
}

.market-box .market-con ul {
    height: auto !important;
	line-height: 1.1em !important;
    padding: 2px 0 !important;
	background: transparent !important;
    color: #ccc !important;
}

.market-box .market-con ul:nth-child(2n) {
	background: rgba(255,255,255,0.04) !important;
}

.market-box .market-con ul:hover {
	background: rgba(255,255,255,0.1) !important;
}

.market-box .market-con ul.selectedPro {
	background: rgba(255,255,100,0.2) !important;
}

.market-box .market-con ul li {
	color: inherit !important;
}

.market-box .market-con ul li:nth-child(2) {
	font-weight: bold !important;
}

.search-box, 
.search-box input {
	border: none !important;
	background-color: transparent !important;
}

.tradehistory .item-title {
	height: auto !important;
	line-height: 3em !important;
	font-size: 14px !important;
	color: #f0f0f0 !important;
    background-color: rgba(255,255,255,0.1) !important;
	margin: 0 !important;
    padding: 0 0 0 8px !important;
}

.tradehistory .item-title .item-title-btns span {
	border: none !important;
}

.tradehistory .item-con {
	margin: 0 !important; 
    border: none !important; 
}

.tradehistory .item-con .scrollStyle {
	height: 162px !important; 
}

.tradehistory table td {
	padding: 5px 8px 0 8px !important;
	border: none !important;
    color: #999;
}

.userRecords {
    margin: 15px 0 0 0 !important;
    padding: 0 !important; 
    border: none !important; 
    background: #20262c !important; 
    border-radius: 3px !important;
}

.userRecords .userRecords-title {
	line-height: 1.2em !important;
	padding: 8px !important;
	background-color: rgba(255,255,255,0.1) !important;
}

.userRecords .userRecords-title span {
	color: #f0f0f0;
}

.userRecords .userRecords-title .f-fr {
	margin: 0 0 0 5px !important;
	background-color: rgba(255,255,255,0.1) !important;
	border-radius: 3px !important;
	padding: 2px 8px !important;
    color: #ccc;
}

.userRecords .item-con {
	border: none !important; 
    background: transparent !important; 
}

.userRecords .table .btn {
	background: rgba(255,255,255,0.2);
	color: #f0f0f0 !important; 
	border: none !important; 
    border-radius: 3px !important;
}

.userRecords .table .btn:hover {
	background: rgba(255,255,255,0.1);
}

.userRecords .table thead th {
    height: auto !important;
    font-weight: bold;
}

.userRecords .table tr td:nth-child(1), 
.userRecords .table tr td:last-child, 
.userRecords .table tr th:nth-child(1), 
.userRecords .table tr th:last-child {
	padding: 8px !important;
}

.userRecords .table tr th, 
.userRecords .table tr td {
    color: #aaa;
	background-color: rgba(255,255,255,0);
    border: none !important; 
}

.userRecords .scrollStyle {
	height: 216px !important;
}

.userRecords-main .table th.cancels .iconfont {
	margin-top: 0 !important;
	border: none !important;
}

.userRecords .scrollStyle .table tr:nth-child(odd) th, 
.userRecords .scrollStyle .table tr:nth-child(odd) td {
	background: rgba(255,255,255,0.04);
}

.userRecords .scrollStyle .table tr:hover th, 
.userRecords .scrollStyle .table tr:hover td {
	background-color: rgba(255,255,255,0.1) !important;
    border: none !important; 
}

.footer, 
.footerTWrap, 
.footerB {
	background-image: none !important; 
}
}
