css:
@-moz-document domain("primewire.ag"), domain("primewire.is"), domain("primewire.org") {
[class*='vpn'],
.info_message,
.col2,
.footer,
iframe,
.comment_box,
h2.sub_titles,
h1.titles,
.show_reviews,
.tolerance_box {
	display: none;
}
.main_body,
.header,
h1.titles span,
h1.titles,
.featured_movie_item a,
#menu-bar ul li:hover,
.movie_version,
body {
	background: none;
}
body,
html {
	background-color: #333;
	color: #777;
}
.index_item img:hover {
	border: #5BA2D8 solid 1px;
}
.index_item a:hover {
	color: #5BA2D8;
}
.index_item a {
	color: #666;
}
.listing_hd_link a {
	background-color: #254a66;
}
.unit-rating,
#menu-bar a img {
	filter: brightness(0%);
}
#menu-bar ul li a {
	color: #499bd8;
}
#menu-bar ul li ul,
.movie_version {
	border: 1px solid #000;
	background: #2c2c2c;
}
#menu-bar ul li ul li a:hover {
	background: #3e3e3e;
}
[class*="mlink_"] a,
ul#multi_drop,
[class*="quality_"],
.search_container .btn {
	filter: contrast(90%)brightness(40%);
}
.movie_info p {
	color: #777;
}
div.pagination a {
	border: 2px;
}
.search_container input.box {
	background: #0006!important;
}
}

@-moz-document domain("reelvidz.com") {
.wrapper .wrapper,
.text.center,
.box.helper,
.box.gray-lighter,
.shadowstrong,
.screen-overlay {
	display: none !important;
}
}
