.message .message__sender a {
    color: #4a0e0e;
}
.message__body {
    box-sizing: border-box;
    background-color: #ffffff6e;
    border-radius: 10px;
    max-width: 500px;
    padding: 2px 10px 5px;
    box-shadow: -1px 1px 4px #d0d0d0;
}
.message--sended .message__body {
    background: #1de8a991;
}
.message--sticker.message--sended .message__body {
    background: transparent;
    box-shadow: none;
}
.recent__item {
    background-color: #fff
}
.recent__item__counter {
    font-size: 14px;
    font-weight: 500;
    line-height: 24px;
    display: inline-block;
    box-sizing: border-box;
    min-width: 24px;
    height: 24px;
    margin: 0 2px 0 0;
    padding: 0 4px;
    text-align: center;
    color: #fff;
    border-radius: 12px;
    background-color: #10c592;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.recent__item--deActive {
    background: #b6bbbb;
}
.recent__item:hover {
    background: linear-gradient(to right, #42c79ccc 0%, #346381cc 100%);
}
.recent__item--active {
    background-color: #00986c!important;
}
.scroller__container::-webkit-scrollbar-thumb {
    background-color: #c5bdbb;
}
.sidebar__header__user .dropdown__button,
.sidebar__header__user__name {
    color: #fff!important;
}
.sidebar__header .dropdown__menu {
    background-color: #737677;
    top: 47px;
    min-width: 255px;
    margin-right: 20px;
}
.sidebar__header .dropdown__menu {
    background-color: #737677;
    top: 47px;
    min-width: 255px;
    margin-right: 20px;
}
.dropdown__menu__item:hover {
    color: #060606;
    background-color: #aebcce;
}
.dropdown__menu__item {
    font-size: 15px;
    line-height: 22px;
    overflow: hidden;
    padding: 10px 50px 10px 16px;
    cursor: pointer!important;
    color: #eeeeee;
}
.dropdown__menu__item a {
    display: block;
    text-decoration: none;
    color: #eeeeee;
}
.dropdown__menu__item:hover a {
    color: #000;
}
.toolbar__main.LEGAL,
.toolbar__main.VERIFIED {
    background-color: #046508;
}
.chat__messages {
    background-size: cover;
    background-repeat: no-repeat;
    background-image: url("https://fileserver-c002.bale.ai:443//928521122-7853894085252087552");
    position: relative;
}
.message--active,
.message:hover {
    background-color: #eaeaea8f;
}
.message .message__sender a .message__sender__name {
    color: inherit!important;
    font-weight: bold;
}
.avatar__image,
.avatar__placeholder {
    border-radius: 50%!important;
    height: 40px;
    width: 40px;
}
}
