css:
@-moz-document regexp("https?://learnvimscriptthehardway.stevelosh.com.*") {
hr,
body,
div.container,
header#top,
main.doc,
div.body
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat;
    color: #aaa;
}

hr {
    margin-top: 0px;
    margin-bottom: 0px;
    border: 0;
    box-sizing: inherit;
}

article.container,
div.document 
{
    background: #012028;
}


section.content h1, section.content h2, section.content h3, section.content h4, section.content h5, section.content h6
{
    background: none;
    color: #3776A3 !important;
}

div.related,
div.sphinxsidebar 
{
    background-color: #111;
}

div.sphinxsidebar input {
    border: 1px solid #333;
}

div.note {
    background-color: #111;
    border-color: #000;
}

div.related:first-child {
    border-color: #AAA;
}

div#sidebarbutton {
    background-color: #000 !important;
}

a {
    color: #4983AB !important;
}

tt
{
    background: none;
}

pre 
{
    background: #222;
    color: #AAA;
}

.highlight .go /*output*/
{
    color: #AAA;
}

.highlight .gt, .highlight .nf /*tracebacks*/
{
    color: #4772D1;
}

.highlight .nd {
    color: #aaa;
}

table.docutils thead tr {
    background-color: #322;
}

table.docutils tbody tr.row-even {
    background-color: #222;
}

table.docutils tbody tr.row-odd {
    background-color: #181818;
}

table.docutils td, table.docutils th
{
    background-color: inherit;
    border-color: #333;
}

select, input 
{
    background-color: #111;
    color: #AAA;
}

input[name="q"] {
    background-color: #333;
}

select
{
    border-color: #000;
}

dt:target, .highlight {
    background-color: #7C6600;
    color: #EEE;
}

div.warning,
.deprecated, .deprecated-removed {
    background-color: #211;
}

div.seealso {
    background-color: #221;
}

code {
    background-color: inherit !important;
}
}
