css:
@-moz-document domain("www.youtube.com") {
body {
    overflow-y: scroll !important
}
ytd-app {
    margin: unset !important
}
#masthead-container {
    padding: unset !important
}
#container, #search-icon-legacy, #masthead, #scrim, #guide-content, #header, ytd-guide-section-renderer, #guide-links-primary, #contentContainer, ytd-watch, #content, ytd-two-column-browse-results-renderer, ytd-playlist-sidebar-renderer, ytd-playlist-sidebar-primary-info-renderer, #alerts, ytd-browse, #channel-container, #tabs-container, #channel-header, #tabs-inner-container, #selectionBar, ytd-video-renderer, .ytd-playlist-panel-renderer, #chat, #chatframe, #show-hide-button, #show-hide-button > ytd-toggle-button-renderer, #document, html, body, body > yt-live-chat-app, #contents, #contents > yt-live-chat-renderer, #content-pages, #chat-messages, #chat-messages > yt-live-chat-header-renderer, #contents, #panel-pages, #action-panel, #action-panel > yt-live-chat-message-input-renderer, #items, yt-live-chat-item-list-renderer, yt-live-chat-item-list-renderer, #menu, #menu-button, yt-live-chat-text-message-renderer, #menu.yt-live-chat-text-message-renderer {
    border-color: #00000000 !important;
    background-color: #00000000 !important
}
ytd-toggle-button-renderer.style-default-active:nth-child(1), #like-button.style-default-active {
    color: #2288ff !important
}
ytd-toggle-button-renderer.style-default-active:nth-child(2), #dislike-button.style-default-active {
    color: #ff1122 !important
}
ytd-app {
    background-color: #000000 !important
}
:after {
    box-shadow:  inset 0px 4px 8px -3px rgba(0, 0, 0, 0) !important
}
#page-manager {
    margin-top: var(--ytd-masthead-height, 32px) !important
}
#guide-content>div>yt-icon-button>button>yt-icon>svg>g>path, #guide-content>div>ytd-topbar-logo-renderer>a>div>svg>g>g>path, #guide-content>div>ytd-topbar-logo-renderer>a>div>svg>g>g>polygon, #guide-content>div>ytd-topbar-logo-renderer>a>div>svg>g>g>g>path, #guide-content>div>ytd-topbar-logo-renderer>span, yt-live-chat-text-message-renderer {
    fill: #00000000 !important;
    color: #00000000 !important
}
#menu.yt-live-chat-text-message-renderer {
    background: linear-gradient(to right, transparent, var(--yt-live-chat-background-color, hsl(0, 0%, 0%)) 1000%) !important
}
#contentContainer.app-drawer, ytd-guide-renderer.ytd-app {
        width: 400px !important
}
#page-manager {
    margin-left: 0px !important
}
ytd-browse[persistent-guide] ytd-two-column-browse-results-renderer.ytd-browse {
    width: 1284px !important
}
ytd-browse[persistent-guide] ytd-playlist-sidebar-renderer.ytd-browse {
    left: 0 !important
}
ytd-browse[page-subtype="playlist"] ytd-two-column-browse-results-renderer.ytd-browse {
    width: 100% !important
}
ytd-searchbox[has-focus] #container.ytd-searchbox {
    box-shadow: inset 0 1px 2px var(--ytd-searchbox-legacy-border-shadow-color) !important
}
}
