css:
@-moz-document domain(www.google.com) {
  * {
      background-color: #2f3640 !important;
      color: white;
  }

  #content {
      background-color: #2f3640 !important;
  }

  html::-webkit-scrollbar, html::-webkit-scrollbar-corner {
      width: 5px;
      height: 5px;
      background-color: #2d3436 !important;
      transition: 0.25s;
  } html::-webkit-scrollbar-track:hover, html::-webkit-scrollbar-corner:hover {
      filter: drop-shadow(0 0 20px rgb(0, 184, 148));
  }

  html::-webkit-scrollbar-thumb {
      background-color: #00b894 !important;
  }

  #slim_appbar {
      border: none !important;
  }

  #logo img {
      filter: hue-rotate(-32deg);
  }
  
  @-webkit-keyframes glitched {
    0% {
        filter: drop-shadow(-5px 7px 0 rgba(129, 236, 236, 1));
    }
    
    4% {
        filter: drop-shadow(12px 7px 0 rgba(129, 236, 236, 1));
    }
    
    17% {
        filter: drop-shadow(-17px 7px 0 rgba(214, 48, 49, 1));
    }
    
    20% {
        filter: drop-shadow(-19px 6px 0 rgba(129, 236, 236, 1));
    }
    
    23% {
        filter: drop-shadow(20px 4px 0 rgba(214, 48, 49, 1));
    }
    
    25% {
        filter: drop-shadow(4px 1px 0 rgba(214, 48, 49, 1));
    }
    
    31% {
        filter: drop-shadow(-2px -1px 0 rgba(129, 236, 236, 1));
    }
    
    37% {
        filter: drop-shadow(-12px -4px 0 rgba(129, 236, 236, 1));
    }

    41% {
        filter: drop-shadow(42px -6px 0 rgba(214, 48, 49, 1));
    }

    46% {
        filter: drop-shadow(6px -7px 0 rgba(129, 236, 236, 1));
    }

    49% {
        filter: drop-shadow(9px -7px 0 rgba(214, 48, 49, 1));
    }

    58% {
        filter: drop-shadow(-2px -7px 0 rgba(214, 48, 49, 1));
    }

    61% {
        filter: drop-shadow(-18px -6px 0 rgba(129, 236, 236, 1));
    }

    63% {
        filter: drop-shadow(9px -4px 0 rgba(214, 48, 49, 1));
    }

    66% {
        filter: drop-shadow(4px -1px 0 rgba(129, 236, 236, 1));
    }

    73% {
        filter: drop-shadow(-6px 1px 0 rgba(129, 236, 236, 1));
    }

    80% {
        filter: drop-shadow(-2px 4px 0 rgba(214, 48, 49, 1));
    }

    81% {
        filter: drop-shadow(6px 6px 0 rgba(214, 48, 49, 1));
    }

    85% {
        filter: drop-shadow(2px 7px 0 rgba(129, 236, 236, 1));
    }

    87% {
        filter: drop-shadow(5px 7px 0 rgba(214, 48, 49, 1));
    }

    94% {
        filter: drop-shadow(5px 7px 0 rgba(129, 236, 236, 1));
    }

    97% {
        filter: drop-shadow(5px 6px 0 rgba(214, 48, 49, 1));
    }
						
    98% {
        filter: drop-shadow(-89px 4px 0 rgba(129, 236, 236, 1));
    }

    99% {
        filter: drop-shadow(104px 4px 0 rgba(129, 236, 236, 1));
    }
  }
  
  #hplogo, #hplogo a img {
      background: none !important;
      animation: glitched 2s infinite linear;
  }

  #gbsfw iframe html body c-wiz {
      background-color: #000 !important;
  }

  #gsri_ok0 {
      filter: hue-rotate(-32deg);
  }

  .jsb center input {
      background: none !important;
      background-color: #2d3436 !important;
  }

  #butterbar-container {
      visibility: hidden !important;
  }

  .sfbgg {
      background: none !important;
      border: none !important;
  }
  
  .appbar {
      border: none !important;
  }
  
  .sect .jfk-radiobutton {
      background: none !important;
  }
  
  .jfk-checkbox-checked .jfk-checkbox-checkmark, .jfk-checkbox-checkmark {
      background-color: rgba(0, 0, 0, 0) !important;
      filter: invert(100%) !important;
  }
  
  .goog-slider li {
      color: #ffffff !important;
  }
  
  .jfk-button {
  	  cursor: pointer !important;
  }

  .jfk-button:hover {
      filter: drop-shadow(2px 2px 0 #55efc4) !important;
  }

  #eobp_1 {
      color: #ffffff !important;
  } #eobs_1 {
      background-color: none !important;
      filter: invert(100%) !important;
  }
  
  #Q05LFe * {
      background-color: rgba(0, 0, 0, 0) !important;
  }

  .gb_Vb {
      background: none !important;
  }

  #irc_bgl {
      background-color: #222222;
  }

  .gb_b.gb_hc {
    	background-color: #ffffff !important;
    	filter: brightness(200%) invert(100%) !important;
  }
  
  .gb_ga.gb_fa.gb_za.gb_g.gb_ha::-webkit-scrollbar {
		background-color: #2d3436 !important;
  }
  
  .gb_ga.gb_fa.gb_za.gb_g.gb_ha::-webkit-scrollbar-thumb {
  		background-color: #00b894 !important;
  }
  
  .gb_3b {
  		color: #ffffff !important;
  }

  #hdtb {
      border: none !important;
  } .fYWO0d {
      background-image: url("http://awsassets.panda.org/img/original/icon-chevron-down-512px.png") !important;
      filter: drop-shadow(2px 2px 0 #ffffff) !important;
      transition: 0.25s !important;
  }

  a {
      color: #3498db !important;
  }

  cite {
      color: #2ecc71 !important;
  }

  em {
      color: #b2bec3 !important;
  }

  input {
      color: #ffffff !important;
  }

  .lr_dct_spkr input{
      background: none !important;
      filter: invert(100%) !important;
  }

  .ab_button {
      background: none !important;
      background-color: #2d3436 !important;
      cursor: pointer !important;
      color: #ffffff !important;
      transition: 0.25s ease-in-out !important;
  } .ab_button:hover {
      filter: drop-shadow(5px 5px 0 #55efc4);
      border-color: #00b894 !important;
  }

  .q.qs, .hdtb-mitem.hdtb-msel.hdtb-imb, .hdtb-dd-b, #hdtb-tls {
      font-weight: bolder;
      font-size: 125%;
      color: #55efc4 !important;
  } .hdtb-mitem.hdtb-msel.hdtb-imb {
      border-color: #55efc4 !important;
      border-radius: 5px;
  } #hdtb-tls, .hdtb-dd-b {
      background-color: #2d3436 !important;
      border-radius: 2px;
      transition: 0.25s ease-in-out !important;
  } #hdtb-tls:hover, .hdtb-dd-b:hover {
      border: 1px solid #00b894;
      background-image: none !important;
      background-color: #2d3436 !important;
      filter: drop-shadow(5px 5px 0 #55efc4);
  }

  #resultStats {
      color: #dfe6e9 !important;
  }

  .slp.f span {
      filter: hue-rotate(-24deg) !important;
  }

  .med.dPAwzb {
      color: #ffffff !important;
  }

  td.cur {
      color: #ffffff !important;
  }

  .th {
      border: 0 !important;
      filter: brightness(50%) !important;
      transition: 0.25s;
  } .th:hover {
      filter: brightness(100%) drop-shadow(5px 5px 0 #00cec9) !important;
  }

  #fbar {
      border: none !important;
  }

  #hdtbSum {
      border: none !important;
  }

  .fbar.b2hzT {
      border: none !important;
  }
}

@-moz-document domain(store.google.com) {
  body {
      background-color: #2f3640 !important;
  }

  .container-sm-lock.navbar-inner.relative.flex-center {
      background-color: #2f3640 !important;
  }

  .mqn-at3 {
      background-color: #2f3640 !important;
  }
}
