    /*=== 搜索结果页面  ===*/
    .search-type-nav-game {
        display: none!important;
    }
    [class*="index-searchPageWrap"] {
        background: #212121!important;
    }
    .search-type-nav,
    .div_title,
    .video-list,
    .item-data,
    .layout-Card-horizon,
    .layout-Cover-item,
    .layout-Card-history,
    .layout-Card-rank,
    .layout-Nav-backTop,
    .layout-Tab-container.is-fixed,
    .layout-Module-trending-list,
    .SearchPost,
    [class*="index-listWrap"],
    [class*="index-groupItem"],
    [class*="index-dyPage"] a,
    [class*="index-dyPage"] span {
        background: #2c2c2c!important;
    }
    .Search-rank i {
        background: #424242!important;
    }
    .Search-rank-wrapper:hover .Search-rank,
    .Search-direct,
    .SearchPost-hot,
    [class*="index-listReplyNumber"],
    [class*="index-sourceFromTextBox"] {
        background: #272727!important;
    }
    .layout-Nav-backTop:hover,
    span[class*="index-dyPageActive"] {
        background-color: #ff5d23!important;
    }
    .div_title {
        border-bottom: 1px solid #424242!important;
    }
    .search-type-nav em,
    .search-type-nav span,
    .div_title h2,
    .anchor-list-box h3,
    .item-amount i,
    .item-anchor i,
    .anchor-list-box strong,
    .anchor-name,
    .layout-Result,
    .layout-Tab-item,
    .Search-anchor-info h3,
    .Search-rank,
    .layout-Module-extra,
    .SearchAnchorVideo-title,
    .Search-upSmall-info h3,
    .Search-historyItem,
    .Search-category h3,
    .SearchPost-title,
    .SearchPost-hot,
    .SearchPost-des,
    .SearchPost-nick,
    [class*="index-listTitle"],
    [class*="index-listDesc"],
    [class*="index-listReplyNumber"],
    [class*="index-listReplyNumber"],
    [class*="index-groupName"],
    [class*="index-sourceFromTextBox"],
    [class*="index-dyPage"] a,
    [class*="index-dyPage"] span,
    [class*="index-sourceFrom"] {
        color: #868686!important;
    }
    .SearchAnchorVideo-time,
    .Search-upSmall-info p,
    .Search-anchor-info p,
    .Search-anchor-info h4,
    .layout-Module-subtitle,
    .SearchPost-time,
    .SearchPost-group,
    [class*="index-endec"],
    [class*="index-listAuthorInfo"] a,
    [class*="index-pubtime"],
    [class*="index-memberNumber"] {
        color: #666!important;
    }
    .layout-Tab-item.is-active,
    .layout-Module-label.is-active,
    [class*="index-headerTitle"],
    [class*="index-sourceFromTextBox"]:hover {
        color: #ff5d23!important;
    }
    .search_info h3 {
        color: #f60!important;
    }
    span[class*="index-dyPageActive"] {
        color: #fff!important;
    }
    .sort-list li a,
    .layout-Tab,
    .Search-anchor-avatar,
    .Search-direct,
    .SearchAnchorVideo-container,
    .Search-upSmall-avatar,
    .SearchPost-line,
    .SearchPost-group,
    [class*="index-leftListItem"],
    [class*="index-sourceFromTextBox"],
    [class*="index-dyPage"] a,
    [class*="index-dyPage"] span {
        border-color: #424242!important;
    }
    [class*="index-sourceFromTextBox"]:hover {
        border-color: #ff5d23!important;
    }
    .item-amount i,
    .item-anchor i {
        background-image: url(https://www.asifadeaway.com/Stylish/douyu/icon-all.png)!important;
    }
    .anchor-card {
        background-color: #2c2c2c!important;
    }
    .anchor-card:hover {
        box-shadow: 0 0 15px #141414!important;
    }
    [class*="index-groupItem"],
    [class*="index-groupItem"]:hover {
        box-shadow: none!important;
    }
    .anchor-action-btn {
        background: #212121!important;
        transition: .3s;
    }
    .anchor-action-btn:hover {
        background: #f70!important;
        color: #fff!important;
    }
    .anchor-list-box {
        margin-top: -10px!important;
    }
    .shark-webp .SearchAnchorVideo-time i {
        filter: invert(81%) sepia(2%) saturate(18%) hue-rotate(13deg) brightness(97%) contrast(102%)!important;
    }
    [class*="index-listReplyNumber"] {
        border-radius: 999px!important;
    }
    [class*="index-iconcomment"],
    [class*="index-iconuser"] {
        mix-blend-mode: soft-light!important;
    }
    input[type="text"] {
        color: #868686!important;
        background: #272727!important;
        border: 1px solid #424242!important;
    }
    .Search input[type="text"]{
        border:none!important;
        background: transparent!important
    }
}

