css:
@-moz-document domain("lichess.org") {
/* green circles and arrows, right click */

line[stroke="#15781B"]
{
    stroke: /*[[green-change]]*/ !important
}

circle[stroke="#15781B"]
{
    stroke: /*[[green-change]]*/ !important
                                            
}

path[fill="#15781B"]
{
    fill: /*[[green-change]]*/ !important
}

/* red circles and arrows, shift+right click */
line[stroke="#882020"]
{
    stroke: /*[[red-change]]*/ !important
}

circle[stroke="#882020"]
{
    stroke: /*[[red-change]]*/ !important
}

path[fill="#882020"]
{
    fill: /*[[red-change]]*/ !important
}

/* blue circles and arrows, alt+right click */
line[stroke="#003088"]
{
    stroke: /*[[blue-change]]*/ !important
}

circle[stroke="#003088"]
{
    stroke: /*[[blue-change]]*/ !important
}

path[fill="#003088"]
{
    fill: /*[[blue-change]]*/ !important
}

/* gold circles and arrows, shift+alt+right click*/
line[stroke="#e68f00"]
{
    stroke: /*[[gold-change]]*/ !important
}

circle[stroke="#e68f00"]
{
    stroke: /*[[gold-change]]*/ !important
}

path[fill="#e68f00"]
{
    fill: /*[[gold-change]]*/ !important
}
}
