css:
@-moz-document url-prefix("https://zbigz.com/"), url-prefix("https://zbigz.com/myfiles"), url-prefix("https://zbigz.com/account"), url-prefix("https://zbigz.com/payment") {
.side_bar_container {
    background: #343a40;
}

.z_input {
    display: inline-block;
    background: #6c757d !important;
    color: #ffffff !important;
    font-size: 14px;
    line-height: 21px;
    font-weight: 400;
    padding: 6px 12px;
    border: 1px solid #e1e6ef;
    border-radius: 0;
    height: 35px;
    transition: all .2s ease;
    box-shadow: none;
    -webkit-appearance: none;
    outline: none;
}

.main_files_page .no_files_block h2 {
    color: #ffffff !important;
    font-weight: 500;
    text-transform: uppercase;
    line-height: 21px;
}

.main_files_page .no_files_block p {
    font-size: 20px;
    line-height: 32px;
    font-weight: 500;
    color: #ffffff !important;
    margin: 15px 0;
}

.main_files_page .no_files_block {
    text-align: center;
    padding: 40px 20px;
    background: #343a40 !important;
    border: 1px dashed #20a8d8;
    max-width: 500px;
    margin: 20px auto;
    color: white !important;
}

.file_name_block .file_name {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    font-weight: 500;
    font-size: 16px;
    line-height: 22px;
    padding: 15px;
    transition: all .2s ease;
    color: #ffffff !important;
    text-decoration: none;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 100%;
}

.file_name_block .file_name {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    font-weight: 500;
    font-size: 16px;
    line-height: 22px;
    padding: 15px;
    transition: all .2s ease;
    color: #ffffff;
    text-decoration: none;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 100%;
    background: #343a40;
}

.main_file_block .status_block {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-flow: row wrap;
    flex-flow: row wrap;
    padding: 10px 19px 0;
    margin: 0 -8px;
    border-top: 1px solid #e1e6ef;
    background: #495057;
}

.main_files_page .no_files_block p {
    font-size: 20px;
    line-height: 32px;
    font-weight: 500;
    color: #384042;
    margin: 15px 0;
}

.file_actions, .file_actions>div {
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: center;
    justify-content: center;
    -ms-flex-flow: row wrap;
    background: #343a40;
}

/*Payment Page*/


body.hide_menu .side_bar_wrapper {
    z-index: 0;
    background: #151b1e;
}

.header_wrap {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 60px;
    z-index: 500;
    background: #495057;
}

header.main_header .header_content {
    display: -ms-inline-flexbox;
    display: inline-flex;
    display: -webkit-inline-flex;
    -ms-flex-align: center;
    align-items: center;
    transition: width .2s linear;
    width: calc(100% - 250px);
    border-bottom: none;
    background: #495057;
}

.side_bar_wrapper {
    position: fixed;
    left: 0;
    top: 60px;
    width: 100%;
	background: #151b1e;
}

.z_input {
    display: inline-block;
    background: #6c757d;
    color: #f9f9fa;
    font-size: 14px;
    line-height: 21px;
    font-weight: 400;
    padding: 6px 12px;
    border: 1px solid #e1e6ef;
    border-radius: 0;
    height: 35px;
    transition: all .2s ease;
    box-shadow: none;
    -webkit-appearance: none;
    outline: none;
}

.z_progress.green {
    background: #343a40;
    color: white;
}

.file_page__file .item.size {
    -ms-flex-flow: row wrap;
    flex-flow: row wrap;
    min-width: 80px;
    max-width: 80px;
    background: #343a40;
    color: white;
}

.file_page__file .file_name_block {
    padding: 0;
    background: #343a40;
}

footer.footer:not(.main_page_footer) .col.social ul li+li {
    margin: 0 0 0 10px;
    color: white;
}

header.main_header .header_links .profile .dropdown-item:not(.text):not(.no_hover):hover {
    background: #343a40 !important;
    color: #fff;
    text-shadow: white -5px 1px 20px !important;
}

.dropdown-item.no_hover:hover {
    background: #343a40 !important;
    text-shadow: white -5px 1px 20px !important;
}

.dropdown-header {
    background: #343a40 !important;
    color: #20a8d8 !important;
}

.dropdown-item.log {
    color: white !important;
}

a.dropdown-item {
    color: white !important;
}

.dropdown-item.no_hover {
    color: white !important;
}

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: 1rem;
    color: #ffffff !important;
    text-align: left;
    list-style: none;
    background-color: #343a40 !important;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: .25rem;
}

.dropdown-header, .dropdown-item {
    padding: 10px 20px;
    border-bottom: 1px solid #e1e6ef;
    background: #495057;
    color: white;
}

/*Profile*/
.profile_page__info_blocks .card .card-footer {
    background: #343a40 !important;
    /* color: white; */
}

.card-group>.card:last-child .card-header, .card-group>.card:last-child .card-img-top {
    border-top-left-radius: 0;
    background: #343a40;
}

.card-header:first-child {
    border-radius: calc(.25rem - 1px) calc(.25rem - 1px) 0 0;
    background: #343a40;
    color: white;
}

.profile_page__info_blocks .card.change_tab .card-header label.active {
    cursor: default;
    color: #ffffff;
}

.list-group-flush:first-child .list-group-item:first-child {
    border-top: 0;
    color: white;
    background: #343a40;
}

.list-group-flush .list-group-item {
    border-right: 0;
    border-left: 0;
    border-radius: 0;
    background: #343a40;
    color: white;
}

.profile_page__info_blocks .card .bands_auth .list-group-item>div>span {
    font-size: 18px;
    font-weight: 700;
    color: #ffffff;
}

.profile_page__info_blocks .card .card-footer {
    background: #343a40;
}
.card-body {
    -webkit-box-flex: 1;
    -ms-flex: 1 1 auto;
    flex: 1 1 auto;
    padding: 1.25rem;
    background: #343a40;
    color: white;
}

.main_files_page .no_files_block p {
    font-size: 20px;
    line-height: 32px;
    font-weight: 500;
    color: #ffffff;
    margin: 15px 0;
}

.main_files_page .no_files_block h2 {
    color: #ffffff;
    font-weight: 500;
    text-transform: uppercase;
    line-height: 21px;
}

.main_files_page .no_files_block {
    text-align: center;
    padding: 40px 20px;
    background: #495057;
    border: 1px dashed #20a8d8;
    max-width: 500px;
    margin: 20px auto;
}

/*Payment Page*/
body, h1, h2, h3, h4, h5, h6 {
    color: #ffffff;
}

.pay_table th {
    font-size: 16px;
    vertical-align: top;
    text-align: center;
    font-weight: 500;
    color: #fff;
    padding-bottom: 15px;
}

.payment_page section.gray {
    background: #343a40;
}

.pay_table td {
    font-size: 15px;
    font-weight: 400;
    text-align: center;
    vertical-align: middle;
    color: #ffffff;
    padding: 10px 0;
    background: #212529;
}

.pay_table td, .pay_table th {
    text-shadow: none !important;
}

.payment_page .cuppon_block {
    margin-top: 20px;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-pack: center;
    justify-content: center;
    -ms-flex-align: center;
    align-items: center;
    padding: 20px;
    background: #495057;
}

/*Footer Links*/
footer.footer:not(.main_page_footer) .col:not(.social) ul li {
    cursor: pointer;
    margin: 0;
    background: #343a40;
}

footer.footer:not(.main_page_footer) .col:not(.social) ul li a:hover {
    color: #151b1e;
    text-decoration: none;
}

footer.footer:not(.main_page_footer) .col:not(.social) ul li:hover {
    cursor: pointer;
    margin: 0;
    background: #343a40;
}

footer.footer:not(.main_page_footer) .col:not(.social) ul li a {
    display: inline-block;
    padding: 12px 15px;
    color: #ffffff;
    background: #343a40;
}

footer.footer:not(.main_page_footer) .col:not(.social) ul li a:hover {
    color: #00ceff;
    text-decoration: none;
}

/*Support page*/

.z_textarea {
    display: block;
    width: 100%;
    border: 1px solid #e1e6ef;
    color: #ffffff;
    font-size: 14px;
    font-weight: 400;
    line-height: 16px;
    padding: 10px;
    outline: none;
    background: #495057;
}

.bg-white {
    background-color: #343a40!important;
}

.main_files_page.anim_fadein.container-fluid {
    background: #343a40;
}
}
