css:
@-moz-document domain("forum.sbenny.com") {
html {
    font: 15px / 1.4 sans-serif;
    font-family: 'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    font-weight: 400;
    color: #CCC;
    margin: 0;
    padding: 0;
    word-wrap: break-word;
    background-color: #0a0;
}
.p-body {
    background-color: #0a0 !important;
    background-image: url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTAkhtaE790Xt2JCBWA1LEqsDt2vlf6Kf9xvF_JFLl1XTio16In6A) !important;
    background-attachment: fixed;
    background-repeat: repeat;
}
.block-header {
    background: linear-gradient(#0a0, #47a7eb, #0a0);
    font-family: Joti One;
}
.block-header {
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 20px;
    color: #333;
    border-bottom: 1px solid #000;
}
#siropuChatTabs {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    border-bottom: 1px solid #0a0;
    padding-right: 10px;
    padding-left: 10px;
    background: #0a0;
}
#siropuChatTabs a {
    font-size: 12px;
    color: #3b5998;
    padding-top: 10px;
    padding-bottom: 10px;
}
.block-container {
    color: #0a0;
    background: #3b5998ba;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    border-radius: 4px;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
}
.fr-toolbar .fr-command.fr-btn,
.fr-popup .fr-command.fr-btn {
    background: transparent;
    color: #3b5998;
    -moz-outline: 0;
    outline: 0;
    border: 0;
    line-height: 1;
    cursor: pointer;
    text-align: left;
    margin: 0px 1px;
    -webkit-transition: background .25s ease 0s;
    -moz-transition: background .25s ease 0s;
    -ms-transition: background .25s ease 0s;
    -o-transition: background .25s ease 0s;
    border-radius: 0;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    z-index: 2;
    position: relative;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    text-decoration: none;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    float: left;
    padding: 0;
    width: 32px;
    height: 32px;
}
.fr-toolbar {
    color: #3b5998;
    background: #0a0;
    position: relative;
    z-index: 4;
    font-family: 'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #0d0d0d;
    border-top: 3px solid #47a7eb;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}
.block-footer {
    padding: 6px 10px;
    font-size: 12px;
    color: #AAA;
    border-top: 1px solid #000;
    background: linear-gradient(180deg, #3b5998, #151515);
}
.avatar.avatar--xxs {
    width: 24px;
    height: 24px;
    font-size: 14.4px;
    border-radius: 50%;
}
h3.block-minorHeader {
    background: linear-gradient(#0a0, #47a7eb, #0a0);
    color: #333;
}
h3.block-minorHeader {
    font-family: Joti One;
}
.p-sectionLinks {
    font-size: 13px;
    color: #47a7eb;
    background: #287197;
    border-bottom: 1px solid #47a7eb;
}
.p-nav-list .p-navEl.is-selected {
    color: #287197;
    font-weight: 700;
    background: #287197;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
}
.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #CCC;
    background: #287197;
    border-radius: 3px;
    border-top: 3px solid #47a7eb;
}
.menu-header {
    padding: 6px 15px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 17px;
    color: #3b5998;
    border-bottom: 1px solid #47a7eb;
    background: linear-gradient(0deg, #0a0, #0a0);
}
.overlay-title {
    display: none;
    margin: 0;
    font-weight: 400;
    font-size: 24px;
    color: #47a7eb;
    border-bottom: 1px solid #47a7eb;
    padding-top: 6px;
    padding-right: 10px;
    padding-bottom: 6px;
    padding-left: 10px;
    background: linear-gradient(0deg, #0d0d0d, #0a0);
}
.tooltip--member .tooltip-content {
    color: #47a7eb;
    background: #287197;
    padding: 0;
    text-align: left;
    border: 1px solid #47a7eb;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.35);
}
.pairs>dt {
    padding: 0;
    margin: 0;
    color: #333;
}
.menu-row.menu-row--highlighted {
    color: #3b5998;
    background: #0a0;
}
.contentRow-minor {
    font-size: 13px;
    color: #0d0d0d;
}
.block--messages .message,
.block--messages .block-row {
    color: #edf6fd;
    background: #287197b8;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    border-radius: 4px;
}
.message-cell.message-cell--user,
.message-cell.message-cell--action {
    position: relative;
    background: #0a0;
    border-right: 1px solid #0d0d0d;
    min-width: 0;
}
.fr-element {
    background: #00aa0080;
    position: relative;
    z-index: 2;
    -webkit-user-select: auto;
}
.avatar img {
    background-color: #3b5998;
}
.avatar.avatar--m {
    width: 96px;
    height: 96px;
    font-size: 57.6px;
    border-radius: 50%;
}
.avatar.avatar--s {
    width: 48px;
    height: 48px;
    font-size: 28.8px;
    border-radius: 50%;
}
.menu-footer {
    padding: 6px 15px;
    font-size: 12px;
    color: #3b5998;
    border-top: 1px solid #47a7eb;
    background: linear-gradient(180deg, #287197, #151515);
}
.menu-row.menu-row--alt {
    color: #CCC;
    background: #287197;
}
a {
    color: #00d504;
    text-decoration: none;
}
h1.p-title-value {
    border-bottom: 3px solid #00d504;
    border-left: 20px solid transparent;
    border-right: 20px solid transparent;
    box-shadow: 0px 1px 0px 0px #47a7eb;
    margin: 0 auto;
    text-shadow: 2px 2px 0 #00da0f, 0 0 10px #00da0f;
    font-weight: bold;
    color: #4080ff;
}
.menu-linkRow.menu-linkRow--alt {
    color: #CCC;
    background: #0a0;
}
.overlay {
    position: relative;
    margin: 40px auto 10px;
    margin-top: 4vh;
    width: 100%;
    max-width: 800px;
    color: #CCC;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    border-radius: 4px;
    box-shadow: 0 5px 15px 0 #0d0d0d;
    outline: none;
}
.avatar.avatar--xs {
    width: 32px;
    height: 32px;
    font-size: 19.2px;
    border-radius: 50%;
}
.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    background: #0a0;
    border-bottom: 1px solid #47a7eb;
    color: #3b5998;
}
.p-staffBar,
.p-footer {
    background: #5fc71b !important;
}
.button,
a.button {
    display: inline-block;
    text-decoration: none;
    cursor: pointer;
    border: 1px solid transparent;
    white-space: nowrap;
    -webkit-transition: background-color .25s ease;
    transition: background-color .25s ease;
    font-size: 13px;
    border-radius: 4px;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 10px;
    text-align: center;
    color: #edf6fd;
    background: #287197;
    border-color: #47a7eb #47a7eb #47a7eb #47a7eb;
}
.button.button--cta,
a.button.button--cta {
    color: #000;
    background: #0a0;
    border-color: #00da0f #00da0f #00da0f #00da0f;
}
.menu-row.menu-row--separated+.menu-row {
    border-top: 1px solid #47a7eb;
}
.tabs--editor {
    color: #00d504;
    background: #0a0;
    font-weight: 400;
    border-bottom: 1px solid #47a7eb;
    font-size: 0;
}
.p-breadcrumbs>li a {
    display: inline-block;
    vertical-align: bottom;
    max-width: 300px;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    text-overflow: ellipsis;
    color: #3b5998;
}
.siropuChatDateTime {
    font-size: 10px;
    color: #00da0f;
    background: #3b5998;
    border-radius: 5px;
    padding: 5px;
}
.block-row.block-row--alt {
    color: #CCC;
    background: #287197;
}
.menu-row.menu-row--alt:hover {
    color: #CCC;
    background: #4080ff;
}
.menu-row.menu-row--clickable:hover {
    background: #4080ff;
}
.pageNav-page {
    display: table-cell;
    background: linear-gradient(0deg, #287197, #47a7eb);
    color: #00d504;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    font-size: 13px;
    white-space: nowrap;
}
.pageNav-page:hover {
    display: table-cell;
    background: linear-gradient(0deg, #47a7eb, #3b5998);
    color: #00d504;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    font-size: 13px;
    white-space: nowrap;
}
.pageNav-page.pageNav-page--current {
    background: #3b5998;
    color: #00ff08;
    border: 1px solid #66ed64;
    cursor: pointer;
}
.pageNav-jump {
    display: inline-block;
    background: linear-gradient(0deg, #3b5998, #47a7eb);
    color: #00d504;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}
.pageNav-jump:hover {
    display: inline-block;
    background: linear-gradient(0deg, #47a7eb, #3b5998);
    color: #00d504;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}
.block-filterBar {
    padding: 6px 10px;
    font-size: 13px;
    color: #00d504;
    border-bottom: 1px solid #47a7eb;
    background: linear-gradient(0deg, #287197, #0a0);
}
.button.button--link,
a.button.button--link {
    background: #287197;
    color: #00d504;
    border-color: #47a7eb #47a7eb #47a7eb #47a7eb;
}
.formRow>dt {
    border-right: 1px solid transparent;
    background: #0a0;
    border-color: #47a7eb;
    text-align: right;
    width: 33%;
    padding: 15px 10px 15px 10px;
}
.formSubmitRow-bar {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: #0a0;
    border-top: 1px solid #47a7eb;
}
.menu-linkRow.is-selected,
.menu-linkRow:hover,
.menu-linkRow:focus {
    color: #5fc71b;
    text-decoration: none;
    background: #3b5998;
    border-left-color: rgba(71, 167, 235, 0.5);
}
.blockLink:hover {
    background: #3b5998;
    text-decoration: inherit;
}
.blockLink.is-selected {
    font-weight: 700;
    background: #4080ff;
    border-left: 3px solid #47a7eb;
    padding-left: 7px;
}
.message-responseRow {
    margin-top: -1px;
    background: #3b5998;
    border: 1px solid #47a7eb;
    padding: 6px;
}
.tabs--standalone {
    padding: 0;
    margin-bottom: 20px;
    font-weight: 400;
    border-width: 1px;
    border-style: solid;
    border-top-color: #47a7eb;
    border-right-color: #47a7eb;
    border-bottom-color: #47a7eb;
    border-left-color: #47a7eb;
    font-size: 15px;
    color: #00d504;
    background: #3b5998;
}
.button.button--link:hover,
a.button.button--link:hover,
.button.button--link:active,
a.button.button--link:active,
.button.button--link:focus,
a.button.button--link:focus {
    text-decoration: none;
    background: #4080ff;
}
.button:not(.button--splitTrigger):hover,
a.button:not(.button--splitTrigger):hover,
.button.button--splitTrigger>.button-text:hover,
a.button.button--splitTrigger>.button-text:hover,
.button.button--splitTrigger>.button-menu:hover,
a.button.button--splitTrigger>.button-menu:hover,
.button:not(.button--splitTrigger):focus,
a.button:not(.button--splitTrigger):focus,
.button.button--splitTrigger>.button-text:focus,
a.button.button--splitTrigger>.button-text:focus,
.button.button--splitTrigger>.button-menu:focus,
a.button.button--splitTrigger>.button-menu:focus,
.button:not(.button--splitTrigger):active,
a.button:not(.button--splitTrigger):active,
.button.button--splitTrigger>.button-text:active,
a.button.button--splitTrigger>.button-text:active,
.button.button--splitTrigger>.button-menu:active,
a.button.button--splitTrigger>.button-menu:active {
    background-color: #4080ff;
}
.button.button--cta:not(.button--splitTrigger):hover,
a.button.button--cta:not(.button--splitTrigger):hover,
.button.button--cta.button--splitTrigger>.button-text:hover,
a.button.button--cta.button--splitTrigger>.button-text:hover,
.button.button--cta.button--splitTrigger>.button-menu:hover,
a.button.button--cta.button--splitTrigger>.button-menu:hover,
.button.button--cta:not(.button--splitTrigger):focus,
a.button.button--cta:not(.button--splitTrigger):focus,
.button.button--cta.button--splitTrigger>.button-text:focus,
a.button.button--cta.button--splitTrigger>.button-text:focus,
.button.button--cta.button--splitTrigger>.button-menu:focus,
a.button.button--cta.button--splitTrigger>.button-menu:focus,
.button.button--cta:not(.button--splitTrigger):active,
a.button.button--cta:not(.button--splitTrigger):active,
.button.button--cta.button--splitTrigger>.button-text:active,
a.button.button--cta.button--splitTrigger>.button-text:active,
.button.button--cta.button--splitTrigger>.button-menu:active,
a.button.button--cta.button--splitTrigger>.button-menu:active {
    background-color: #00da0f;
}
.memberOverviewBlock-seeMore {
    background: #287197;
    border: 1px solid #0a0;
    padding: 3px;
    text-align: center;
    margin-top: auto;
}
.fr-command.fr-btn+.fr-dropdown-menu .fr-dropdown-wrapper {
    background: #0a0;
    border: 1px solid #47a7eb;
}
.message-attribution {
    color: #00d504;
    font-size: 12px;
    padding-bottom: 3px;
    border-bottom: 1px solid #000;
}
.structItem-iconContainer .structItem-secondaryIcon {
    position: absolute;
    right: -5px;
    bottom: -5px;
    width: 20px;
    height: 20px;
    font-size: 12px;
}
.p-description {
    margin: 5px 0 0;
    padding: 0;
    font-size: 13px;
    color: #0d0d0d;
}
}
