css:
@-moz-document url-prefix("https://docs.google.com/document/") {
#docs-chrome {
	background: #333 none repeat scroll 0% 0% !important;
	color: #DDD !important;
}

.docs-companion-app-switcher-container {
    background-color: black;
    border-top: 1px solid #000000;
    box-sizing: border-box;
    height: calc(100% - 60px);
    position: absolute;
    right: 0;
    top: 60px;
    width: 56px;
    z-index: 1;
}

#gb#gb a.gb_b,
/* Account email in top right corner */

.docs-presence-plus-widget-status
/* x total viewers text */

	{
	color: #DDD !important;
}

.docs-horizontal-ruler {
    border-bottom: 1px solid #333333;
    height: 13px;
}

.docs-material #docs-header .docs-titlebar-buttons {
    align-items: center;
    background-color: #333;
    display: flex;
    height: 64px;
    padding: 0 12px 0 16px;
    top: 0;
    z-index: 900;
}

.docs-vertical-ruler .docs-ruler-face {
    width: 15px;
    background-color: #3e3e3e;
    border-color: #3e3e3e;
}

#kix-vertical-ruler {
    background: #3e3e3e;
    border: 1px solid #3e3e3e;
    margin-left: -1px;
    position: absolute;
    top: 14px;
}

.companion-app-switcher-container {
    background-color: #3c3d40;
    border: 1px solid #000000;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    height: 100%;
    overflow: hidden;
    position: relative;
    width: 56px;
    border-top: 1px solid #5b5c5d;
}

body {
    background-color: #333333;
    color: black;
    font: normal 13px arial,sans-serif;
    margin: 0;
}

.docs-material-appbar #docs-header .docs-titlebar-buttons {
	align-items: center;
	background-color: #333;
	display: flex;
	height: 64px;
	padding: 0 12px 0 16px;
	top: 0;
	z-index: 900;
}

/* Document title box */

.docs-title-input {
	background-color: #2B2B2C !important;
	border-radius: 2px !important;
	border: 1px solid #171717 !important;
	border-top-color: #0f0f0f !important;
	box-shadow: 0 1px rgba(255, 255, 255, 0.15), inset 0 1px rgba(0, 0, 0, 0.1) !important;
	font-style: normal !important;
}

.docs-title-input:focus {
	color: white !important;
	background-color: #2B2B2C !important;
	border: 1px solid #171717 !important;
	border-top-color: #0f0f0f !important;
}

.docs-title-untitled {
	display: none !important;
}

/* Fix for menu bar text when hover */

.goog-control-hover {
	color: black !important;
}

/* Main toolbar */

#docs-toolbar-wrapper {
	border-top: 1px solid #151516 !important;
	border-bottom: 1px solid #040404 !important;
	background-image: linear-gradient(#3c3d40, #3c3d40) !important;
	box-shadow: inset 0 1px rgba(255, 255, 255, 0.15) !important;
}

/* Toolbar's vertical dividers */

.goog-toolbar-separator.goog-inline-block {
	border-left: 1px solid #222 !important;
}

/* A very simple/lazy fix to get toolbar item colors to look good on a dark background */

.goog-toolbar-button, .goog-toolbar-toggle-button, .goog-toolbar-menu-button:not([id="textColorButton"]), .goog-toolbar-combo-button, .docs-icon-folder-solid {
	-webkit-filter: invert(1) !important;
	filter: invert(1) !important;
}

.docs-horizontal-ruler .docs-ruler-face {
	height: 15px;
	background-color: #3e3e3e;
}

/* Fixes for suggestion mode when editing own doc, viewing mode when editing others' docs */

#docs-toolbar-mode-switcher, #docs-access-level-indicator {
	background-color: transparent !important;
	color: black !important;
}

.docs-icon-mode-review-white, .docs-icon-mode-view-white {
	filter: invert(1) !important;
}

/* Background behind the document */

#docs-editor {
	background: #333 none repeat scroll 0% 0% !important;
}

/* Document drop-shadow removal */

.kix-page-paginated {
	box-shadow: none !important;
}
}
