css:
@-moz-document domain("paste.imirhil.fr"), domain("paste.fizi.ca"), domain("paste.warth-hofer.de"), url-prefix("https://jaegers.net/apps/privatebin/"), domain("privatebin.net"), domain("plexle.fi"), domain("vim.cx") {
  body {
    background-color: #f2f2f2;  /*OUTER background*/
    background-size: 48px 48px;
  }
  pre {
    padding: 0;
  }
  body,h1,h2,h3,h4,h5,h6,pre,p {
    color: #000; /*paste text color*/
  }
  pre, p, section.container, .row {
    background: #fff; /*INNER background*/
  }
  pre::first-line, h1, h2, h3 {
    font-family: Merriweather,serif;
    font-size: 28px;
    font-weight: 700;
    margin: 0 0 10px 0;
    -webkit-margin-before: 0em;
    -webkit-margin-after: 1em;
    -webkit-margin-start: 0px;
    -webkit-margin-end: 0px;
    line-height: 1.5;
  }
  article div {
    padding-right: 24px;
    padding-left: 24px;
    margin-top: 1em;
  }
  /*body text*/
  pre, p {
    font-size: 20px;
    font-family: Merriweather,serif;
    font-weight: 400;
    line-height: 1.4;
    border: none 0;
  }
  section.container {
    font-size: 18px;
    position: relative;
    max-width: 802px;
    margin: 0 auto;
    margin-bottom: 20px;
    padding: 1px 3% 0px;
    border: 1px solid #e5e5e5;
  }
  #prettymessage {
    padding-left: 0px;
    padding-right: 0px;
  }
  #prettyprint {
    box-shadow: none;
  }
  
  .input-group {
    width:100%
  }
 
  .well {
    background-color: white;
    padding-left: 0;
    padding-right: 0;
    box-shadow: none;
  }
}
