css:
@-moz-document domain("hinative.com") {
.parentbody{
    background-color: #191919 !important;
}

.section.section_bg_white, .quick_wrapper .footer, .quality_wrapper .footer  {
    background-color: #242424 !important;
}

.chat_content .wrapper_fukidashi, .container_fukidashi .wrapper_fukidashi, #navbar_body_ft ul.dropdown-menu,.quick_wrapper .header,.profile_item .box,.detailed_introduction_wrapper, .activity{
    background-color: #404040 !important;
}

.q_title, h1,.heading_label label,.label_specifics span, .ticket_count_wrapper, .label_title {
    color: #999999 !important;
}

.wrapper_language .wrapper_language_name span, .wrapper_language .wrapper_country_name span,.detailed_introduction_wrapper .introduction_details span,.detailed_introduction_wrapper .introduction_details,.detailed_introduction_wrapper .introduction_details .lang_parts, div[id*="registrations_"] h2, div[id*="_new"] h2, div[id*="_edit"] h2, div[id*="settings_"] h2, .choice_wrapper label {
    color: #ffffff !important;
}

.language_information .name_label, a {
    color: #c1c1c1 !important;
}

/* .sound_parent_box .sound_parts_front {
    background-color: #404040 !important;
} */

.heading_main>.textarea_p span.keyword, .textarea_p, .activity_wrapper .content p>b>span.keyword, .activity_wrapper .content p>b, .activity_wrapper .content p>b>span {
    color: #ffffff !important;
}

.desc_box {
    background-color: #999999 !important;
}

/* Arrow of the chat box*/
.myreply_owner .default_arrow:after, .myreply_owner .owner_arrow:after, .question_owner .default_arrow:after, .question_owner .owner_arrow:after, .chat_content .default_arrow:after, .chat_content .owner_arrow:after, .container_fukidashi .default_arrow:after, .container_fukidashi .owner_arrow:after,.myreply .myreply_arrow:after, .myreply .myreply_owner_arrow:after {
    color: #404040 !important;
}
.myreply_owner .arrow_left_wrapper:after, .question_owner .arrow_left_wrapper:after,.myreply_owner .default_arrow, .myreply_owner .owner_arrow, .question_owner .default_arrow, .question_owner .owner_arrow, .chat_content .arrow_left_wrapper:after, .container_fukidashi .arrow_left_wrapper:after,.chat_content .default_arrow, .chat_content .owner_arrow, .container_fukidashi .default_arrow, .container_fukidashi .owner_arrow {
    background-color: #404040 !important;
}

.myreply .arrow_left_wrapper {
    background-color: #242424 !important;
}



.section_setting .list-group.list-group_link .list-group-item a {
    background-color: #404040 !important;
}


.list-group-item {
    background-color: #626262 !important;
}
}
