css:
@-moz-document domain("ameblo.jp"),
domain("ameba.jp") {
/* Ameblo Management ver.202111.08.01 *********************** */
/* 統合背景色設定 */
:root {
    --home: #fff;
    --home_dim: #f8f8f8;
    --hov: #e2eef0;
    --backg: #c5d8e1; }

:focus {
    outline: none; }

/* 下のコメントアウトを外すと、画面の低輝度の設定が適用されます */
/*
:root {
    --home: #fafafa;
    --home_dim: #f0f0f0; }
*/ }

@-moz-document url-prefix("https://blog.ameba.jp/ucs/top"),
url-prefix("https://blog.ameba.jp/ucs/entry/srventrylist"),
url-prefix("https://blog.ameba.jp/ucs/entry/srventrydelete"),
url-prefix("https://blog.ameba.jp/ucs/pic"),
url-prefix("https://kuchikomi.ameba.jp/ucs/lis"),
url-prefix("https://blog.ameba.jp/ucs/ana"),
url-prefix("https://blog.ameba.jp/ucs/iin"),
url-prefix("https://blog.ameba.jp/ucs/com"),
url-prefix("https://blog.ameba.jp/ucs/banip"),
url-prefix("https://blog.ameba.jp/ucs/banuser"),
url-prefix("https://blog.ameba.jp/ucs/ban"),
url-prefix("https://blog.ameba.jp/ucs/reb"),
url-prefix("https://blog.ameba.jp/ucs/ski"),
url-prefix("https://blog.ameba.jp/ucs/edi"),
url-prefix("https://blog.ameba.jp/ucs/gui"),
url-prefix("https://blog.ameba.jp/ucs/blo"),
url-prefix("https://blog.ameba.jp/ucs/rea"),
url-prefix("https://blog.ameba.jp/ucs/blg"),
url-prefix("https://blog.ameba.jp/ucs/upl"),
url-prefix("https://blog.ameba.jp/ucs/msg"),
url-prefix("https://blog.ameba.jp/ucs/mai"),
url-prefix("https://blog.ameba.jp/ucs/sns"),
url-prefix("https://blog.ameba.jp/ucs/the"),
url-prefix("https://blog.ameba.jp/ucs/ame"),
url-prefix("https://blog.ameba.jp/ucs/out"),
url-prefix("https://blog.ameba.jp/ucs/seo"),
url-prefix("https://blog.ameba.jp/ucs/pin"),
url-prefix("https://blog.ameba.jp/ucs/sid"),
url-prefix("https://blog.ameba.jp/ucs/plu"),
url-prefix("https://blog.ameba.jp/ucs/boo"),
url-prefix("https://blog.ameba.jp/ucs/pro") {
/* ブログ管理メニュー 全体 ********************************* */
/* ページ全体の背景と幅 *************************** */
html {
    overflow-y: overlay; }

html,
body {
    background: var(--backg); }

#ucsContent {
    width: 840px;
    background: var(--home); }

/* グローバルヘッダー  **************************** */
#globalHeader {
    position: absolute;
    top: 0;
    left: 0;
    margin-left: 242px;
    height: 34px;
    min-width: 200px;
    width: 600px;
    border: none;
    background: none;
    z-index: 2; }

@media screen and (min-width: 860px) {
#globalHeader {
    margin-left: calc(50% - 180px); } }

#globalHeader .icon {
    display: none !important; }

/* グローバル左 */
#gHeaderLeft {
    margin: 8px 0 0 !important;
    padding: 0 !important; }

#gHeaderLeft .logo {
    display: none; }

#gHeaderLeft .link {
    display: inline-block;
    width: 28px;
    height: 20px;
    border: 1px solid #aaa;
    border-radius: 4px;
    background: #fff; }

#gHeaderLeft .link:hover {
    background: #03a9f4; }

#gHeaderLeft .link a {
    top: 3px !important;
    left: 30px;
    padding: 4px 2px;
    font-size: 12px !important;
    white-space: nowrap;
    text-decoration: none; }

#gHeaderLeft .link a:before {
    content: "\EA1e";
    font: normal 22px/16px ameba-symbols;
    position: absolute;
    top: 4.6px;
    left: -27px;
    color: #2d8b3c !important; }

#gHeaderLeft .link:hover a:before {
    color: #fff !important; }

/* グローバル右 */
#gHeaderRight {
    margin: 8px 0 0 !important;
    width: 240px !important; }

/* アメーバID */
#gHeaderRight .amebaId {
    top: 0 !important;
    right: 86px !important;
    padding: 3px 4px 1px;
    height: 16px;
    border: 1px solid #aaa;
    border-radius: 4px;
    background: #fff;
    text-decoration: none !important; }

#gHeaderRight .amebaId:hover {
    background: #efefef;
    z-index: 2; }

#gHeaderRight #amebaIdInfo {
    right: 67px !important; }

/* 設定 */
#settingBtn,
#serviceBtn {
    top: 0 !important;
    height: 20px !important;
    text-indent: 0 !important;
    font-size: 0;
    border: 1px solid #aaa;
    border-radius: 4px;
    background: #fff !important; }

#settingBtn {
    right: 48px !important; }

#serviceBtn {
    right: 10px !important; }

#settingBtn:hover,
#serviceBtn:hover {
    background: #efefef !important; }

#settingBtn:before {
    content: "\EA2f";
    font: normal 21px/1em ameba-symbols;
    position: relative;
    left: 4px; }

#serviceBtn:before {
    content: "\EA29";
    font: normal 21px/1em ameba-symbols;
    position: relative;
    left: 4px; }

/* ブログ管理ページ ヘッダー *********************** */
#ucsHeader {
    background: #eaf0f4;
    box-shadow: 0px 4px 8px #607d8b52; }

/*「ブログ管理」ボタン */
#ucsHeaderTitle {
    color: transparent; }

/*「ヘッダーリンク」ボタン */
#ucsSubMenu {
    top: 10px;
    left: 355px;
    bottom: initial;
    width: 255px;
    z-index: 2; }

#myUcsPage {
    display: none; }

#ucsSubMenu li {
    position: relative;
    margin: 0 0 0 70px;
    padding: 0;
    background: none; }

#ucsSubMenu li a:hover {
    text-decoration: none; }

#ucsSubMenu li a:before {
    font: normal 21px/21px ameba-symbols;
    position: absolute;
    top: -4px;
    left: -32px;
    height: 20px;
    padding: 0 3px 0 4px;
    border: 1px solid #aaa;
    border-radius: 4px;
    background: #fff; }

#ucsSubMenu li:nth-child(2) a:before {
    content: "\EA44";
    font-size: 25px;
    padding: 0 2px 0 1px; }

#ucsSubMenu li:nth-child(3) a:before {
    content: "\EA98"; }

#ucsSubMenu li a:hover:before {
    color: #fff;
    background: #03a9f4; }

/* 左メニュー *********************************** */
.l-ucs-sidemenu-area {
    position: absolute;
    top: 5px;
    left: 2px;
    width: 155px;
    border: none;
    z-index: 2;
    padding-top: 32px; }

@media screen and (min-width: 860px) {
.l-ucs-sidemenu-area {
    margin-left: calc(50% - 422px); } }

.l-ucs-sidemenu-area:before {
    content: "管理メニュー";
    position: absolute;
    top: 3px;
    left: 15px;
    padding: 3px 20px 1px 40px;
    border: 1px solid #aaa;
    border-radius: 4px;
    background: #fff; }

.l-ucs-sidemenu-area:hover:before {
    color: #fff;
    background: #03a9f4; }

.l-ucs-sidemenu-area::after {
    content: "\EA31";
    font: normal 22px/21px ameba-symbols;
    position: absolute;
    top: 4px;
    left: 30px;
    color: #4CAF50; }

.l-ucs-sidemenu-area:hover::after {
    color: #fff; }

.l-ucs-sidemenu-area ul {
    position: absolute;
    display: flex;
    width: 840px;
    box-shadow: 0px 4px 8px #607d8b52; }

.l-ucs-sidemenu-area li {
    display: block;
    height: 0;
    background: #deeff7;
    box-shadow: inset 0 0 0 1px #80deea;
    border: none;
    overflow: hidden;
    transition: .2s;
    transition-delay: 1s; }

.l-ucs-sidemenu-area li:hover {
    flex-shrink: 0; }

.l-ucs-sidemenu-area:hover li {
    height: 48px;
    transition-delay: 0s; }

.l-ucs-sidemenu-area a {
    width: 88px;
    height: 48px;
    white-space: nowrap;
    outline: none !important; }

/* ブログネタを非表示 */
.l-ucs-sidemenu-area a[href*="kuchikomi"] {
    display: none !important; }

.l-ucs-sidemenu-area a:hover {
    background: #fff;
    box-shadow: inset 0 0 0 1px #80deea;
    opacity: 1; }

.l-ucs-sidemenu-area a.active {
    background: #fff;
    box-shadow: inset 0 0 0 2px #2d8c3c; }

.l-ucs-sidemenu-icon {
    height: 28px; }

.l-ucs-sidemenu-txt {
    font-weight: normal;
    font-size: 10px; }

.l-ucs-sidemenu-link-inner:before {
    width: 0 !important; }

/* 不要表示の削除 ******************************** */
/* AD非表示 */
#adContent-anchor {
    display: none; }

#footerAd {
    display: none; }

/* フッター非表示 */
#globalFooter {
    display: none; }

/* 右余白 AD */
#ucsMainRight {
    display: none; }

/* 各種設定終了画面 AD */
[id^="div-gpt-ad"] {
    display: none; } }

@-moz-document url-prefix("https://blog.ameba.jp/ucs/com"),
url-prefix("https://blog.ameba.jp/ucs/ban"),
url-prefix("https://blog.ameba.jp/ucs/edi"),
url-prefix("https://blog.ameba.jp/ucs/blo"),
url-prefix("https://blog.ameba.jp/ucs/rea"),
url-prefix("https://blog.ameba.jp/ucs/blg"),
url-prefix("https://blog.ameba.jp/ucs/upl"),
url-prefix("https://blog.ameba.jp/ucs/msg"),
url-prefix("https://blog.ameba.jp/ucs/mai"),
url-prefix("https://blog.ameba.jp/ucs/sns"),
url-prefix("https://blog.ameba.jp/ucs/the"),
url-prefix("https://blog.ameba.jp/ucs/out"),
url-prefix("https://blog.ameba.jp/ucs/seo"),
url-prefix("https://blog.ameba.jp/ucs/pin"),
url-prefix("https://blog.ameba.jp/ucs/sid"),
url-prefix("https://blog.ameba.jp/ucs/plu"),
url-prefix("https://blog.ameba.jp/ucs/boo"),
url-prefix("https://blog.ameba.jp/ucs/pro") {
/* ブログ管理メニュー コンテンツ幅が狭いページの修正 ************ */
/* 共通設定 ************************************* */
/* ページ全体の背景と幅 */
#ucsContent {
    width: 840px;
    background: var(--home); }

#ucsMain {
    min-height: 400px;
    background: none; }

/* ページコンテンツ 右寄せ */
#ucsMainLeft {
    width: 640px;
    position: relative;
    z-index: 1; }

/* 右余白 AD */
#ucsMainRight {
    display: block;
    width: 150px;
    height: 400px;
    margin-top: 14px;
    background: #f4f5f7; }

.listModule:not(#sideHowToUse) {
    display: none; }

/* 個別ページレイアウトのアレンジ ******************* */
/* 禁止IPアドレス */
#banipIndex #leftArea {
    width: 260px; }

#banipIndex #leftArea dd input,
#banipIndex #leftArea dd textarea {
    width: 235px; }

/* ブログデザインのCSSの編集 */
#editCss #contentsForm textarea {
    width: 618px; }

#editCss #contentsForm textarea:focus {
    outline: 1px solid #2196f3; }

#editCss input[type="submit"]:focus {
    outline-color: #2196f3; }

/* フォロワー管理 */
#readerList .tableList {
    width: 618px; }

/* pingの送信先設定 */
#pingIndex #sendPingOthers {
    width: 384px; }

#pingIndex #sendPingOthers textarea {
    width: 362px; }

/* 設定・編集 */
#editSetup #contentsForm table {
    width: 618px; }

/* プラグインの追加 */
.textarea1 {
    font-size: 16px;
    overflow-y: scroll; }

#pluginIndex #tagEdit {
    width: 585px; }

#pluginIndex .tabContentInnerBox {
    padding: 15px; }

/* フリースペース編集 */
#inputProfile {
    overflow: visible; }

#profileIndex .textInputArea .textarea1 {
    width: 618px; }

/* メッセージボード編集 */
#entryMain {
    border: none !important;
    background: none !important; }

#entryBody.entryWrap {
    width: auto; }

#entryMain textarea {
    font-size: 16px;
    width: 620px; }

/* 入力枠・テキストエリア・決定ボタン =共通=  */
input[type="submit"]:focus {
    outline-color: #2196f3; }

input[type="text"]:focus,
textarea:focus {
    outline: 1px solid #2196f3; }

select:focus {
    outline-color: #2196f3; } }

@-moz-document url-prefix("https://blog.ameba.jp/ucs/top") {
/* 管理トップページ ************************************** */
/* 管理ヘッダー ********************************* */
#ucsMainLeft h1 {
    margin-bottom: 4px; }

#contents {
    display: flex;
    flex-direction: column; }

/* プロフィール ********************************* */
/* プロフィール画像 */
#contents .user {
    height: 50px;
    order: 0; }

#contents .user__profile {
    width: 50px;
    height: 50px; }

#contents .user__anchor {
    width: 50px;
    height: 50px; }

#contents .user__image {
    width: 50px !important;
    height: 50px !important; }

/* ブログ名と設定 */
#contents .user__blog {
    height: 50px; }

#contents .user__blog__inner {
    display: flex;
    margin-top: 10px; }

#contents .user__blog__title {
    color: #fff;
    background: #2196F3;
    padding: 6px 15px 4px;
    border-radius: 5px; }

#contents .user__blog__title:hover {
    opacity: 1;
    background: #077bd6;
    text-decoration: none; }

#contents .user__blog__edit {
    margin: 0 0 0 30px;
    width: 70px;
    height: 32px;
    font-size: 14px;
    font-weight: bold;
    line-height: 35px;
    border: 1px solid #2196f3;
    border-radius: 5px;
    text-align: center; }

#contents .user__blog__edit:hover {
    background: var(--hov); }

#contents .user__blog__icon {
    font-size: 18px;
    vertical-align: -4px;
    color: #2196f3; }

#contents .user__blog__edit a {
    color: #2196f3; }

/* ブログを書くボタン */
#contents .user__btn {
    width: 210px;
    height: 32px; }

#contents .user__btn__anchor {
    width: 200px;
    height: 32px;
    line-height: 35px;
    border: 1px solid #2d8c3c;
    font-weight: bold;
    color: #2d8c3c;
    background: #fff; }

#contents .user__btn__anchor:hover {
    opacity: 1;
    background: var(--hov); }

/* アクセス解析の表題 */
#contents .realtimeAccess {
    display: none; }

/* ランクイン告知 ******************************** */
#contents .notification {
    margin-top: 5px;
    order: 1; }

#contents .notification__anchor {
    display: flex;
    justify-content: space-between;
    padding: 6px 50px;
    overflow: hidden; }

#contents .notification__anchor:hover {
    text-decoration: none;
    outline: 1px solid red; }

#contents .notification__text {
    margin: 0 -5em 0 0 !important;
    line-height: 18px;
    white-space: nowrap; }

.notification__text:after {
    content: "\2001\2001\2001\2001\2001";
    background: #fcfae8;
    margin-left: -5em; }

/* ジャンルの特集で書く *************************** */
#contents .officialTag {
    margin-top: 10px;
    padding: 10px 0 0;
    order: 2; }

/* ランキング総合評価 **************************** */
#root {
    min-height: 136px;
    order: 3; }

.p-realtimeAccess {
    height: 120px; }

.p-realtimeAccess__title {
    margin-top: 14px; }

.p-realtimeAccess__count {
    font-size: 24px;
    margin-top: 2px; }

.c-amebaNumbers--loaded {
    font-family: Meiryo;
    font-weight: bold; }

.p-analysisSummary {
    height: 120px;
    margin: 0;
    padding-top: 12px; }

.p-analysisSummary__access__count {
    margin: 0; }

.p-analysisSummary__access__detail {
    margin: 0; }

.p-analysisSummary__ranking {
    height: 95px; }

.p-analysisSummary__ranking__title {
    height: 20px; }

.p-analysisSummary__ranking__item:nth-child(2) {
    padding-top: 7px; }

.p-analysisSummary__ranking__item .c-amebaNumbers--loaded {
    color: #189e90; }

/* Google Search 連携画面へ *********************** */
.gsc {
    position: absolute;
    top: 63px;
    left: calc(50% - 90px);
    padding: 0;
    border: none; }

.gsc__link {
    padding: 3px 8px; }

.gsc__link:hover {
    opacity: 1;
    background: var(--hov); }

/* ブログから広告を外す *************************** */
.premiumInfo {
    position: relative;
    display: block;
    width: 50%;
    padding: 8px 10px 0 0;
    margin: 20px 0 -46px auto;
    z-index: 1;
    order: 7; }

/* リキューメニュー ****************************** */
/* ブログの収入レポート */
#contents .revenue {
    margin: 0;
    order: 10; }

/* Ameba Pick（アフィリエイト） */
.revenue__heading {
    display: none; }

.amebaPickBlogTopModule {
    margin: 0; }

.amebaPickBlogTopModule .amebaPickBlogTopModule__heading,
#contents .revenue__title {
    font-weight: bold;
    color: #666;
    border: 1px solid #e2e2e2;
    margin: 20px 0 0;
    padding: 8px 9px 6px;
    background: var(--home_dim); }

.amebaPickBlogTopModule__introduction,
#contents .revenueCard {
    padding: 0 24px;
    margin-top: -1px; }

.amebaPickBlogTopModule__icon,
#contents .revenueCard__image,
#contents .revenueCard__image img {
    height: 50px;
    width: 50px;
    line-height: 0; }

.amebaPickBlogTopModule__textHeading,
.revenueCard__informationTitle {
    display: none; }

.amebaPickBlogTopModule__textBody br {
    display: none; }

#contents .revenueCard__informationDescription {
    margin: 0; }

.amebaPickBlogTopModule__link {
    height: 26px;
    line-height: 26px; }

.amebaPickBlogTopModule__link:hover {
    background: var(--hov); }

#contents .revenueCard__link {
    height: 26px;
    line-height: 26px;
    margin-top: 7px; }

#contents .revenueCard__link:hover {
    background: var(--hov);
    opacity: 1; }

/* アメーバ キャンペーン *************************** */
.adcross_loader {
    height: 120px;
    overflow: hidden;
    padding: 8px 0 0 !important;
    border: 1px solid #e2e2e2;
    order: 8; }

.adcross_loader::before {
    content: "アメーバ キャンペーン ▶";
    display: block;
    text-align: left;
    padding: 0 9px 4px;
    font-weight: bold;
    color: #048c7f; }

#contents .adcross_loader a:hover {
    opacity: 1; }

#contents .adcross_loader a:hover img {
    box-shadow: 0 0 0 2px #2196f3; }

/* 1年前の記事 ********************************** */
.lookBackEntry {
    padding: 10px 172px;
    background: #fff;
    overflow: hidden;
    order: 4; }

.lookBackEntry .lookBackEntry__entryTitle {
    padding: .5em 0.4em 0.3em;
    box-shadow: 50px 0 0 64px #fff,
    50px 0 0 65px #e2e2e2,
    0 0 0 300px #f8f8f8; }

.lookBackEntry .lookBackEntry__entryTitle:hover {
    color: #333;
    background: var(--hov);
    text-decoration: none; }

.lookBackEntry__reblog:first-child {
    padding: 1px 12px;
    border-radius: 4px;
    background: #fff; }

.lookBackEntry__reblog:first-child:hover {
    background: var(--hov);
    color: #000;
    text-decoration: none;
    box-shadow: 0 0 0 1px #2196f3; }

/* いいね！された記事 ***************************** */
#iine {
    position: relative;
    height: 190px;
    background: var(--home);
    order: 5; }

#iine #iineEntry table {
    border: 1px solid #e2e2e2; }

#iineEntryBody {
    color: #666; }

/* いいね！タイトル */
#contents #iine #iineEntry table .ttl th {
    font-size: 14px;
    padding: 8px 9px 6px;
    border-color: #e2e2e2;
    background: var(--home_dim); }

/* リストのフォバー色 */
#contents #iine #iineEntry table .amember:hover td,
#contents #iine #iineEntry table .default:hover td {
    background: var(--hov); }

#contents #iine #iineEntry table tr td .wrap {
    padding: 3px 8px 0; }

/* 記事タイトル */
#contents #iine #iineEntry table tr td .wrap dl dt {
    font-size: 14px;
    width: 320px;
    padding: 0;
    color: #000; }

/* 日付 */
#contents #iine #iineEntry table tr td .wrap dl dd {
    font-size: 12px;
    padding: 0 10px;
    color: #000; }

/* 押された数 */
#contents #iine #iineEntry table tr .iineCount {
    width: 60px; }

/* サムネイルのリスト */
#contents #iine #iineEntry table tr .iineImg {
    width: 320px; }

/* 時刻 */
#contents #iine #iineEntry table tr .relativeTime {
    width: 80px; }

#contents #iine #iineEntry table tr .relativeTime time {
    color: #000;
    font-size: 13px; }

#iine .more {
    position: absolute;
    right: 0;
    top: 0;
    padding: 10px 15px 8px;
    border: none; }

#iine .more a {
    line-height: 16px;
    background-position: 0 0; }

/* ユーザーアイコン コンテナ */
#contents #iine #iineEntry table tr .iineImg .img {
    padding: 2px; }

/* ユーザーアイコン */
#contents #iine #iineEntry table tr .iineImg .img img {
    object-fit: cover; }

/* いいね！ダイアログ ***************************** */
/* ダイアログ幅 */
#frame {
    font-size: 14px;
    width: 600px !important;
    max-height: 670px !important; }

/* ヘッダー */
#iineEntryFrame .header {
    overflow: hidden; }

#iineEntryFrame .header p {
    padding: 10px 30px 8px 17px;
    background-position: left 10px; }

#iineEntryFrame .header p a:hover {
    position: relative;
    color: #06c;
    text-underline-position: under; }

/* 閉じるボタン */
#iineEntryFrame .header .closeBtn {
    top: 8px;
    right: 8px; }

/* リストアイテム */
