css:
@-moz-document domain("learn.twu.ca") {
    html, body {
        background: #333;
        color: #ddd;
    }
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        color: #eee;
    }
    p {
        color: #ddd;
    }
    textarea,
    input[type="text"] {
        color: #ddd !important;
        background: #4a4a4a;
        border: 1px solid #555;
    }
    select,
    select:focus,
    option,
    option:focus,
    optgroup,
    optgroup:focus {
        background-color: #444;
        color: #ddd;
        border: none;
    }
    select option:hover {
        background: #555;
    }
    #navwrap {
        border: none;
    }
    .fullin .container {
        width: 100%;
    }
    a,
    a:visited,
    .tabtree .tabrow0 li a,
    .dropdown-menu.usermen > li > a {
        color: #ddd !important;
    }
    hr {
        border-top: none;
        border-bottom: 1px solid #777;
    }
    #ticker-wrap {
        background: #444;
    }
    img#logo {
        padding-left: 1em;
        filter: brightness(300%);
    }
    /* Header */
    
    #region-main,
    [role="main"],
    .content,
    .header {
        background: #333 !important;
        color: #ddd;
    }
    .header h2,
    #frontpage-category-names h2 {
        color: #dddddd !important;
    }
    header {
        background: #444;
    }
    #above-header,
    footer#page-footer,
    #page-footer,
    #page-footer p {
        background: #111;
        border: none;
    }
    #page-header,
    #page-header-wrapper {
        background: #333;
    }
    #sitetitle,
    #sitetitle a,
    #sitetitle p {
        color: #00e1ff !important;
    }
    /* Navigation */
    
    .navbar,
    #navwrap {
        background: #333;
        margin-left: 0;
    }
    .navbar-inner {
        padding-right: 1em;
    }
    .navbar .nav > li > a {
        color: #ddd !important;
    }
    .navbar .nav > li > a:hover {
        background: #555 !important;
    }
    .dropdown-menu {
        background: #555 !important;
        border-color: #555 !important;
    }
    .nav .dropdown-menu li a {
        color: #ddd !important;
    }
    .nav .dropdown-menu li a:hover {
        background: #6a6a6a;
    }
    #page-navbar {
        background: #333;
    }
    .pull-left .usermenu2.nav a {
        padding-left: 1em;
    }
    .dropdown-menu.usermen > li > a:hover,
    .dropdown-menu.usermen > li > a:active {
        background: #6a6a6a;
    }
    #above-header .headermenu .dropdown-menu a,
    #above-header .headermenu .dropdown-toggle a {
        color: #ddd !important;
    }
    .breadcrumb {
        background: #333;
    }
    .breadcrumb ul i,
    .breadcrumb li.lastli span {
        color: #ddd;
        font-weight: bold;
    }
    .jsenabled .block_settings .block_tree .tree_item.branch {
        color: #eee !important;
    }
    #dock .dockedtitle,
    #dock .dockedtitle:hover,
    #dockeditempanel .dockeditempanel_hd,
    #dockeditempanel .dockeditempanel_bd,
    #dockeditempanel .dockeditempanel_hd h2 {
        background: #555;
        color: #ddd;
    }
    #dock .dockedtitle,
    #dock .dockedtitle:hover {
        border: none;
    }
    /* Calendar */
    
    .calendar_event_course {
        background: #555;
    }
    .block .calendar-event-panel .yui3-overlay-content {
        background: #666;
        border-color: #666;
        box-shadow: inset 0 1px 1px #555;
    }
    .block .calendar-event-panel {
        background: #555;
        border-color: #555;
    }
    .calendar-event-panel .eventcontent {
        color: #ddd;
    }
    .block_calendar_month table.minicalendar.calendartable td.today a,
    .block .minicalendar td.today a {
        color: #bce516 !important;
    }
    /* Content */
    
    table#form td.submit,
    .form-buttons,
    .path-admin .buttons,
    #fitem_id_submitbutton,
    #fgroup_id_buttonar {
        background: transparent;
    }
    .moodleSkin td.mceToolbar,
    .moodleSkin td.mceStatusbar,
    .moodleSkin .mceMenuItemTitle a,
    .moodleSkin table,
    .moodleSkin tbody,
    .moodleSkin a,
    .moodleSkin img,
    .moodleSkin tr,
    .moodleSkin div,
    .moodleSkin td,
    .moodleSkin iframe,
    .moodleSkin span,
    .moodleSkin *,
    .moodleSkin .mceText,
    .moodleSkin .mceListBox .mceText,
    .moodleSkin .mceToolbar td + td {
        background: #444;
        color: #ddd;
    }
    .moodleSkin table.mceListBoxEnabled:hover .mceText,
    .moodleSkin .mceListBoxHover .mceText,
    .moodleSkin .mceListBoxSelected .mceText,
    .moodleSkin a.mceButtonEnabled:hover {
        background: #444;
    }
    .moodleSkin .mceMenu {
        border: 1px solid #444;
    }
    .mceMenuItemEnabled a:hover {
        background: #555 !important;
    }
    .filemanager .fp-navbar,
    .filepicker-filelist,
    .filemanager-container,
    .fp-viewbar,
    .fp-toolbar .fp-btn-add,
    .fp-toolbar .fp-btn-download,
    .fp-toolbar .fp-btn-mkdir,
    .fp-toolbar .fp-tb-help,
    .fp-toolbar .fp-tb-manage,
    .fp-toolbar .fp-tb-logout,
    .fp-toolbar .fp-tb-refresh,
    .fp-viewbar a {
        background: #444;
        border: none;
    }
    .fp-viewbar a.checked,
    .fp-viewbar a:active {
        background: #555;
    }
    .dndupload-arrow {
        filter: invert(70%);
    }
    span.mceText {
        background: transparent !important;
        color: #ddd !important;
    }
    .moodleSkin .mceToolbar .mceGroupEnd,
    .moodleSkin .mceMenuItemTitle a {
        border: none;
    }
    .moodleSkin .mceListBox .mceOpen {
        filter: invert(100%);
    }
    .moodleSkin table.mceLayout,
    .moodleSkin .mceIframeContainer,
    .moodleSkin table.mceLayout tr.mceLast td,
    .moodleSkin .mceToolbar td + td {
        border: 1px solid #444;
    }
    .collapsibleregioncaption {
        color: #ccc;
    }
    .content .name {
        color: #eee;
        font-weight: bold;
    }
    .nav-tabs > li > a {
        border-color: #4a4a4a;
    }
    .nav-tabs > li > a:hover,
    .nav-tabs > .active > a {
        background: #555 !important;
        border-color: #555;
    }
    #page .course-content ul li.section.main {
        background-color: #333;
        border-color: #555;
    }
    ul.weeks h3.sectionname {
        background-color: #555;
        color: #69c7ce !important;
    }
    .course-content .current .content .sectionname {
        background: #777;
    }
    .path-calendar .maincalendar .eventlist .event {
        background: none;
        border-color: #555;
    }
    .path-calendar .maincalendar .eventlist .event .description {
        background: #555;
    }
    .course_category_tree .category > .info > .categoryname {
        color: #aaa;
    }
    /* Assignments/Grades */
    
    #intro.generalbox {
        background: #4a4a4a;
        border: none;
    }
    .generaltable td {
        border: none;
        color: #ddd;
    }
    .path-mod-assign td.earlysubmission,
    .path-mod-assign td.submissionstatussubmitted {
        color: #7fc48b;
        background: #4a4a4a;
    }
    table#explaincaps tbody > tr:nth-child(2n+1) > td,
    table#defineroletable tbody > tr:nth-child(2n+1) > td,
    table.grading-report tbody > tr:nth-child(2n+1) > td,
    table#listdirectories tbody > tr:nth-child(2n+1) > td,
    table.rolecaps tbody > tr:nth-child(2n+1) > td,
    table.userenrolment tbody > tr:nth-child(2n+1) > td,
    table#form tbody > tr:nth-child(2n+1) > td,
    form#movecourses table tbody > tr:nth-child(2n+1) > td,
    #page-admin-course-index .editcourse tbody > tr:nth-child(2n+1) > td,
    .forumheaderlist tbody > tr:nth-child(2n+1) > td,
    table.flexible tbody > tr:nth-child(2n+1) > td,
    .generaltable tbody > tr:nth-child(2n+1) > td,
    table#explaincaps tbody > tr:nth-child(2n+1) > th,
    table#defineroletable tbody > tr:nth-child(2n+1) > th,
    table.grading-report tbody > tr:nth-child(2n+1) > th,
    table#listdirectories tbody > tr:nth-child(2n+1) > th,
    table.rolecaps tbody > tr:nth-child(2n+1) > th,
    table.userenrolment tbody > tr:nth-child(2n+1) > th,
    table#form tbody > tr:nth-child(2n+1) > th,
    form#movecourses table tbody > tr:nth-child(2n+1) > th,
    #page-admin-course-index .editcourse tbody > tr:nth-child(2n+1) > th,
    .forumheaderlist tbody > tr:nth-child(2n+1) > th,
    table.flexible tbody > tr:nth-child(2n+1) > th,
    .generaltable tbody > tr:nth-child(2n+1) > th,
    .user-enroller-panel .uep-search-results .users tbody tr:hover > td,
    .user-enroller-panel .uep-search-results .cohorts tbody tr:hover > td,
    table.grading-report tbody tr:hover > td,
    .forumheaderlist tbody tr:hover > td,
    .generaltable tbody tr:hover > td,
    table.flexible tbody tr:hover > td,
    .category_subcategories tbody tr:hover > td,
    table#modules tbody tr:hover > td,
    table#permissions tbody tr:hover > td,
    .user-enroller-panel .uep-search-results .users tbody tr:hover > th,
    .user-enroller-panel .uep-search-results .cohorts tbody tr:hover > th,
    table.grading-report tbody tr:hover > th,
    .forumheaderlist tbody tr:hover > th,
    .generaltable tbody tr:hover > th,
    table.flexible tbody tr:hover > th,
    .category_subcategories tbody tr:hover > th,
    table#modules tbody tr:hover > th,
    table#permissions tbody tr:hover > th {
        background: #333;
    }
    .path-grade-report-user .user-grade.generaltable .levelodd {
        background: #333;
    }
    table.flexible th,
    .generaltable th,
    table.flexible td,
    .generaltable td {
        border-top: 1px solid #4a4a4a;
    }
    .fp-iconview .fp-filename-field .fp-filename {
        background: transparent;
    }
    .fp-iconview .fp-thumbnail {
        border: none;
    }
    .fp-iconview .fp-thumbnail img {
        border: none;
        box-shadow: none;
    }
    .moodle-dialogue-base .moodle-dialogue-wrap {
        background: #555;
        border: none;
    }
    .moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
    .moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
        background: #333;
        border: none;
    }
    .moodle-dialogue-base .closebutton {
        filter: invert(80%);
    }
    button,
    input.form-submit,
    input[type="button"],
    input[type="submit"],
    input[type="reset"] {
        background: linear-gradient(to bottom, #4a4a4a, #444);
        color: #ccc;
        border: none;
        text-shadow: none;
        box-shadow: none;
    }
    button:hover,
    input.form-submit:hover,
    input[type="button"]:hover,
    input[type="submit"]:hover,
    input[type="reset"]:hover,
    button:focus,
    input.form-submit:focus,
    input[type="button"]:focus,
    input[type="submit"]:focus,
    input[type="reset"]:focus {
        color: #ccc;
        background: linear-gradient(to bottom, #444, #333);
    }
    .fp-select .fp-hr {
        background: none;
        border: none;
    }
    .moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd {
        color: #ccc;
    }
    .fp-select .fp-thumbnail {
        background: transparent;
        border: none;
        box-shadow: none;
    }
    .fp-select .fp-thumbnail img {
        border: none;
    }
    .btn:first-child {
        color: #00b5cc !important;
    }
    .btn:first-child:hover {
        color: white !important;
    }
    /* Marking Tools */
    
    .path-mod-assign .gradingtable tr.unselectedrow td,
    .path-mod-assign [data-region="grade-panel"] {
        background: transparent;
    }
}
