css:
@-moz-document url-prefix('https://www.coursera.org') {
/* ------------------------------------------------------------------------ */

  /*
      blueish coursera
   */

    * {
        background: #455A84 !important;
        color: #373a3c;
        color: #000000;
    }

    .c-authenticated-dropdown-menu {
        border: white 1px solid;
    }

    div [class^="Box"],    
    div.mega-menu-dropdown{
        border: white 1px solid;
        margin: 0px;
        padding: 0px;
    }

	div [class^='cover'],
    div.mega-menu-items * a {
        background: #455A84 !important;
    }

    div.mega-menu-container:hover,
    div.mega-menu-items a:hover ,
    div.mega-menu-items span>:hover {
        background: #354A74 !important;
    }

    a[class='bt3-collapsed']:hover div div,
    a[class='bt3-collapsed']       div:hover div,
    a[class='bt3-collapsed']       div       div :hover,
    li .sign-out:hover,
    li [class^='rc-HeaderRightNavButton'] a:hover,
    li [class^='rc-HeaderRightNavButton'] *:hover,
    li [class^='rc-HeaderRightNavButton'] > * > span:hover,
    li [class^='rc-HeaderRightNavButton'] > * > *:hover,
    li [class^='rc-HeaderRightNavButton'] > a > span:hover,

	div[class^='rc-CourseEnrollButton']:hover ,
    div[class^='rc-CourseEnrollButton']:hover *,

	a[class^='rc-MegaMenuItem']:hover *,

	a[class*='rc-TextNavItem']:hover,
	a[class*='rc-TextNavItem']       span:hover,

	div[class^='bt3-dropdown'] button[class^='button-link']:hover,
	button[class^='button-link']:hover *,
	button[class^='button-link']:hover,

    a[class*='rc-DomainNavItem']:hover *,
    a[class*='rc-DomainNavItem']:hover,

	div[class^='c-page-item']:hover,
	div[class^='c-page-item']       * :hover,
	div[class='rc-MegaMenuWrapper']:hover > a,
	div[class='rc-MegaMenuWrapper']:hover > a:hover
	{
        background: #354A74 !important;
        color: #000;
    }


}
