css:
@-moz-document domain("workflowy.com")
{

/* HOME PAGE GAP */
div.project.selected.mainTreeRoot > div.name {
  height: 0 !important;
} 


.page {
	max-width: 70% !important;
	margin-top: 30px;
}


/* WORKFLOWY PAGE COLOR */
.page {
	background: white;
	border: none;
	border-radius: 3px;
}

/* WORKFLOWY BACKGROUND COLOR */
body {
	background: white;
}

/* SHOW TOOLBAR ONLY ON HOVER */
.header {
    opacity: 0;
}

.header:hover {
    opacity: 1;
    -webkit-transition-duration: .5s;
    -moz-transition-duration: .5s;
    transition-delay: .3s !important;
} 


/* SPACE BETWEEN BULLETS AND TEXT */
.project > .name > .content, .noted > .notes > .content {
	padding-left: 4px;
}


/* BREADCRUMB HIDE CURRENT PAGE */
.breadcrumbs > a:last-of-type {
  display: none !important;
  
}
.breadcrumbs > svg:last-of-type {
  display: none !important;
}

/* HOME PAGE GAP */
div.project.selected.mainTreeRoot > div.name {
  height: 0 !important;
}


/* COLOR OF EXPAND/ COLLAPSE ICON */   
._1dkrrqd:hover > .expand {
    color: rgb(176,176,176);
}

/* WIDTH OF +/- */
line {
    stroke-width: 1 ;
    /* CURSOR BEHAVIOR FOR +/- 
    cursor: pointer !important;*/
}

/* HIDE STAR AND PAGE MENU BUTTONS */
.starButton, .pageMenu {
    display: none;
}


/* NO AUTO-BOLD FOR ZOOMED-IN LIST TITLES */

.selected.project > .name {
    font-weight: normal;
}




}
